import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';

import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    HelpCircle,
    ArrowRight,

    CloudUpload,
    Trophy,
    Italic,  // Added new icon for benchmarking
  } from 'lucide-react';

  const styles = {
    landingPage: {
      display: 'flex',
      height: '100vh',
      backgroundColor: '#f4f4f9',
      fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
      width: '250px',
      background: 'linear-gradient(to bottom right, #064e3b, #276749)',
      color: 'white',
      padding: '20px',
      boxSizing: 'border-box',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'flex-start',
      position: 'fixed',
      height: '100vh',
      zIndex: 10,
      boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
      transition: 'width 0.3s ease',
    },
    logo: {
      fontSize: '25px',
      marginBottom: '30px',
      fontWeight: 'bold',
      color: '#fff',
      textAlign: 'center',
      marginTop: '20px',
      transition: 'transform 0.3s ease',
    },
    logoHover: {
      transform: 'scale(1.1)',
    },
    navList: {
      listStyle: 'none',
      padding: 0,
      margin: 0,
      width: '100%',
    },
    navItem: {
      color: 'white',
      textDecoration: 'none',
      padding: '15px 20px',
      display: 'flex',
      alignItems: 'center',
      width: '100%',
      borderRadius: '8px',
      marginBottom: '12px',
      transition: 'background-color 0.3s ease, transform 0.3s ease',
      cursor: 'pointer',
      fontfamily: "'Poppins', sans-serif", // Custom font for links
          fontWeight: '500', // Optional for bold text
      },
        navItemHover: {
      backgroundColor: '#3b6b5c',
      transform: 'scale(1.05)',
    },
    navIcon: {
      marginRight: '20px',
      transition: 'transform 0.3s ease',
    },
    button: {
        backgroundColor: '#ffffff', // Default background color
        color: 'Black', // Text color
        border: 'none', // No border
        padding: '10px 20px', // Padding for a nice clickable area
        borderRadius: '5px', // Rounded corners
        fontWeight: 'bold', // Bold text
        fontSize: '16px', // Font size
        cursor: 'pointer', // Pointer cursor on hover
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Subtle shadow for a 3D effect
        transition: 'background-color 0.3s ease, transform 0.2s ease', // Smooth transition for hover effects

  },
};

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
];

const Sidebar = () => {
  const [showLogoutPopup, setShowLogoutPopup] = useState(false);
  const UploadSections = () => {
    // States for form inputs and file uploads
    const [environmentalData, setEnvironmentalData] = useState({
      energyConsumption: '',
      renewableEnergy: '',
      ghgEmissions: '',
      waterUsage: '',
    });
    const [socialData, setSocialData] = useState({
      employeeDiversity: '',
      employeeEngagement: '',
      employeeTurnover: '',
      communityInitiatives: '',
    });
    const [governanceData, setGovernanceData] = useState({
      boardDiversity: '',
      cybersecurityIncidents: '',
      complianceViolations: '',
      ceoPayRatio: '',
    });

    const [csvFile, setCsvFile] = useState(null);
    const [logoFile, setLogoFile] = useState(null);

    const [formErrors, setFormErrors] = useState([]);
    const [previewData, setPreviewData] = useState(null);

    // Handle CSV File Upload
    const handleCsvUpload = (e) => {
      const file = e.target.files[0];
      if (file && file.size < 5 * 1024 * 1024 && file.name.endsWith('.csv')) {
        setCsvFile(file);
        setFormErrors([]);
      } else {
        setFormErrors(['Invalid file format or size!']);
      }
    };

    // Handle Logo Upload
    const handleLogoUpload = (e) => {
      const file = e.target.files[0];
      if (file && ['image/png', 'image/jpg', 'image/jpeg', 'image/svg+xml'].includes(file.type) && file.size < 2 * 1024 * 1024) {
        setLogoFile(URL.createObjectURL(file));
        setFormErrors([]);
      } else {
        setFormErrors(['Invalid logo format or size!']);
      }
    };

    // Handle Submit
    const handleSubmit = () => {
      // Perform any further validation before submission
      console.log('Form submitted');
    };
  }  
  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
      <div style={{ display: 'flex', alignItems: 'center', }}>
  <img
    src={GreenTrendz}
    alt="Logo"
    width="50"
    height="50"
    className="d-inline-block align-top me-2"
  />
  <h5
    style={{
      ...styles.logo,
      size: '20px',
      margin: 0, // Remove default margin
      onMouseEnter: (e) => (e.target.style.transform = 'scale(1.0)'),
      onMouseLeave: (e) => (e.target.style.transform = ''),
      fontsize: '18px', // Adjust font size here
      fontWeight: 'bold',
    }}
  >
    GreenTrendz
  </h5>
</div>

        <ul style={styles.navList}>
          {menuItems.map((item) => (
            <li key={item.path}>
              <a
                href={item.path}
                style={styles.navItem}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#3b6b5c'; // Hover effect color
                  e.target.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '';
                  e.target.style.transform = '';
                }}
              >
                <item.icon style={styles.navIcon} size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>
        {/* Logo and User Info */}
        <div style={{ display: 'flex', flexDirection: 'column', marginTop: 'auto', alignItems: 'center' }}>

        <button
          style={styles.button}
          onClick={() => setShowLogoutPopup(true)}
          onMouseEnter={(e) => {
            e.target.style.transform = 'scale(1.05)'; // Slight scale up
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'scale(1)'; // Revert scale
          }}
        >
          Logout
        </button>

          </div>
      </nav>
      <div style={{ marginTop: '20px' }}>
      <h2>Annual Input</h2>

      {/* Environmental Metrics Section */}
      <div style={sectionStyle}>
        <h3>Environmental</h3>
        <input type="number" placeholder="Energy Consumption (kWh)" value={environmentalData.energyConsumption} onChange={(e) => setEnvironmentalData({ ...environmentalData, energyConsumption: e.target.value })} />
        <input type="number" placeholder="Renewable Energy Percentage (%)" value={environmentalData.renewableEnergy} onChange={(e) => setEnvironmentalData({ ...environmentalData, renewableEnergy: e.target.value })} />
        <input type="number" placeholder="Greenhouse Gas Emissions (CO2e)" value={environmentalData.ghgEmissions} onChange={(e) => setEnvironmentalData({ ...environmentalData, ghgEmissions: e.target.value })} />
        <input type="number" placeholder="Water Usage (liters)" value={environmentalData.waterUsage} onChange={(e) => setEnvironmentalData({ ...environmentalData, waterUsage: e.target.value })} />
      </div>

      {/* Social Metrics Section */}
      <div style={sectionStyle}>
        <h3>Social</h3>
        <input type="number" placeholder="Employee Diversity (%)" value={socialData.employeeDiversity} onChange={(e) => setSocialData({ ...socialData, employeeDiversity: e.target.value })} />
        <input type="number" placeholder="Employee Engagement Score (%)" value={socialData.employeeEngagement} onChange={(e) => setSocialData({ ...socialData, employeeEngagement: e.target.value })} />
        <input type="number" placeholder="Employee Turnover Rate (%)" value={socialData.employeeTurnover} onChange={(e) => setSocialData({ ...socialData, employeeTurnover: e.target.value })} />
        <input type="number" placeholder="Number of Community Initiatives" value={socialData.communityInitiatives} onChange={(e) => setSocialData({ ...socialData, communityInitiatives: e.target.value })} />
      </div>

      {/* Governance Metrics Section */}
      <div style={sectionStyle}>
        <h3>Governance</h3>
        <input type="number" placeholder="Board Diversity (%)" value={governanceData.boardDiversity} onChange={(e) => setGovernanceData({ ...governanceData, boardDiversity: e.target.value })} />
        <input type="number" placeholder="Cybersecurity Incidents (#)" value={governanceData.cybersecurityIncidents} onChange={(e) => setGovernanceData({ ...governanceData, cybersecurityIncidents: e.target.value })} />
        <input type="number" placeholder="Regulatory Compliance Violations (#)" value={governanceData.complianceViolations} onChange={(e) => setGovernanceData({ ...governanceData, complianceViolations: e.target.value })} />
        <input type="number" placeholder="CEO to Median Employee Pay Ratio" value={governanceData.ceoPayRatio} onChange={(e) => setGovernanceData({ ...governanceData, ceoPayRatio: e.target.value })} />
      </div>

      {/* CSV File Upload Section */}
      <div style={sectionStyle}>
        <h3>CSV File Upload</h3>
        <input type="file" accept=".csv" onChange={handleCsvUpload} />
        {csvFile && <p>File uploaded: {csvFile.name}</p>}
      </div>

      {/* Logo Upload Section */}
      <div style={sectionStyle}>
        <h3>Logo Upload</h3>
        <input type="file" accept=".png, .jpg, .jpeg, .svg" onChange={handleLogoUpload} />
        {logoFile && <img src={logoFile} alt="Logo Preview" width="100" />}
      </div>

      {/* Error Messages */}
      {formErrors.length > 0 && <div style={{ color: 'red' }}>{formErrors.join(', ')}</div>}

      {/* Submit Button */}
      <div style={{ marginTop: '20px' }}>
        <button style={styles.button} onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};



    </div>
  );
};

export default Sidebar;
