                            <div key={idx} style={styles.section}>
                                <div
                                    style={styles.sectionHeader}
                                    onClick={() => toggleSection(section.title)}
                                >
                                    <h4>{section.title}</h4>
                                    <span>{expandedSections[section.title] ? '▲' : '▼'}</span>
                                </div>
                                {expandedSections[section.title] && (
                                    <div>
                                        {section.metrics.map((metric, i) => (
                                            <div key={i} style={styles.inputContainer}>
                                                <label htmlFor={metric.key}>
                                                    {metric.label}
                                                    <Info
                                                        style={styles.infoIcon}
                                                        onMouseEnter={() => showTooltip(metric.key)}
                                                        onMouseLeave={() => hideTooltip(metric.key)}
                                                    />
                                                </label>
                                                <input
                                                    id={metric.key}
                                                    type={metric.type}
                                                    value={socialData[metric.key] || ''}
                                                    onChange={(e) =>
                                                        setSocialData((prev) => ({
                                                            ...prev,
                                                            [metric.key]: e.target.value,
                                                        }))
                                                    }
                                                    style={styles.inputField}
                                                />
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}
                        <button
                            style={styles.button}
                            onClick={() => alert('Add New Metric functionality here')}
                        >
                            Add New Social Metric
                        </button>
                    </div>

                    {/* Governance Section */}
                    <div className="section">
                        <h3>Governance</h3>
                        {governanceSections.map((section, idx) => (
                            <div key={idx} style={styles.section}>
                                <div
                                    style={styles.sectionHeader}
                                    onClick={() => toggleSection(section.title)}
                                >
                                    <h4>{section.title}</h4>
                                    <span>{expandedSections[section.title] ? '▲' : '▼'}</span>
                                </div>
                                {expandedSections[section.title] && (
                                    <div>
                                        {section.metrics.map((metric, i) => (
                                            <div key={i} style={styles.inputContainer}>
                                                <label htmlFor={metric.key}>
                                                    {metric.label}
                                                    <Info
                                                        style={styles.infoIcon}
                                                        onMouseEnter={() => showTooltip(metric.key)}
                                                        onMouseLeave={() => hideTooltip(metric.key)}
                                                    />
                                                </label>
                                                <input
                                                    id={metric.key}
                                                    type={metric.type}
                                                    value={governanceData[metric.key] || ''}
                                                    onChange={(e) =>
                                                        setGovernanceData((prev) => ({
                                                            ...prev,
                                                            [metric.key]: e.target.value,
                                                        }))
                                                    }
                                                    style={styles.inputField}
                                                />
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}
                        <button
                            style={styles.button}
                            onClick={() => alert('Add New Metric functionality here')}
                        >
                            Add New Governance Metric
                        </button>
                    </div>






{/* Data Import Section */}
<div className="section">
  <h3>Data Import</h3>

  <div className="date-picker-container">
    <div className="inputColumn">
      <label>Start Date</label>
      <input
        type="date"
        value={dataImportData.startDate}
        onChange={(e) =>
          setDataImportData({
            ...dataImportData,
            startDate: e.target.value,
          })
        }
        className="inputField"
      />
    </div>
    <div className="inputColumn">
      <label>End Date</label>
      <input
        type="date"
        value={dataImportData.endDate}
        onChange={(e) =>
          setDataImportData({
            ...dataImportData,
            endDate: e.target.value,
          })
        }
        className="inputField"
      />
    </div>
  </div>
</div>



           {/* File Upload Section */}
<div className="section">
    <h3>Upload CSV</h3>
    <div className="file-upload-container">
        <input
            type="file"
            accept=".csv"
            onChange={handleCsvUpload}
            className="inputField"
            id="csv-upload"
        />
        <label htmlFor="csv-upload" className="upload-label">
            <p>Click to upload or drag and drop</p>
            <p>Upload a CSV file (max. 5MB) containing Metric Name, Value, and Timestamp columns</p>
        </label>
    </div>
    {csvFile && (
        <div className="preview">
            <p>{csvFile.name}</p>
            <p>{Math.round(csvFile.size / 1024)} KB</p>
        </div>
    )}
</div>

{/* Logo Upload Section */}
<div className="section">
    <h3>Upload Logo</h3>
    <div className="file-upload-container">
        <input
            type="file"
            accept="image/*"
            onChange={handleLogoUpload}
            className="inputField"
            id="logo-upload"
        />
        <label htmlFor="logo-upload" className="upload-label">
            <p>Click to upload or drag and drop</p>
            <p>Upload your company logo (JPEG/PNG/SVG, max. 2MB)</p>
        </label>
    </div>
    {logoFile && (
        <div className="preview">
            <img src={logoFile} alt="Logo" width="100" />
        </div>
    )}
</div>

{/* Submit Button */}
<button className="button" onClick={handleSubmit}>
    Submit Data
</button>
<div>
        {loading && <p>Submitting data, please wait...</p>}
        {successMessage && <p className="success">{successMessage}</p>}
        {errorMessage && <p className="error">{errorMessage}</p>}
    </div>
</div>
    </div>

    </div>
    );
};

export default Sidebar;
