import React from 'react';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
  ArrowRight,
  ShieldCheck,
  BarChart,
  Globe,
  CloudUpload // Add the CloudUpload icon from lucide-react
} from 'lucide-react';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #2f855a, #276749)',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed', // Keep sidebar fixed
    height: '100vh', // Ensure it stretches full height
    zIndex: 10,
    boxShadow: '2px 0 10px rgba(0, 0, 0, 0.15)', // Slightly enhanced shadow for more depth
  },
  logo: {
    fontSize: '24px',
    marginBottom: '30px',
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center', // Center logo
    marginTop: '20px',
    letterSpacing: '1px', // Added letter spacing for a modern feel
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    width: '100%',
  },
  navItem: {
    color: 'white',
    textDecoration: 'none',
    padding: '12px 20px', // Increased padding for better click area
    display: 'flex',
    alignItems: 'center', // Align icon and text vertically
    width: '100%',
    borderRadius: '8px', // More rounded corners for a smoother feel
    marginBottom: '10px', // Add space between items
    transition: 'background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease', // Smooth transition effects
  },
  navItemHover: {
    background: 'linear-gradient(to bottom right, #3e4a61, #2f3b52)', // Gradient hover effect
    transform: 'scale(1.05)', // Slightly scale up on hover for emphasis
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)', // Add subtle glow on hover
  },
  navIcon: {
    marginRight: '15px', // Ensure there's enough space between icon and text
    transition: 'transform 0.3s', // Smooth icon scaling
  },
  content: {
    flex: 1,
    padding: '20px',
    boxSizing: 'border-box',
    backgroundColor: '#fff',
    overflowY: 'auto',
    marginLeft: '250px', // Offset content to the right to avoid overlap
  },
  header: {
    marginBottom: '30px',
    borderBottom: '2px solid #f0f0f0',
    paddingBottom: '10px',
  },
  mainSection: {
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    marginBottom: '20px',
  },
};
// Features Data
const features = [
  {
    title: 'Automated Reporting',
    description: 'Generate ESG and BRSR reports effortlessly using AI.',
    icon: ShieldCheck,
  },
  {
    title: 'Data Analytics',
    description: 'Visualize and analyze your sustainability metrics.',
    icon: BarChart,
  },
  {
    title: 'Global Standards',
    description: 'Adhere to international ESG guidelines.',
    icon: Globe,
  },
];

// Stats Data
const stats = [
  { value: '2M+', label: 'Carbon Data Points' },
  { value: '500+', label: 'Companies' },
  { value: '98%', label: 'Accuracy Rate' },
  { value: '24/7', label: 'Support' },
];

const menuItems = [
  { icon: Home, label: 'Home', path: '/' },
  { icon: CloudUpload, label: 'Data Upload', path: '/data-upload' }, // Added Data Upload item
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
];

const LandingPage = () => {
  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
      <h2 style={styles.logo}>Platform Logo</h2>
      <ul style={styles.navList}>
        {menuItems.map((item) => (
          <li key={item.path}>
            <a
              href={item.path}
              style={styles.navItem}
              onMouseEnter={(e) => {
                e.target.style.background = 'linear-gradient(to bottom right, #3e4a61, #2f3b52)';
                e.target.style.transform = 'scale(1.05)';
                e.target.style.boxShadow = '0 0 10px rgba(0, 0, 0, 0.2)';
              }}
              onMouseLeave={(e) => {
                e.target.style.background = '';
                e.target.style.transform = '';
                e.target.style.boxShadow = '';
              }}
            >
              <item.icon style={styles.navIcon} size={20} />
              <span>{item.label}</span>
            </a>
          </li>
        ))}
      </ul>
    </nav>

      {/* Main Content */}
      <main style={styles.content}>
        {/* Hero Section */}
        <div
          style={{
            background: 'linear-gradient(to bottom right, #2f855a, #276749)',
            color: 'white',
            padding: '40px',
            borderRadius: '12px',
            marginBottom: '20px',
          }}
        >
          <h1 style={{ fontSize: '36px', fontWeight: 'bold', marginBottom: '10px' }}>
            Transform Your ESG Reporting Process
          </h1>
          <p style={{ fontSize: '18px', marginBottom: '20px' }}>
            Generate comprehensive ESG reports, track sustainability metrics, and make data-driven decisions with our powerful platform.
          </p>
          <div style={{ display: 'flex', gap: '10px' }}>
            <button
              style={{
                backgroundColor: '#fff',
                color: '#276749',
                padding: '10px 20px',
                borderRadius: '5px',
                border: 'none',
                fontWeight: 'bold',
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
              }}
            >
              Get Started <ArrowRight size={20} />
            </button>
            <button
              style={{
                backgroundColor: 'transparent',
                color: '#fff',
                padding: '10px 20px',
                borderRadius: '5px',
                border: '1px solid #fff',
                fontWeight: 'bold',
              }}
            >
              Watch Demo
            </button>
          </div>
        </div>

        {/* Features Section */}
        <section style={styles.mainSection}>
          <h2 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '20px' }}>
            Why Choose Our Platform?
          </h2>
          <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
            {features.map((feature, index) => (
              <div
                key={index}
                style={{
                  backgroundColor: '#fff',
                  padding: '20px',
                  borderRadius: '8px',
                  boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
                  flex: '1 1 calc(33% - 20px)',
                }}
              >
                <div
                  style={{
                    backgroundColor: '#e6fffa',
                    width: '50px',
                    height: '50px',
                    borderRadius: '8px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    marginBottom: '10px',
                  }}
                >
                  <feature.icon size={24} color="#276749" />
                </div>
                <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '10px' }}>
                  {feature.title}
                </h3>
                <p style={{ color: '#4a5568' }}>{feature.description}</p>
              </div>
            ))}
          </div>
        </section>

        {/* Stats Section */}
        <section style={styles.mainSection}>
          <h2 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '20px' }}>
            Key Statistics
          </h2>
          <div style={{ display: 'flex', gap: '20px', justifyContent: 'space-between' }}>
            {stats.map((stat, index) => (
              <div key={index} style={{ textAlign: 'center' }}>
                <div
                  style={{
                    fontSize: '32px',
                    fontWeight: 'bold',
                    color: '#276749',
                    marginBottom: '5px',
                  }}
                >
                  {stat.value}
                </div>
                <div style={{ color: '#4a5568' }}>{stat.label}</div>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default LandingPage;
