                )
            },
            dateRange: {
                startDate: dataImportData.startDate,
                endDate: dataImportData.endDate
            },
            fileReferences: fileUploadResults,
            dynamicMetrics: dynamicMetrics
        };



            const response = await fetch('http://localhost:5003/api/save-esg-data', {
                method: 'POST',
                body: formData,

            });

            if (response.ok) {
                const result = await response.json();
                setSuccessMessage(result.message || 'Data submitted successfully!');
            } else {
                const errorDetail = await response.text();
                throw new Error(`Error ${response.status}: ${errorDetail}`);
            }
        } catch (error) {
            setErrorMessage('Error submitting data: ' + error.message);
        } finally {
            setLoading(false);
        }
    };

