import React from 'react';

import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
  ArrowRight,
  ShieldCheck,
  BarChart,
  Globe,
  CloudUpload
} from 'lucide-react';

const FeatureCard = ({ title, description, icon: Icon }) => {
  return (
    <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition-shadow">
      <div className="bg-emerald-100 w-12 h-12 rounded-lg flex items-center justify-center mb-4">
        <Icon className="h-6 w-6 text-emerald-600" />
      </div>
      <h3 className="text-xl font-semibold mb-2 text-gray-800">{title}</h3>
      <p className="text-gray-600">{description}</p>
    </div>
  );
};

const HeroSection = () => {
  return (
    <div className="bg-gradient-to-br from-emerald-900 to-emerald-700 text-white py-20 px-8 rounded-3xl mb-12">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-5xl font-bold mb-6">
          Transform Your ESG Reporting Process
        </h1>
        <p className="text-xl text-emerald-100 mb-8">
          Generate comprehensive ESG reports, track sustainability metrics, and make data-driven decisions with our powerful platform.
        </p>
        <div className="flex gap-4">
          <button className="bg-white text-emerald-900 px-6 py-3 rounded-lg font-semibold hover:bg-emerald-50 transition-colors flex items-center">
            Get Started <ArrowRight className="ml-2 h-5 w-5" />
          </button>
          <button className="border border-white px-6 py-3 rounded-lg font-semibold hover:bg-emerald-800 transition-colors">
            Watch Demo
          </button>
        </div>
      </div>
    </div>
  );
};

const StatsSection = () => {
  const stats = [
    { value: '2M+', label: 'Carbon Data Points' },
    { value: '500+', label: 'Companies' },
    { value: '98%', label: 'Accuracy Rate' },
    { value: '24/7', label: 'Support' },
  ];

  return (
    <div className="grid grid-cols-2 md:grid-cols-4 gap-8 bg-emerald-50 p-8 rounded-2xl">
      {stats.map((stat, index) => (
        <div key={index} className="text-center">
          <div className="text-3xl font-bold text-emerald-900 mb-2">{stat.value}</div>
          <div className="text-emerald-700">{stat.label}</div>
        </div>
      ))}
    </div>
  );
};




const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    fontFamily: 'Arial, sans-serif',
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #064e3b, #276749)',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed',
    height: '100vh',
    zIndex: 10,
    boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
    transition: 'width 0.3s ease',
  },
  logo: {
    fontSize: '30px',
    marginBottom: '30px',
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
    marginTop: '20px',
    transition: 'transform 0.3s ease',
  },
  logoHover: {
    transform: 'scale(1.1)',
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    width: '100%',
  },
  navItem: {
    color: 'white',
    textDecoration: 'none',
    padding: '15px 20px',
    display: 'flex',
    alignItems: 'center',
    width: '100%',
    borderRadius: '8px',
    marginBottom: '12px',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    cursor: 'pointer',
  },
  navItemHover: {
    backgroundColor: '#3b6b5c',
    transform: 'scale(1.05)',
  },
  navIcon: {
    marginRight: '20px',
    transition: 'transform 0.3s ease',
  },
  content: {
    flex: 1,
    padding: '30px',
    boxSizing: 'border-box',
    backgroundColor: '#fff',
    overflowY: 'auto',
    marginLeft: '250px',
    transition: 'margin-left 0.3s ease',
  },
  header: {
    marginBottom: '30px',
    borderBottom: '2px solid #f0f0f0',
    paddingBottom: '10px',
  },
  mainSection: {
    padding: '30px',
    backgroundColor: '#f9f9f9',
    borderRadius: '12px',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
    marginBottom: '30px',
    transition: 'box-shadow 0.3s ease',
  },
  button: {
    backgroundColor: '#fff',
    color: '#276749',
    padding: '12px 25px',
    borderRadius: '8px',
    border: 'none',
    fontWeight: 'bold',
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
    transition: 'transform 0.3s ease, background-color 0.3s ease, box-shadow 0.3s ease',
