import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaUserCircle } from 'react-icons/fa';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  UserCircle,
  HelpCircle,
  CloudUpload,
} from 'lucide-react';
import GreenTrendz from '../assets/img/logo_1.png';
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend } from 'recharts';

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [activeSection, setActiveSection] = useState('environmental');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5003/api/esg-data');
        setData(response.data);
      } catch (error) {
        console.error('Error fetching ESG data:', error);
        setError('Failed to fetch data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) return <p>Loading ESG data...</p>;
  if (error) return <p>{error}</p>;

  const filteredData = data.find((item) => item.section === activeSection)?.metrics || {};
  const chartData = Object.entries(filteredData).map(([key, value]) => ({
    name: key,
    value: parseFloat(value) || 0,
  }));

  return (
    <div className="dashboard-container">
      <nav className="sidebar">
        <div className="logo-container">
          <img src={GreenTrendz} alt="Logo" width="50" height="50" />
          <h5>GreenTrendz</h5>
        </div>
        <ul className="nav-list">
          {[{ icon: Home, label: 'Home', path: '/' }, { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' }].map((item) => (
            <li key={item.path} onClick={() => navigate(item.path)}>
              <item.icon size={20} /> {item.label}
            </li>
          ))}
        </ul>
        <button className="logout-btn" onClick={() => navigate('/')}>Logout</button>
      </nav>
      <main className="content">
        <h1>ESG Dashboard</h1>
        <div className="button-group">
          {['environmental', 'social', 'governance'].map((section) => (
            <button
              key={section}
              className={activeSection === section ? 'active' : ''}
              onClick={() => setActiveSection(section)}
            >
              {section.charAt(0).toUpperCase() + section.slice(1)}
            </button>
          ))}
        </div>
        <div className="chart-container">
          <h2>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Metrics</h2>
          <BarChart width={500} height={300} data={chartData}>
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="value" fill="#8884d8" />
          </BarChart>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
