import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  UserCircle,
  HelpCircle,
  CloudUpload,
} from 'lucide-react';
import GreenTrendz from '../assets/img/logo_1.png';
import { Card, CardHeader, CardTitle, CardContent } from './ui/card';
import { Button } from './ui/button';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  PieChart,
  Pie,
  LineChart,
  Line,
  AreaChart,
  Area,
  RadarChart,
  Radar,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
} from 'recharts';

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'CarbonCalculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
  { icon: UserCircle, label: 'Account', path: '/account' },
];

const Dashboard = () => {
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState('environmental');
  const [data] = useState({
    environmental: {
      renewableEnergyUsage: '75%',
      totalEnergyConsumption: '5000 kWh',
      greenBuildingCertifications: 'LEED Gold',
      ghgEmissionTargets: '20% reduction',
      wastewaterReused: '30%',
      wasteSentToRecyclers: '80%',
    },
    social: {
      genderDiversity: '45% female',
      employeeTrainingHours: '20 hours',
      volunteerismHoursPerEmployee: '10 hours',
      communityEngagementHours: '1000 hours',
    },
    governance: {
      independentDirectors: '40%',
      regulatoryComplianceStatus: 'Yes',
      employeesTrainedOnCodeOfConduct: '90%',
      ethicsViolationsReported: '2',
    },
  });

  // Ensure data[activeSection] exists
  const sectionData = data[activeSection] || {};

  // Chart Data
  const chartData = {
    environmental: [
      { name: 'Renewable Energy', value: 75 },
      { name: 'Wastewater Reused', value: 30 },
      { name: 'Recycled Water', value: 40 },
      { name: 'Waste to Recyclers', value: 80 },
    ],
    social: [
      { name: 'Gender Diversity', value: 45 },
      { name: 'Training Hours', value: 20 },
      { name: 'Volunteerism Hours', value: 10 },
      { name: 'Community Hours', value: 1000 },
    ],
    governance: [
      { name: 'Independent Directors', value: 40 },
      { name: 'Employees Trained', value: 90 },
      { name: 'Ethics Violations', value: 2 },
    ],
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <nav className="w-64 bg-green-700 text-white p-5 flex flex-col fixed h-full shadow-md">
        <div className="flex items-center space-x-3">
          <img src={GreenTrendz} alt="Logo" width="50" />
          <h5 className="text-xl font-bold">GreenTrendz</h5>
        </div>

        <ul className="mt-6 space-y-2">
          {menuItems.map((item) => (
            <li key={item.path}>
              <a href={item.path} className="flex items-center space-x-2 px-4 py-2 hover:bg-green-800 rounded">
                <item.icon size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>

        <button
          className="mt-auto bg-white text-green-700 p-2 rounded flex items-center justify-center space-x-2"
          onClick={() => setShowLogoutModal(true)}
        >
          <Settings size={18} />
          <span>Logout</span>
        </button>
      </nav>

      {/* Main Content */}
      <main className="ml-64 p-6 flex-1">
        <h1 className="text-3xl font-bold mb-4">ESG Dashboard</h1>

        {/* Toggle Buttons */}
        <div className="flex space-x-4 mb-6">
          {['environmental', 'social', 'governance'].map((section) => (
            <Button
              key={section}
              onClick={() => setActiveSection(section)}
              variant={activeSection === section ? 'default' : 'outline'}
              className="w-1/3 bg-green-600 hover:bg-green-700 text-white"
            >
              {section.charAt(0).toUpperCase() + section.slice(1)}
            </Button>
          ))}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Metrics Card */}
          <Card>
            <CardHeader>
              <CardTitle>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Metrics</CardTitle>
            </CardHeader>
            <CardContent>
              {Object.entries(sectionData).map(([key, value]) => (
                <div key={key} className="flex justify-between border-b py-2">
                  <span className="text-gray-600">{key.replace(/([A-Z])/g, ' $1').trim()}</span>
                  <span className="font-bold">{value}</span>
                </div>
              ))}
            </CardContent>
          </Card>

          {/* Chart Card */}
          <Card>
            <CardHeader>
              <CardTitle>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Visualization</CardTitle>
            </CardHeader>
            <CardContent>
              <PieChart width={400} height={300}>
                <Pie data={chartData[activeSection]} dataKey="value" cx="50%" cy="50%" outerRadius={100} fill="#82ca9d" label />
                <Tooltip />
                <Legend />
              </PieChart>
            </CardContent>
          </Card>
        </div>
      </main>

      {/* Logout Modal */}
      {showLogoutModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-96">
            <h3 className="text-xl font-bold mb-4">Confirm Logout</h3>
            <p className="text-gray-600 mb-6">Are you sure you want to logout?</p>
            <div className="flex justify-end space-x-4">
              <Button variant="outline" onClick={() => setShowLogoutModal(false)}>
                Cancel
              </Button>
              <Button onClick={() => navigate('/')}>Yes, Logout</Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
