import { BarElement, CategoryScale, Chart, Legend, LinearScale, Title, Tooltip } from 'chart.js';
import React from 'react';
import { Bar } from 'react-chartjs-2';

// Register the required components
Chart.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const EsgDataVisualization = ({ esgData }) => {
    // Prepare data for chart
    const chartData = {
        labels: esgData.map((data) => data.companyName || 'Unknown Company'), // Company names
        datasets: [
            {
                label: 'Energy Usage (kWh)',
                data: esgData.map((data) => data.extractedMetrics?.energyUsage || 0), // Energy usage data
                backgroundColor: 'rgba(75, 192, 192, 0.6)',
            },
            {
                label: 'Carbon Emissions (tons)',
                data: esgData.map((data) => data.extractedMetrics?.carbonEmissions || 0),
                backgroundColor: 'rgba(255, 99, 132, 0.6)',
            },
            {
                label: 'Waste Management (%)',
                data: esgData.map((data) => data.extractedMetrics?.wasteManagement || 0),
                backgroundColor: 'rgba(54, 162, 235, 0.6)',
            },
            {
                label: 'Employee Diversity (%)',
                data: esgData.map((data) => data.extractedMetrics?.employeeDiversity || 0),
                backgroundColor: 'rgba(255, 206, 86, 0.6)',
            },
            {
                label: 'Governance Score',
                data: esgData.map((data) => data.extractedMetrics?.governanceScore || 0),
                backgroundColor: 'rgba(153, 102, 255, 0.6)',
            },
        ],
    };

    return (
        <div>
            <h2>ESG Data Visualization</h2>
            {esgData.length > 0 ? (
                <Bar data={chartData} /> // Bar chart visualization
            ) : (
                <p>No ESG data available</p>
            )}
        </div>
    );
};

export default EsgDataVisualization;
