import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';

import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    UserCircle,
    HelpCircle,
    ArrowRight,

    CloudUpload,
    Trophy,
    Italic,  // Added new icon for benchmarking
  } from 'lucide-react';

  const styles = {
    landingPage: {
      display: 'flex',
      height: '100vh',
      backgroundColor: '#f4f4f9',
      fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
      width: '250px',
      background: 'linear-gradient(to bottom right, #064e3b, #276749)',
      color: 'white',
      padding: '20px',
      boxSizing: 'border-box',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'flex-start',
      position: 'fixed',
      height: '100vh',
      zIndex: 10,
      boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
      transition: 'width 0.3s ease',
    },
    logo: {
      fontSize: '25px',
      marginBottom: '30px',
      fontWeight: 'bold',
      color: '#fff',
      textAlign: 'center',
      marginTop: '20px',
      transition: 'transform 0.3s ease',
    },
    logoHover: {
      transform: 'scale(1.1)',
    },
    navList: {
      listStyle: 'none',
      padding: 0,
      margin: 0,
      width: '100%',
    },
    navItem: {
      color: 'white',
      textDecoration: 'none',
      padding: '15px 20px',
      display: 'flex',
      alignItems: 'center',
      width: '100%',
      borderRadius: '8px',
      marginBottom: '12px',
      transition: 'background-color 0.3s ease, transform 0.3s ease',
      cursor: 'pointer',
      fontfamily: "'Poppins', sans-serif", // Custom font for links
          fontWeight: '500', // Optional for bold text
      },
        navItemHover: {
      backgroundColor: '#3b6b5c',
      transform: 'scale(1.05)',
    },
    navIcon: {
      marginRight: '20px',
      transition: 'transform 0.3s ease',
    },
    content: {
      flex: 1,
      padding: '30px',
      boxSizing: 'border-box',
      backgroundColor: '#fffff',
      overflowY: 'auto',
      marginLeft: '250px',
      transition: 'margin-left 0.3s ease',

    },


    button: {
      backgroundColor: 'linear-gradient(to right, #38a169, #2f855a)', // Green gradient
      color: 'white',
      padding: '12px 25px',
      borderRadius: '8px',
      border: 'none',
      fontWeight: 'bold',
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      transition: 'transform 0.3s ease, background-color 0.3s ease, box-shadow 0.3s ease',
      boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
      cursor: 'pointer',
    },
    buttonHover: {
      transform: 'scale(1.05)',
      backgroundColor: 'linear-gradient(to right, #2f855a, #22543d)', // Darker green gradient
      color: 'white',
      boxShadow: '0 6px 15px rgba(0, 0, 0, 0.2)',
    },
    buttonOutline: {
      backgroundColor: 'transparent',
      color: '#2f855a',
      padding: '12px 25px',
      borderRadius: '8px',
      border: '2px solid #2f855a',
      fontWeight: 'bold',
      transition: 'background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease',
    },
    buttonOutlineHover: {
      backgroundColor: '#2f855a',
      color: 'white',
      transform: 'scale(1.05)',
      boxShadow: '0 6px 15px rgba(0, 0, 0, 0.1)',
    },

    header: {    
      fontSize: '50px',    
      fontWeight: 1000, // Use numeric font weight    
      marginBottom: '10px', 
      marginLeft: '90px',   
      fontFamily: "'Roboto', sans-serif", // Make sure to use a font that supports 900 weight    
      color: '#ffffff',

    },
      button: {
        backgroundColor: '#ffffff', // Default background color
        color: 'Black', // Text color
        border: 'none', // No border
        padding: '10px 20px', // Padding for a nice clickable area
        borderRadius: '5px', // Rounded corners
        fontWeight: 'bold', // Bold text
        fontSize: '16px', // Font size
        cursor: 'pointer', // Pointer cursor on hover
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Subtle shadow for a 3D effect
        transition: 'background-color 0.3s ease, transform 0.2s ease', // Smooth transition for hover effects
      },
    };

const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
    { icon: UserCircle, label: 'Account', path: '/account' }, // Added Account item
  ];

const AccountPage = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    address: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission logic here (e.g., API call to create the account).
    console.log(formData);
  };

  return (
    <div className="account-page-container">
        <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img
                        src={GreenTrendz}
                        alt="Logo"
                        width="50"
                        height="50"
                        className="d-inline-block align-top me-2"
                    />
                    <h5 style={styles.logo}>GreenTrendz</h5>
                </div>
                <ul style={styles.navList}>
                    {menuItems.map((item) => (
                        <li key={item.path}>
                            <a
                                href={item.path}
                                style={styles.navItem}
                                onMouseEnter={(e) => {
                                    e.target.style.backgroundColor = '#3b6b5c';
                                    e.target.style.transform = 'scale(1.05)';
                                }}
                                onMouseLeave={(e) => {
                                    e.target.style.backgroundColor = '';
                                    e.target.style.transform = '';
                                }}
                            >
                                <item.icon style={styles.navIcon} size={20} />
                                <span>{item.label}</span>
                            </a>
                        </li>
                    ))}
                </ul>
                <div style={{ marginTop: 'auto', textAlign: 'center' }}>
                    <button
                        style={styles.button}
                    >
                        Logout
                    </button>
                </div>
            </nav>
            <h2 style={{ color: '#064e3b', marginBottom: '20px' }}>Account Details</h2>

        {isEditing ? (
          <form onSubmit={handleSave}>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="companyName" style={{ display: 'block', marginBottom: '5px' }}>Company Name</label>
              <input
                type="text"
                id="companyName"
                name="companyName"
                value={formData.companyName}
                onChange={handleChange}
                style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="email" style={{ display: 'block', marginBottom: '5px' }}>Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="phone" style={{ display: 'block', marginBottom: '5px' }}>Phone</label>
              <input
                type="text"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="address" style={{ display: 'block', marginBottom: '5px' }}>Address</label>
              <input
                type="text"
                id="address"
                name="address"
                value={formData.address}
                onChange={handleChange}
                style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
              />
            </div>
            <button type="submit" style={{
              background: '#38a169',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              marginRight: '10px',
              cursor: 'pointer',
            }}>Save</button>
            <button type="button" onClick={handleCancel} style={{
              background: '#e53e3e',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}>Cancel</button>
          </form>
        ) : (
          <div>
            <p><strong>Company Name:</strong> {userData.companyName}</p>
            <p><strong>Email:</strong> {userData.email}</p>
            <p><strong>Phone:</strong> {userData.phone}</p>
            <p><strong>Address:</strong> {userData.address}</p>
            <button onClick={handleEdit} style={{
              background: '#3182ce',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}>Edit</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AccountPage;
