import React from 'react';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
  ArrowRight,
  ShieldCheck,
  BarChart,
  Globe,
  CloudUpload
} from 'lucide-react';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    fontFamily: 'Arial, sans-serif', // Added for consistent font across the page
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #2f855a, #276749)',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed',
    height: '100vh',
    zIndex: 10,
    boxShadow: '2px 0 10px rgba(0, 0, 0, 0.15)', // Deepened shadow for more depth
    transition: 'width 0.3s ease', // Smooth transition for sidebar width if expanded
  },
  logo: {
    fontSize: '28px', // Increased font size for better visibility
    marginBottom: '30px',
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
    marginTop: '20px',
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    width: '100%',
  },
  navItem: {
    color: 'white',
    textDecoration: 'none',
    padding: '15px 20px', // Increased padding for a larger click area
    display: 'flex',
    alignItems: 'center',
    width: '100%',
    borderRadius: '8px', // Rounded corners for nav items
    marginBottom: '12px',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
  },

  navIcon: {
    marginRight: '20px', // Increased space between icon and text
    transition: 'transform 0.3s ease', // Smooth icon scaling
  },
  content: {
    flex: 1,
    padding: '30px', // Increased padding for better spacing
    boxSizing: 'border-box',
    backgroundColor: '#fff',
    overflowY: 'auto',
    marginLeft: '250px',
    transition: 'margin-left 0.3s ease', // Smooth transition for content margin when sidebar expands
  },
  header: {
    marginBottom: '30px',
    borderBottom: '2px solid #f0f0f0',
    paddingBottom: '10px',
  },
  mainSection: {
    padding: '30px',
    backgroundColor: '#f9f9f9',
    borderRadius: '12px', // Rounded corners for main sections
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)', // Subtle shadow for depth
    marginBottom: '30px',
    transition: 'box-shadow 0.3s ease', // Smooth box shadow effect on hover
  },
  button: {
    backgroundColor: '#fff',
    color: '#276749',
    padding: '12px 25px',
    borderRadius: '8px',
    border: 'none',
    fontWeight: 'bold',
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
    transition: 'transform 0.3s ease, background-color 0.3s ease', // Smooth transform and color change
  },
  buttonHover: {
    transform: 'scale(1.05)', // Scale effect on hover for emphasis
    backgroundColor: '#276749', // Change background on hover
    color: '#fff', // Change text color on hover
  },
  buttonOutline: {
    backgroundColor: 'transparent',
    color: '#fff',
    padding: '12px 25px',
    borderRadius: '8px',
    border: '1px solid #fff',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
  },
  buttonOutlineHover: {
    backgroundColor: '#fff',
    color: '#276749',
    transform: 'scale(1.05)',
  },
};

// Features Data
const features = [
  {
    title: 'Automated Reporting',
    description: 'Generate ESG and BRSR reports effortlessly using AI.',
    icon: ShieldCheck,
  },
  {
    title: 'Data Analytics',
    description: 'Visualize and analyze your sustainability metrics.',
    icon: BarChart,
  },
  {
    title: 'Global Standards',
    description: 'Adhere to international ESG guidelines.',
    icon: Globe,
  },
];

// Stats Data
const stats = [
  { value: '2M+', label: 'Carbon Data Points' },
  { value: '500+', label: 'Companies' },
  { value: '98%', label: 'Accuracy Rate' },
  { value: '24/7', label: 'Support' },
];

const menuItems = [
  { icon: Home, label: 'Home', path: '/' },
  { icon: CloudUpload, label: 'Data Upload', path: '/data-upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
];

const LandingPage = () => {
  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
        <h2 style={styles.logo}>Platform Logo</h2>
        <ul style={styles.navList}>
          {menuItems.map((item) => (
            <li key={item.path}>
              <a
                href={item.path}
                style={styles.navItem}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#3e4a61';
                  e.target.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '';
                  e.target.style.transform = '';
                }}
              >
                <item.icon style={styles.navIcon} size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>
      </nav>

      {/* Main Content */}
      <main style={styles.content}>
        {/* Hero Section */}
        <div
          style={{
            background: 'linear-gradient(to bottom right, #2f855a, #276749)',
            color: 'white',
            padding: '50px',
            borderRadius: '12px',
            marginBottom: '30px',
            textAlign: 'center',
          }}
        >
          <h1 style={{ fontSize: '42px', fontWeight: 'bold', marginBottom: '20px' }}>
            Transform Your ESG Reporting Process
          </h1>
          <p style={{ fontSize: '20px', marginBottom: '30px' }}>
            Generate comprehensive ESG reports, track sustainability metrics, and make data-driven decisions with our powerful platform.
          </p>
          <div style={{ display: 'flex', gap: '15px', justifyContent: 'center' }}>
            <button
              style={{ ...styles.button, ...styles.buttonHover }}
            >
              Get Started <ArrowRight size={20} />
            </button>
            <button
              style={{ ...styles.buttonOutline, ...styles.buttonOutlineHover }}
            >
              Watch Demo
            </button>
          </div>
        </div>

        {/* Features Section */}
        <section style={styles.mainSection}>
          <h2 style={{ fontSize: '30px', fontWeight: 'bold', marginBottom: '20px' }}>
            Why Choose Our Platform?
          </h2>
          <div style={{ display: 'flex', gap: '25px', flexWrap: 'wrap' }}>
            {features.map((feature, index) => (
              <div
                key={index}
                style={{
                  backgroundColor: '#fff',
                  padding: '25px',
                  borderRadius: '12px',
                  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
                  flex: '1 1 calc(33% - 30px)',
                  transition: 'box-shadow 0.3s ease',
                }}
                onMouseEnter={(e) => {
                  e.target.style.boxShadow = '0 4px 15px rgba(0,0,0,0.2)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.boxShadow = '0 4px 12px rgba(0,0,0,0.1)';
                }}
              >
                <feature.icon size={40} style={{ marginBottom: '15px' }} />
                <h3 style={{ fontSize: '22px', fontWeight: 'bold' }}>{feature.title}</h3>
                <p>{feature.description}</p>
              </div>
            ))}
          </div>
        </section>

        {/* Stats Section */}
        <section style={styles.mainSection}>
          <h2 style={{ fontSize: '30px', fontWeight: 'bold', marginBottom: '20px' }}>Our Impact</h2>
          <div style={{ display: 'flex', gap: '30px' }}>
            {stats.map((stat, index) => (
              <div key={index} style={{ textAlign: 'center' }}>
                <p style={{ fontSize: '40px', fontWeight: 'bold', color: '#276749' }}>
                  {stat.value}
                </p>
                <p>{stat.label}</p>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default LandingPage;
