import React, { useState } from 'react';

const styles = {
  body: {
    fontFamily: "'Poppins', sans-serif",
    backgroundColor: '#f4f7fc',
    margin: 0,
    padding: 0,
    color: '#333',
  },
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column',
    padding: '40px',
  },
  section: {
    backgroundColor: '#ffffff',
    borderRadius: '8px',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
    marginBottom: '30px',
    padding: '25px',
    width: '80%',
    maxWidth: '800px',
  },
  sectionTitle: {
    fontSize: '24px',
    fontWeight: '600',
    color: '#2e3a59',
    marginBottom: '20px',
  },
  h3: {
    fontSize: '20px',
    fontWeight: '500',
    color: '#2e3a59',
    marginBottom: '15px',
  },
  label: {
    fontSize: '16px',
    marginBottom: '5px',
    display: 'block',
    color: '#5f6368',
  },
  inputField: {
    width: '100%',
    padding: '12px',
    borderRadius: '8px',
    border: '1px solid #ddd',
    fontSize: '16px',
    marginBottom: '15px',
    transition: 'border-color 0.3s ease',
  },
  inputFieldFocus: {
    borderColor: '#6200ea',
    outline: 'none',
  },
  fileInput: {
    padding: '10px',
    border: 'none',
    backgroundColor: '#f9f9f9',
    cursor: 'pointer',
  },
  preview: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '10px',
  },
  previewImg: {
    width: '100px',
    borderRadius: '8px',
  },
  previewP: {
    color: '#5f6368',
  },
  button: {
    backgroundColor: '#6200ea',
    color: 'white',
    padding: '12px 30px',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonHover: {
    backgroundColor: '#3700b3',
  },
  buttonActive: {
    backgroundColor: '#03dac5',
  },
  '@media (max-width: 768px)': {
    container: {
      padding: '20px',
    },
    section: {
      width: '100%',
    },
  },
};

const ESGUploadForm = () => {
  const [environmentalData, setEnvironmentalData] = useState({
    totalEnergyConsumed: '',
    renewableEnergyUsage: '',
    dataCenterPUE: '',
    scope1Emissions: '',
    scope2Emissions: '',
    scope3Emissions: '',
    carbonOffsets: '',
    waterConsumption: '',
    waterRecycled: '',
    eWasteGenerated: '',
    eWasteRecycled: '',
    recyclabilityPercentage: '',
    ethicalSourcingPercentage: '',
    sustainabilityCertifications: '',
    renewableEnergyCertificates: '',
  });

  const [socialData, setSocialData] = useState({
    employeeDiversity: '',
    employeeEngagement: '',
    employeeTurnover: '',
    communityInitiatives: '',
    employeeSatisfaction: '',
    trainingHours: '',
    workplaceIncidents: '',
    genderPayGap: '',
  });

  const [governanceData, setGovernanceData] = useState({
    boardDiversity: '',
    cybersecurityIncidents: '',
    complianceViolations: '',
    ceoPayRatio: '',
    independentDirectors: '',
    esgOversightBudget: '',
    whistleblowerReports: '',
    stakeholderMeetings: '',
  });

  const [csvFile, setCsvFile] = useState(null);
  const [logoFile, setLogoFile] = useState(null);

  const handleCsvUpload = (e) => {
    setCsvFile(e.target.files[0]);
  };

  const handleLogoUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setLogoFile(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = () => {
    // Handle form submission
  };

  return (
    <div style={styles.container}>
      <div style={styles.section}>
        <h1 style={styles.sectionTitle}>Upload ESG Data</h1>

        <div style={styles.section}>
          <h2 style={styles.sectionTitle}>Annual ESG Metrics</h2>

          {/* Environmental Section */}
          <div>
            <h3 style={styles.h3}>Environmental</h3>
            {[
              // Energy Consumption
              'Total Energy Consumed (kWh)',
              'Renewable Energy Usage (%)',
              'Data Center Power Usage Effectiveness (PUE)',
              // Carbon Emissions
              'Scope 1 Emissions (metric tons CO₂e)',
              'Scope 2 Emissions (metric tons CO₂e)',
              'Scope 3 Emissions (metric tons CO₂e)',
              'Carbon Offset Projects and Reductions Achieved (metric tons CO₂e)',
              // Water Usage
              'Water Consumption (liters/year)',
              'Percentage of Water Recycled (%)',
              // Electronic Waste (E-Waste)
              'Volume of E-Waste Generated (tons/year)',
              'E-Waste Recycled (%)',
              // Sustainable Product Design
              'Percentage of Products Designed for Recyclability or Repairability',
              'Ethical Sourcing of Materials (% of total)',
              // Sustainability Certifications
              'List of Sustainability Certifications (e.g., ISO 14001)',
              'Renewable Energy Certificates Purchased',
            ].map((label, idx) => {
              const keys = [
                'totalEnergyConsumed',
                'renewableEnergyUsage',
                'dataCenterPUE',
                'scope1Emissions',
                'scope2Emissions',
                'scope3Emissions',
                'carbonOffsets',
                'waterConsumption',
                'waterRecycled',
                'eWasteGenerated',
                'eWasteRecycled',
                'recyclabilityPercentage',
                'ethicalSourcingPercentage',
                'sustainabilityCertifications',
                'renewableEnergyCertificates',
              ];
              return (
                <div key={idx}>
                  <label style={styles.label}>{label}</label>
                  {keys[idx] === 'sustainabilityCertifications' ||
                  keys[idx] === 'renewableEnergyCertificates' ? (
                    <input
                      type="text"
                      value={environmentalData[keys[idx]]}
                      onChange={(e) =>
                        setEnvironmentalData({
                          ...environmentalData,
                          [keys[idx]]: e.target.value,
                        })
                      }
                      style={styles.inputField}
                    />
                  ) : (
                    <input
                      type="number"
                      value={environmentalData[keys[idx]]}
                      onChange={(e) =>
                        setEnvironmentalData({
                          ...environmentalData,
                          [keys[idx]]: e.target.value,
                        })
                      }
                      style={styles.inputField}
                    />
                  )}
                </div>
              );
            })}
          </div>

          {/* Social Section */}
          <div>
            <h3 style={styles.h3}>Social</h3>
            {[
              'Employee Diversity (%)',
              'Employee Engagement Score',
              'Employee Turnover Rate (%)',
              'Community Initiatives Investment ($)',
              'Employee Satisfaction Score',
              'Training Hours per Employee (hours)',
              'Workplace Incidents Reported',
              'Gender Pay Gap (%)',
            ].map((label, idx) => {
              const keys = [
                'employeeDiversity',
                'employeeEngagement',
                'employeeTurnover',
                'communityInitiatives',
                'employeeSatisfaction',
                'trainingHours',
                'workplaceIncidents',
                'genderPayGap',
              ];
              return (
                <div key={idx}>
                  <label style={styles.label}>{label}</label>
                  <input
                    type="number"
                    value={socialData[keys[idx]]}
                    onChange={(e) =>
                      setSocialData({
                        ...socialData,
                        [keys[idx]]: e.target.value,
                      })
                    }
                    style={styles.inputField}
                  />
                </div>
              );
            })}
          </div>

          {/* Governance Section */}
          <div>
            <h3 style={styles.h3}>Governance</h3>
            {[
              'Board Diversity (%)',
              'Cybersecurity Incidents Reported',
              'Compliance Violations',
              'CEO Pay Ratio',
              'Independent Directors (%)',
              'ESG Oversight Budget ($)',
              'Number of Whistleblower Reports',
              'Stakeholder Engagement Meetings',
            ].map((label, idx) => {
              const keys = [
                'boardDiversity',
                'cybersecurityIncidents',
                'complianceViolations',
                'ceoPayRatio',
                'independentDirectors',
                'esgOversightBudget',
                'whistleblowerReports',
                'stakeholderMeetings',
              ];
              return (
                <div key={idx}>
                  <label style={styles.label}>{label}</label>
                  <input
                    type="number"
                    value={governanceData[keys[idx]]}
                    onChange={(e) =>
                      setGovernanceData({
                        ...governanceData,
                        [keys[idx]]: e.target.value,
                      })
                    }
                    style={styles.inputField}
                  />
                </div>
              );
            })}
          </div>

          {/* File Upload Section */}
          <div>
            <h3 style={styles.h3}>Upload CSV</h3>
            <input
              type="file"
              accept=".csv"
              onChange={handleCsvUpload}
              style={styles.inputField}
            />
            {csvFile && (
              <div style={styles.preview}>
                <p style={styles.previewP}>{csvFile.name}</p>
                <p style={styles.previewP}>
                  {Math.round(csvFile.size / 1024)} KB
                </p>
              </div>
            )}
          </div>

          {/* Logo Upload Section */}
          <div>
            <h3 style={styles.h3}>Upload Logo</h3>
            <input
              type="file"
              accept="image/*"
              onChange={handleLogoUpload}
              style={styles.inputField}
            />
            {logoFile && (
              <div style={styles.preview}>
                <img src={logoFile} alt="Logo" style={styles.previewImg} />
              </div>
            )}
          </div>

          {/* Submit Button */}
          <button
            style={styles.button}
            onClick={handleSubmit}
            onMouseOver={(e) => e.target.style.backgroundColor = styles.buttonHover.backgroundColor}
            onMouseOut={(e) => e.target.style.backgroundColor = styles.button.backgroundColor}
          >
            Submit Data
          </button>
        </div>
      </div>
    </div>
  );
};

export default ESGUploadForm;
