import axios from 'axios';
import { BarElement, CategoryScale, Chart, Legend, LinearScale, Title, Tooltip } from 'chart.js';
import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import './ManualInput.css'; // Import CSS for custom styling

// Register the required components
Chart.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const ManualInput = () => {
    const [formData, setFormData] = useState({
        companyName: '',
        energyUsage: '',
        carbonEmissions: '',
        wasteManagement: '',
        employeeDiversity: '',
        governanceScore: '',
    });
    const [message, setMessage] = useState('');
    const [esgData, setEsgData] = useState([]); // State to hold ESG data for visualization

    // Handle input changes dynamically
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(
                'http://localhost:5000/api/manual-input',
                formData
            );
            setMessage(response.data.message); // Success message from backend

            // Update ESG data for visualization
            setEsgData((prevData) => [...prevData, formData]); // Add new data to the state
            setFormData({ // Reset form fields
                companyName: '',
                energyUsage: '',
                carbonEmissions: '',
                wasteManagement: '',
                employeeDiversity: '',
                governanceScore: '',
            });
        } catch (error) {
            console.error('Error submitting form data:', error);
            setMessage('Error submitting data. Please try again.');
        }
    };

    // Prepare data for chart
    const chartData = {
        labels: esgData.map((data) => data.companyName || 'Unknown Company'), // Company names
        datasets: [
            {
                label: 'Energy Usage (kWh)',
                data: esgData.map((data) => data.energyUsage || 0), // Energy usage data
                backgroundColor: 'rgba(75, 192, 192, 0.6)',
            },
            {
                label: 'Carbon Emissions (tons)',
                data: esgData.map((data) => data.carbonEmissions || 0),
                backgroundColor: 'rgba(255, 99, 132, 0.6)',
            },
            {
                label: 'Waste Management (%)',
                data: esgData.map((data) => data.wasteManagement || 0),
                backgroundColor: 'rgba(54, 162, 235, 0.6)',
            },
            {
                label: 'Employee Diversity (%)',
                data: esgData.map((data) => data.employeeDiversity || 0),
                backgroundColor: 'rgba(255, 206, 86, 0.6)',
            },
            {
                label: 'Governance Score',
                data: esgData.map((data) => data.governanceScore || 0),
                backgroundColor: 'rgba(153, 102, 255, 0.6)',
            },
        ],
    };

    return (
        <div className="manual-input-container">
            <h2>Manual ESG Data Input</h2>
            <form onSubmit={handleSubmit} className="manual-input-form">
                {[
                    { label: 'Company Name', name: 'companyName', type: 'text' },
                    { label: 'Energy Usage (kWh)', name: 'energyUsage', type: 'number' },
                    { label: 'Carbon Emissions (tons)', name: 'carbonEmissions', type: 'number' },
                    { label: 'Waste Management (% recycled)', name: 'wasteManagement', type: 'number' },
                    { label: 'Employee Diversity (%)', name: 'employeeDiversity', type: 'number' },
                    { label: 'Governance Score (1-10)', name: 'governanceScore', type: 'number' },
                ].map((field, index) => (
                    <label key={index}>
                        {field.label}:
                        <input
                            type={field.type}
                            name={field.name}
                            value={formData[field.name]}
                            onChange={handleChange}
                            required
                        />
                    </label>
                ))}
                <button type="submit">Submit</button>
            </form>
            {message && <p className="message">{message}</p>}

           {/* Visualization */}
{esgData.length > 0 && (
    <div className="visualization-container">
        <h2>ESG Data Visualization</h2>
        <Bar data={chartData} />
    </div>
            )}
        </div>
    );
};

export default ManualInput;
