import axios from 'axios';
import React from 'react';
import ReportsDisplay from './ReportsDisplay'; // Import the ReportsDisplay component

const UploadReports = () => {
  const [file, setFile] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [uploading, setUploading] = useState(false);
  const [uploadedReports, setUploadedReports] = useState([]);
  const [showReports, setShowReports] = useState(false); // State to control showing reports

  // Function to handle file upload
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setErrorMessage('');
  };

  // Function to handle file upload to the backend
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setErrorMessage('Please select a file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      setUploading(true);
      const response = await axios.post('http://localhost:5000/api/upload-report', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('Upload response:', response.data);
      setUploadedReports((prevReports) => [...prevReports, response.data]); // Add uploaded report to the list
    } catch (error) {
      console.error('Error uploading the report:', error);
      setErrorMessage('Error uploading the report. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  // Function to fetch reports from the backend
  const fetchReports = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/get-reports'); // Existing route
      setUploadedReports(response.data); // Store fetched reports in state
    } catch (error) {
      console.error('Error fetching reports:', error);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Upload ESG Report</h2>
      {errorMessage && <p style={styles.errorMessage}>{errorMessage}</p>}

      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="file"
          onChange={handleFileChange}
          style={styles.fileInput}
        />
        <button type="submit" disabled={uploading} style={styles.button}>
          {uploading ? 'Uploading...' : 'Upload ESG Report'}
        </button>
      </form>

      {/* Button to show or hide the reports */}
      <button onClick={() => { fetchReports(); setShowReports(!showReports); }}>
        {showReports ? 'Hide Reports' : 'Show Reports'}
      </button>

      {/* Conditionally render the reports display */}
      {showReports && <ReportsDisplay reports={uploadedReports} />}
    </div>
  );
};

// Styles for the component
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px',
    backgroundColor: '#e9f7ef',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    maxWidth: '500px',
    margin: 'auto',
    fontFamily: 'Arial, sans-serif',
  },
  title: {
    color: '#2c3e50',
    marginBottom: '20px',
    fontSize: '24px',
  },
  errorMessage: {
    color: '#e74c3c',
    marginBottom: '15px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    width: '100%',
  },
  fileInput: {
    marginBottom: '15px',
    padding: '10px',
    border: '1px solid #b2e0c7',
    borderRadius: '4px',
    fontSize: '16px',
    backgroundColor: '#ffffff',
  },
  button: {
    padding: '10px',
    border: 'none',
    borderRadius: '4px',
    backgroundColor: '#66bb6a',
    color: '#fff',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
};

export default UploadReports;
