import React from 'react';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
  ArrowRight,
  ShieldCheck,
  BarChart,
  Globe,
  CloudUpload
} from 'lucide-react';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    fontFamily: 'Arial, sans-serif', // Ensure consistent font
    overflow: 'hidden', // Prevents unwanted scroll on the body
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #2f855a, #276749)',
    color: 'white',
    padding: '30px 20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed',
    height: '100vh',
    zIndex: 10,
    boxShadow: '2px 0 15px rgba(0, 0, 0, 0.2)',
    transition: 'width 0.3s ease',
  },
  logo: {
    fontSize: '32px', // Increased size for better visibility
    marginBottom: '40px',
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
    marginTop: '30px',
    textTransform: 'uppercase',
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    width: '100%',
  },
  navItem: {
    color: 'white',
    textDecoration: 'none',
    padding: '15px 25px',
    display: 'flex',
    alignItems: 'center',
    width: '100%',
    borderRadius: '8px',
    marginBottom: '15px',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    fontSize: '16px',
  },
  navItemHover: {
    backgroundColor: '#3b6b5c',
    transform: 'scale(1.05)',
  },
  navIcon: {
    marginRight: '20px',
    transition: 'transform 0.3s ease',
  },
  content: {
    flex: 1,
    padding: '40px',
    boxSizing: 'border-box',
    backgroundColor: '#fff',
    overflowY: 'auto',
    marginLeft: '250px',
    transition: 'margin-left 0.3s ease',
    color: '#333',
  },
  header: {
    marginBottom: '40px',
    borderBottom: '2px solid #f0f0f0',
    paddingBottom: '15px',
  },
  mainSection: {
    padding: '40px',
    backgroundColor: '#f9f9f9',
    borderRadius: '12px',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
    marginBottom: '40px',
    transition: 'box-shadow 0.3s ease',
  },
  button: {
    backgroundColor: '#fff',
    color: '#276749',
    padding: '14px 28px',
    borderRadius: '8px',
    border: 'none',
    fontWeight: 'bold',
    display: 'flex',
    alignItems: 'center',
    gap: '12px',
    transition: 'transform 0.3s ease, background-color 0.3s ease',
  },
  buttonHover: {
    transform: 'scale(1.05)',
    backgroundColor: '#276749',
    color: '#fff',
  },
  buttonOutline: {
    backgroundColor: 'transparent',
    color: '#fff',
    padding: '14px 28px',
    borderRadius: '8px',
    border: '1px solid #fff',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
  },
  buttonOutlineHover: {
    backgroundColor: '#fff',
    color: '#276749',
    transform: 'scale(1.05)',
  },
  featureCard: {
    backgroundColor: '#fff',
    padding: '25px',
    borderRadius: '12px',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
    flex: '1 1 calc(33% - 30px)',
    transition: 'box-shadow 0.3s ease',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
  },
  featureIcon: {
    marginBottom: '20px',
    color: '#276749',
  },
  statsItem: {
    textAlign: 'center',
    padding: '20px',
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
    flex: '1 1 calc(25% - 30px)',
    transition: 'box-shadow 0.3s ease',
  },
  statValue: {
    fontSize: '40px',
    fontWeight: 'bold',
    color: '#276749',
    marginBottom: '10px',
  },
};

const LandingPage = () => {
  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
        <h2 style={styles.logo}>Platform Logo</h2>
        <ul style={styles.navList}>
          {menuItems.map((item) => (
            <li key={item.path}>
              <a
                href={item.path}
                style={styles.navItem}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#3b6b5c';
                  e.target.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '';
                  e.target.style.transform = '';
                }}
              >
                <item.icon style={styles.navIcon} size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>
      </nav>

      {/* Main Content */}
      <main style={styles.content}>
        {/* Hero Section */}
        <div
          style={{
            background: 'linear-gradient(to bottom right, #2f855a, #276749)',
            color: 'white',
            padding: '60px 40px',
            borderRadius: '12px',
            marginBottom: '40px',
            textAlign: 'center',
          }}
        >
          <h1 style={{ fontSize: '48px', fontWeight: 'bold', marginBottom: '25px' }}>
            Transform Your ESG Reporting Process
          </h1>
          <p style={{ fontSize: '22px', marginBottom: '30px' }}>
            Generate comprehensive ESG reports, track sustainability metrics, and make data-driven decisions with our powerful platform.
          </p>
          <div style={{ display: 'flex', gap: '15px', justifyContent: 'center' }}>
            <button
              style={{ ...styles.button, ...styles.buttonHover }}
            >
              Get Started <ArrowRight size={20} />
            </button>
            <button
              style={{ ...styles.buttonOutline, ...styles.buttonOutlineHover }}
            >
              Watch Demo
            </button>
          </div>
        </div>

        {/* Features Section */}
        <section style={styles.mainSection}>
          <h2 style={{ fontSize: '34px', fontWeight: 'bold', marginBottom: '30px' }}>
            Why Choose Our Platform?
          </h2>
          <div style={{ display: 'flex', gap: '25px', flexWrap: 'wrap' }}>
            {features.map((feature, index) => (
              <div
                key={index}
                style={styles.featureCard}
                onMouseEnter={(e) => {
                  e.target.style.boxShadow = '0 4px 15px rgba(0, 0, 0, 0.2)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.boxShadow = '0 4px 12px rgba(0, 0, 0, 0.1)';
                }}
              >
                <feature.icon size={40} style={styles.featureIcon} />
                <h3 style={{ fontSize: '22px', fontWeight: 'bold', marginBottom: '15px' }}>
                  {feature.title}
                </h3>
                <p>{feature.description}</p>
              </div>
            ))}
          </div>
        </section>

        {/* Stats Section */}
        <section style={styles.mainSection}>
          <h2 style={{ fontSize: '34px', fontWeight: 'bold', marginBottom: '30px' }}>Our Impact</h2>
          <div style={{ display: 'flex', gap: '30px', justifyContent: 'space-between' }}>
            {stats.map((stat, index) => (
              <div key={index} style={styles.statsItem}>
                <p style={styles.statValue}>{stat.value}</p>
                <p>{stat.label}</p>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default LandingPage;
