import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';
import './style.css';  // If style.css is in the same folder as FileUploads.js


import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    UserCircle,
    HelpCircle,
    CloudUpload,
} from 'lucide-react';

const styles = {
    landingPage: {
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f4f4f9',
        fontFamily: 'Arial, sans-serif',
      },
      sidebar: {
        width: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        position: 'fixed',
        height: '100vh',
        zIndex: 10,
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
        transition: 'width 0.3s ease',
      },
      logo: {
        fontSize: '25px',
        marginBottom: '30px',
        fontWeight: 'bold',
        color: '#fff',
        textAlign: 'center',
        marginTop: '20px',
        transition: 'transform 0.3s ease',
      },
      logoHover: {
        transform: 'scale(1.1)',
      },
      navList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        width: '100%',
      },
      navItem: {
        color: 'white',
        textDecoration: 'none',
        padding: '15px 20px',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        borderRadius: '8px',
        marginBottom: '12px',
        transition: 'background-color 0.3s ease, transform 0.3s ease',
        cursor: 'pointer',
        fontfamily: "'Poppins', sans-serif", // Custom font for links
            fontWeight: '500', // Optional for bold text
        },
          navItemHover: {
        backgroundColor: '#3b6b5c',
        transform: 'scale(1.05)',
      },
      navIcon: {
        marginRight: '20px',
        transition: 'transform 0.3s ease',
      },

        content: {
            flex: 1,
            padding: '30px',
            boxSizing: 'border-box',
            backgroundColor: '#f8f9fa', // Light gray background for a softer look
            overflowY: 'auto',
            marginLeft: '250px',
            transition: 'margin-left 0.3s ease',
        },





    button: {
        backgroundColor: '#ffffff', // Default background color
        color: 'Black', // Text color
        border: 'none', // No border
        padding: '10px 20px', // Padding for a nice clickable area
        borderRadius: '5px', // Rounded corners
        fontWeight: 'bold', // Bold text
        fontSize: '16px', // Font size
        cursor: 'pointer', // Pointer cursor on hover
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Subtle shadow for a 3D effect
        transition: 'background-color 0.3s ease, transform 0.2s ease', // Smooth transition for hover effects
      },
      infoIcon: {
        marginLeft: '8px',
        cursor: 'pointer',
        fontSize: '6px',
        color: '#666',
        lineHeight: '1',
    },
    tooltip: {
        position: 'absolute',
        backgroundColor: '#333',
        color: '#fff',
        padding: '5px 10px',
        borderRadius: '4px',
        fontSize: '12px',
        zIndex: '1000',
        visibility: 'hidden',
        opacity: 0,
        transition: 'opacity 0.3s',
    },
    tooltipVisible: {
        visibility: 'visible',
        opacity: 1,
    },
};

const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
    { icon: UserCircle, label: 'Account', path: '/account' }, // Added Account item

];

const Sidebar = () => {
    const [showLogoutPopup, setShowLogoutPopup] = useState(false);

    const [tooltipVisible, setTooltipVisible] = useState({});

    const showTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: true }));
    };

    const hideTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: false }));
    };
    const [environmentalData, setEnvironmentalData] = useState({
        renewableEnergyUsage: '',
        totalEnergyConsumption: '',
        greenBuildingCertifications: '',
        ghgEmissionTargets: '',
        energyEfficientSystems: '',

        wastewaterReused: '',
        rainwaterHarvesting: '',
        recycledWaterUse: '',

        zeroWasteToLandfill: '',
        wasteSentToRecyclers: '',

        nativeLandscaping: '',
        biodiversityMeasures: '',
    });

    const [socialData, setSocialData] = useState({
        genderDiversity: '',
        employeeTrainingHours: '',
        volunteerismHoursPerEmployee: '',
        workforceSafetyIncidents: '',
        csrInitiatives: '',
        communityEngagementHours: '',
        sustainableCommunityDevelopment: '',
        humanRightsComplaints: '',
        wellCertifiedProjects: '',
        deiPrograms: '',
    });

    const [governanceData, setGovernanceData] = useState({
        independentDirectors: '',
        boardGovernanceInitiatives: '',
        regulatoryComplianceStatus: '',
        nonComplianceCases: '',
        employeesTrainedOnCodeOfConduct: '',
        ethicsViolationsReported: '',
        riskManagementFramework: '',
        riskAssessmentsConducted: '',
    });

    const [dataImportData, setDataImportData] = useState({
        startDate: '',
        endDate: ''
    });

    const [csvFile, setCsvFile] = useState(null);
    const [logoFile, setLogoFile] = useState(null);

    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleCsvUpload = (e) => {
        const file = e.target.files[0];
        if (file && file.type !== 'text/csv') {
            alert('Please upload a valid CSV file.');
            return;
        }
        setCsvFile(file);
    };

    const handleLogoUpload = (e) => {
        const file = e.target.files[0];
        if (file && !['image/png', 'image/jpeg'].includes(file.type)) {
            alert('Please upload a valid image file (PNG or JPEG).');
            return;
        }
        setLogoFile(file);
    };

    const handleSubmit = async () => {
        // Validation: Check if start and end date are provided when uploading a CSV
        if (!csvFile && (!dataImportData.startDate || !dataImportData.endDate)) {
            alert('Please select both start and end dates if uploading a CSV file.');
            return;
        }

        if (!csvFile || !logoFile) {
            alert('Please upload both CSV and Logo files.');
            return;
        }

        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        const formData = new FormData();
        formData.append('environmentalData', JSON.stringify(environmentalData));
        formData.append('socialData', JSON.stringify(socialData));
        formData.append('governanceData', JSON.stringify(governanceData));
        formData.append('startDate', dataImportData.startDate);
        formData.append('endDate', dataImportData.endDate);

        if (csvFile) formData.append('csvFile', csvFile);
        if (logoFile) formData.append('logoFile', logoFile);
   // Debugging: Log form data for inspection
   console.log('Submitting Form Data:');
   console.log('Environmental Data:', environmentalData);
   console.log('Social Data:', socialData);
   console.log('Governance Data:', governanceData);
   console.log('Start Date:', dataImportData.startDate);
   console.log('End Date:', dataImportData.endDate);

        try {
            const response = await fetch('http://localhost:5003/api/save-esg-data', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const result = await response.json();
                setSuccessMessage(result.message || 'Data submitted successfully!');
            } else {
                const errorDetail = await response.text();
                throw new Error(`Error ${response.status}: ${errorDetail}`);
            }
        } catch (error) {
            setErrorMessage('Error submitting data: ' + error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.landingPage}>
            <nav style={styles.sidebar}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img
                        src={GreenTrendz}
                        alt="Logo"
                        width="50"
                        height="50"
                        className="d-inline-block align-top me-2"
                    />
                    <h5 style={styles.logo}>GreenTrendz</h5>
                </div>
                <ul style={styles.navList}>
                    {menuItems.map((item) => (
                        <li key={item.path}>
                            <a
                                href={item.path}
                                style={styles.navItem}
                                onMouseEnter={(e) => {
                                    e.target.style.backgroundColor = '#3b6b5c';
                                    e.target.style.transform = 'scale(1.05)';
                                }}
                                onMouseLeave={(e) => {
                                    e.target.style.backgroundColor = '';
                                    e.target.style.transform = '';
                                }}
                            >
                                <item.icon style={styles.navIcon} size={20} />
                                <span>{item.label}</span>
                            </a>
                        </li>
                    ))}
                </ul>
                <div style={{ marginTop: 'auto', textAlign: 'center' }}>
                    <button
                        style={styles.button}
                        onClick={() => setShowLogoutPopup(true)}
                    >
                        Logout
                    </button>
                </div>
            </nav>
            <div style={styles.content}>
                <div className="container">
                    <div className="section">
                        <h1 className="sectionTitle">Upload ESG Data</h1>
                        <h2 className="sectionTitle">Annual ESG Metrics</h2>

                        {/* Environmental Section */}
                        <div className="section">
                            <h3>Environmental</h3>
                            <div className="inputContainer">
                                {[
                                    { label: 'Renewable Energy Use (%)', key: 'renewableEnergyUsage', type: 'number' },
                                    { label: 'Total Energy Consumption (GJ)', key: 'totalEnergyConsumption', type: 'number' },
                                    { label: 'Green Building Certifications (%)', key: 'greenBuildingCertifications', type: 'number' },
                                    { label: 'GHG Emissions Reduction Targets', key: 'ghgEmissionTargets', type: 'text' },
                                    { label: 'Energy-efficient Systems', key: 'energyEfficientSystems', type: 'dropdown', options: ['Renewable', 'Grid', 'Fuel Energy'] },
                                    { label: 'Wastewater Reused (KL)', key: 'wastewaterReused', type: 'number' },
                                    { label: 'Rainwater Harvesting', key: 'rainwaterHarvesting', type: 'dropdown', options: ['Yes', 'No'] },
                                    { label: 'Recycled Water Use (%)', key: 'recycledWaterUse', type: 'number' },
                                    { label: 'Zero Waste to Landfill', key: 'zeroWasteToLandfill', type: 'dropdown', options: ['Yes', 'No'] },
                                    { label: 'Waste Sent to Recyclers (MT)', key: 'wasteSentToRecyclers', type: 'number' },
                                    { label: 'Native Landscaping Implemented', key: 'nativeLandscaping', type: 'dropdown', options: ['Yes', 'No'] },
                                    { label: 'Biodiversity Enhancement Measures', key: 'biodiversityMeasures', type: 'dropdown', options: ['Yes', 'No'] },
                                ].map(({ label, key, type, options }, idx) => (
                                    <div key={idx}>
                                        <label>{label}</label>
                                        {type === 'dropdown' ? (
                                            <select
                                                value={environmentalData[key]}
                                                onChange={(e) =>
                                                    setEnvironmentalData({
                                                        ...environmentalData,
                                                        [key]: e.target.value,
                                                    })
                                                }
                                                className="inputField"
                                            >
                                                {options.map((option, i) => (
                                                    <option key={i} value={option}>
                                                        {option}
                                                    </option>
                                                ))}
                                            </select>
                                        ) : (
                                            <input
                                                type={type}
                                                value={environmentalData[key]}
                                                onChange={(e) =>
                                                    setEnvironmentalData({
                                                        ...environmentalData,
                                                        [key]: e.target.value,
                                                    })
                                                }
                                                className="inputField"
                                            />
                                        )}
                                    </div>
                                ))}
                            </div>
                        </div>

                        {/* Social Section */}
                        <div className="section">
                            <h3>Social</h3>
                            <div className="inputContainer">
                                {[
                                    { label: 'Gender Diversity (%)', key: 'genderDiversity', type: 'number' },
                                    { label: 'Employee Training Hours (Total)', key: 'employeeTrainingHours', type: 'number' },
                                    { label: 'Volunteerism Hours per Employee (Avg.)', key: 'volunteerismHoursPerEmployee', type: 'number' },
                                    { label: 'Workforce Safety Incidents', key: 'workforceSafetyIncidents', type: 'dropdown', options: ['LTIFR', 'Fatalities'] },
                                    { label: 'CSR Initiatives Conducted (Count)', key: 'csrInitiatives', type: 'number' },
                                    { label: 'Hours Spent on Community Engagement (Total)', key: 'communityEngagementHours', type: 'number' },
                                    { label: 'Sustainable Community Development', key: 'sustainableCommunityDevelopment', type: 'dropdown', options: ['Yes', 'No'] },
                                    { label: 'Human Rights Complaints', key: 'humanRightsComplaints', type: 'dropdown', options: ['Yes', 'No'] },
                                    { label: 'WELL-certified Projects (%)', key: 'wellCertifiedProjects', type: 'number' },
                                    { label: 'Diversity, Equity, and Inclusion Programs', key: 'deiPrograms', type: 'dropdown', options: ['Yes', 'No'] },
                                ].map(({ label, key, type, options }, idx) => (
                                    <div key={idx}>
                                        <label>{label}</label>
                                        {type === 'dropdown' ? (
                                            <select
                                                value={socialData[key]}
                                                onChange={(e) =>
                                                    setSocialData({
                                                        ...socialData,
                                                        [key]: e.target.value,
                                                    })
                                                }
                                                className="inputField"
                                            >
                                                {options.map((option, i) => (
                                                    <option key={i} value={option}>
                                                        {option}
                                                    </option>
                                                ))}
                                            </select>
                                        ) : (
                                            <input
                                                type={type}
                                                value={socialData[key]}
                                                onChange={(e) =>
                                                    setSocialData({
                                                        ...socialData,
                                                        [key]: e.target.value,
                                                    })
                                                }
                                                className="inputField"
                                            />
                                        )}
                                    </div>
                                ))}
                            </div>
                        </div>

                        {/* Governance Section */}
                        <div className="section">
                            <h3>Governance</h3>
                            <div className="inputContainer">
                                {[
                                    { label: 'Independent Directors (%)', key: 'independentDirectors', type: 'number' },
                                    { label: 'Board-level Governance Initiatives', key: 'boardGovernanceInitiatives', type: 'dropdown', options: ['Yes', 'No'] },
                                    { label: 'Regulatory Compliance Status', key: 'regulatoryComplianceStatus', type: 'dropdown', options: ['Yes', 'No'] },
                                    { label: 'Non-compliance Cases (Count)', key: 'nonComplianceCases', type: 'number' },
                                    { label: 'Employees Trained on Code of Conduct (%)', key: 'employeesTrainedOnCodeOfConduct', type: 'number' },
                                    { label: 'Ethics Violations Reported (Count)', key: 'ethicsViolationsReported', type: 'number' },
                                    { label: 'Risk Management Framework Alignment', key: 'riskManagementFramework', type: 'dropdown', options: ['ISO 31000', 'Others'] },
                                    { label: 'Regular Risk Assessments Conducted', key: 'riskAssessmentsConducted', type: 'dropdown', options: ['Monthly', 'Quarterly', 'Annually'] },
                                ].map(({ label, key, type, options }, idx) => (
                                    <div key={idx}>
                                        <label>{label}</label>
                                        {type === 'dropdown' ? (
                                            <select
                                                value={governanceData[key]}
                                                onChange={(e) =>
                                                    setGovernanceData({
                                                        ...governanceData,
                                                        [key]: e.target.value,
                                                    })
                                                }
                                                className="inputField"
                                            >
                                                {options.map((option, i) => (
                                                    <option key={i} value={option}>
                                                        {option}
                                                    </option>
                                                ))}
                                            </select>
                                        ) : (
                                            <input
                                                type={type}
                                                value={governanceData[key]}
                                                onChange={(e) =>
                                                    setGovernanceData({
                                                        ...governanceData,
                                                        [key]: e.target.value,
                                                    })
                                                }
                                                className="inputField"
                                            />
                                        )}
                                    </div>
                                ))}
                            </div>
                        </div>


{/* Data Import Section */}
<div className="section">
  <h3>Data Import</h3>

  <div className="date-picker-container">
    <div className="inputColumn">
      <label>Start Date</label>
      <input
        type="date"
        value={dataImportData.startDate}
        onChange={(e) =>
          setDataImportData({
            ...dataImportData,
            startDate: e.target.value,
          })
        }
        className="inputField"
      />
    </div>
    <div className="inputColumn">
      <label>End Date</label>
      <input
        type="date"
        value={dataImportData.endDate}
        onChange={(e) =>
          setDataImportData({
            ...dataImportData,
            endDate: e.target.value,
          })
        }
        className="inputField"
      />
    </div>
  </div>
</div>



           {/* File Upload Section */}
<div className="section">
    <h3>Upload CSV</h3>
    <div className="file-upload-container">
        <input
            type="file"
            accept=".csv"
            onChange={handleCsvUpload}
            className="inputField"
            id="csv-upload"
        />
        <label htmlFor="csv-upload" className="upload-label">
            <p>Click to upload or drag and drop</p>
            <p>Upload a CSV file (max. 5MB) containing Metric Name, Value, and Timestamp columns</p>
        </label>
    </div>
    {csvFile && (
        <div className="preview">
            <p>{csvFile.name}</p>
            <p>{Math.round(csvFile.size / 1024)} KB</p>
        </div>
    )}
</div>

{/* Logo Upload Section */}
<div className="section">
    <h3>Upload Logo</h3>
    <div className="file-upload-container">
        <input
            type="file"
            accept="image/*"
            onChange={handleLogoUpload}
            className="inputField"
            id="logo-upload"
        />
        <label htmlFor="logo-upload" className="upload-label">
            <p>Click to upload or drag and drop</p>
            <p>Upload your company logo (JPEG/PNG/SVG, max. 2MB)</p>
        </label>
    </div>
    {logoFile && (
        <div className="preview">
            <img src={logoFile} alt="Logo" width="100" />
        </div>
    )}
</div>

{/* Submit Button */}
<button className="button" onClick={handleSubmit}>
    Submit Data
</button>
<div>
        {loading && <p>Submitting data, please wait...</p>}
        {successMessage && <p className="success">{successMessage}</p>}
        {errorMessage && <p className="error">{errorMessage}</p>}
    </div>
</div>
    </div>
</div>

</div>
    );
};

export default Sidebar;
