import React, { useState } from "react";
import styled from "styled-components";
import { Home, LayoutDashboard, FileText, BarChart2, Calculator, Settings, HelpCircle, CloudUpload } from 'lucide-react';

const GreenTrendz = "path_to_logo_image";  // Replace this with the path to your logo

const LandingPage = styled.div`
  display: flex;
  height: 100vh;
  background-color: #f4f4f9;
  font-family: 'Arial', sans-serif;
`;

const Sidebar = styled.nav`
  width: 250px;
  background: linear-gradient(to bottom right, #064e3b, #276749);
  color: white;
  padding: 20px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  position: fixed;
  height: 100vh;
  z-index: 10;
  box-shadow: 2px 0 15px rgba(0, 0, 0, 0.15);
  transition: width 0.3s ease;
`;

const Logo = styled.h5`
  font-size: 25px;
  margin-bottom: 30px;
  font-weight: bold;
  color: #fff;
  text-align: center;
  margin-top: 20px;
  transition: transform 0.3s ease;
  cursor: pointer;

  &:hover {
    transform: scale(1.1);
  }
`;

const NavList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  width: 100%;
`;

const NavItem = styled.li`
  color: white;
  text-decoration: none;
  padding: 15px 20px;
  display: flex;
  align-items: center;
  width: 100%;
  border-radius: 8px;
  margin-bottom: 12px;
  cursor: pointer;
  transition: background-color 0.3s ease, transform 0.3s ease;

  &:hover {
    background-color: #3b6b5c;
    transform: scale(1.05);
  }
`;

const NavIcon = styled.div`
  margin-right: 20px;
`;

const Content = styled.div`
  flex: 1;
  padding: 20px;
  background: #f4f4f4;
  overflow-y: auto;
`;

const Section = styled.div`
  background: #ffffff;
  border: 1px solid #ddd;
  margin-bottom: 20px;
  border-radius: 8px;
  padding: 20px;
`;

const SectionHeader = styled.div`
  font-weight: bold;
  margin-bottom: 10px;
  cursor: pointer;
`;

const SectionContent = styled.div`
  margin-top: 10px;
`;

const InputField = styled.div`
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 5px;
`;

const Input = styled.input`
  padding: 10px;
  width: 100%;
  border: 1px solid #ddd;
  border-radius: 4px;
`;

const Button = styled.button`
  background: #004d40;
  color: #ffffff;
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background: #00796b;
  }
`;

const Footer = styled.footer`
  background: #004d40;
  color: #ffffff;
  text-align: center;
  padding: 10px;
  position: fixed;
  bottom: 0;
  width: 100%;
`;

const DataUploadPage = () => {
  const [showSection, setShowSection] = useState({
    environmental: false,
    social: false,
    governance: false,
  });

  const toggleSection = (section) => {
    setShowSection((prev) => ({ ...prev, [section]: !prev[section] }));
  };

  const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
  ];

  return (
    <LandingPage>
      <Sidebar>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img src={GreenTrendz} alt="Logo" width="50" height="50" />
          <Logo>GreenTrendz</Logo>
        </div>
        <NavList>
          {menuItems.map((item) => (
            <NavItem key={item.path}>
              <NavIcon>
                <item.icon size={20} />
              </NavIcon>
              <span>{item.label}</span>
            </NavItem>
          ))}
        </NavList>
        <Button>Logout</Button>
      </Sidebar>

      <Content>
        <h2>Data Upload Page</h2>
        <Section>
          <SectionHeader onClick={() => toggleSection("environmental")}>
            Environmental Metrics
          </SectionHeader>
          {showSection.environmental && (
            <SectionContent>
              <InputField>
                <Label>Energy Consumption (kWh)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Renewable Energy Percentage (%)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Greenhouse Gas Emissions (CO2e)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Water Usage (liters)</Label>
                <Input type="text" />
              </InputField>
            </SectionContent>
          )}
        </Section>

        {/* Add additional sections here (social, governance) similar to the environmental section */}

        <Button>Submit</Button>
      </Content>

      <Footer>
        © 2024 Your Company Name. All rights reserved. | Privacy Policy | Terms of Service | Contact Us
      </Footer>
    </LandingPage>
  );
};

export default DataUploadPage;
