import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  UserCircle,
  HelpCircle,
  CloudUpload,
  LogOut
} from 'lucide-react';

import { useNavigate } from 'react-router-dom';
import GreenTrendz from '../assets/img/logo_1.png';
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../components/Card'; // Ensure correct path
import { Button } from '@/components/ui/button'; // Ensure Button component exists

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'CarbonCalculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
  { icon: UserCircle, label: 'Account', path: '/account' }
];

const Dashboard = () => {
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    setShowLogoutModal(true);
  };

  const confirmLogout = () => {
    navigate('/'); // Redirect to home
  };

  const [activeSection, setActiveSection] = useState('environmental');
  const [data, setData] = useState({
    environmentalData: {},
    socialData: {},
    governanceData: {}
  });

  // Fetch ESG data from API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/esg-data');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const jsonData = await response.json();
        console.log("Fetched Data:", jsonData); // Debugging API response
        setData(jsonData || { environmentalData: {}, socialData: {}, governanceData: {} });
      } catch (error) {
        console.error('Error fetching ESG data:', error);
        setData({ environmentalData: {}, socialData: {}, governanceData: {} });
      }
    };
    fetchData();
  }, []);

  // Function to render metrics safely
  const renderMetrics = (sectionData) => {
    if (!sectionData || typeof sectionData !== 'object') {
      return <p>No data available</p>; // Prevents crashes if sectionData is undefined/null
    }

    return Object.entries(sectionData).map(([key, value]) => (
      <div key={key} className="flex justify-between items-center border-b pb-2">
        <span className="font-medium text-gray-600">
          {key.replace(/([A-Z])/g, ' $1').trim()}
        </span>
        <span className="text-lg font-bold">{value}</span>
      </div>
    ));
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <nav className="w-64 bg-green-900 text-white p-6 fixed h-screen">
        <div className="flex items-center">
          <img src={GreenTrendz} alt="Logo" width="50" height="50" />
          <h5 className="text-2xl font-bold ml-3">GreenTrendz</h5>
        </div>

        <ul className="mt-6">
          {menuItems.map((item) => (
            <li key={item.path} className="mt-3">
              <a href={item.path} className="flex items-center p-3 hover:bg-green-700 rounded">
                <item.icon className="mr-3" size={20} />
                {item.label}
              </a>
            </li>
          ))}
        </ul>

        <div className="mt-auto flex justify-center">
          <button
            className="bg-white text-green-900 px-6 py-2 rounded font-bold flex items-center gap-2"
            onClick={handleLogout}
          >
            <LogOut size={18} />
            Logout
          </button>
        </div>
      </nav>

      {/* Main Content */}
      <main className="ml-64 p-6 w-full">
        <h1 className="text-2xl font-bold">Dashboard</h1>
        <p>Welcome to the Dashboard page!</p>

        {/* ESG Data Sections */}
        <div className="p-6 space-y-6">
          <div className="flex space-x-4">
            <Button 
              onClick={() => setActiveSection('environmental')}
              variant={activeSection === 'environmental' ? 'default' : 'outline'}
              className="w-1/3"
            >
              Environmental
            </Button>
            <Button 
              onClick={() => setActiveSection('social')}
              variant={activeSection === 'social' ? 'default' : 'outline'}
              className="w-1/3"
            >
              Social
            </Button>
            <Button 
              onClick={() => setActiveSection('governance')}
              variant={activeSection === 'governance' ? 'default' : 'outline'}
              className="w-1/3"
            >
              Governance
            </Button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <Card>
              <CardHeader>
                <CardTitle>Key Metrics</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {activeSection === 'environmental' && renderMetrics(data.environmentalData)}
                  {activeSection === 'social' && renderMetrics(data.socialData)}
                  {activeSection === 'governance' && renderMetrics(data.governanceData)}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Overview</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-4">
                  {activeSection === 'environmental' && (
                    <>
                      <div>Energy Systems: {data.environmentalData?.energyEfficientSystems}</div>
                      <div>Rainwater Harvesting: {data.environmentalData?.rainwaterHarvesting}</div>
                      <div>Zero Waste: {data.environmentalData?.zeroWasteToLandfill}</div>
                      <div>Biodiversity: {data.environmentalData?.biodiversityMeasures}</div>
                    </>
                  )}
                  {activeSection === 'social' && (
                    <>
                      <div>DEI Programs: {data.socialData?.deiPrograms}</div>
                      <div>Human Rights: {data.socialData?.humanRightsComplaints}</div>
                      <div>Safety: {data.socialData?.workforceSafetyIncidents}</div>
                      <div>Community: {data.socialData?.sustainableCommunityDevelopment}</div>
                    </>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
