    "Tiruchirappalli", "Tirupati", "Udaipur", "Vadodara", "Varanasi", "Vijayawada", "Visakhapatnam", 
    "Warangal", "Zirakpur", "Agraharam", "Ahmednagar", "Ajmer", "Alappuzha", "Aligarh", "Ambala", 
    "Amravati", "Anand", "Anantapur", "Angul", "Asansol", "Aurangabad", "Ayodhya", "Bagalkot", 
    "Bangalore", "Banaras", "Baran", "Bardhaman", "Bareilly", "Baripada", "Barwani", "Basti", 
    "Begusarai", "Bellary", "Berhampur", "Bharuch", "Bhavnagar", "Bhilai", "Bhopal", "Bhubaneswar", 
    "Bikaner", "Bilaspur", "Bokaro", "Chandrapur", "Chennai", "Chhindwara", "Chidambaram", "Chikmagalur", 
    "Chinchwad", "Coimbatore", "Cuttack", "Darbhanga", "Davanagere", "Dehradun", "Dibrugarh", "Dindigul", 
    "Durgapur", "Erode", "Faridabad", "Gadag", "Gandhinagar", "Gangtok", "Gaya", "Ghaziabad", "Gwalior", 
    "Hubli", "Hyderabad", "Imphal", "Indore", "Jabalpur", "Jammu", "Jamshedpur", "Jodhpur", "Kochi", 
    "Kolkata", "Kota", "Kottayam", "Lucknow", "Ludhiana", "Madurai", "Mangalore", "Mysore", "Nagapattinam", 
