    "Jaipur", "Kanpur", "Kolkata", "Kochi", "Lucknow", "Nagpur", "Noida", "Patna", "Pune", "Rajkot", 
    "Ranchi", "Surat", "Thane", "Vijayawada", "Visakhapatnam", "Zirakpur", "Ambala", "Amritsar", "Anand", 
    "Bikaner", "Chennai", "Coimbatore", "Dehradun", "Gandhinagar", "Ghaziabad", "Hyderabad", "Indore", 
    "Jabalpur", "Kochi", "Lucknow", "Madurai", "Nagpur", "Navi Mumbai", "Pune", "Raipur", "Ranchi", 
    "Siliguri", "Surat",  "Tiruchirappalli", "Vadodara", "Varanasi", "Vijayawada", "Visakhapatnam",
    "Agraharam", "Ahmednagar", "Ajmer", "Alappuzha", "Ambala", "Amravati", "Anand", "Angul", "Asansol", 
    "Aurangabad", "Ayodhya", "Bagalkot", "Bangalore", "Banaras", "Baran", "Bardhaman", "Bareilly", "Baripada"
    // Continue adding more cities following the pattern.
  ];
  
  
  const navigate = useNavigate();

  const handleCityInputChange = (e) => {
    const query = e.target.value.toLowerCase();
    const filteredCities = cities.filter(city => city.toLowerCase().startsWith(query)); // Start with query instead of includes
    setCitySuggestions(filteredCities);
    setLocation(e.target.value);  // Update the location state
  };
  

  const handleSignup = async (e) => {
    e.preventDefault();
    console.log('OTP Verified:', otpVerified);
    console.log('Accepted Terms:', acceptedTerms);
    

    // Password and Terms Validation
    if (password !== confirmPassword) {
      alert('Passwords do not match!');
      return;
    }
    if (!acceptedTerms) {
      alert('You must accept the terms and conditions!');
      return;
    }
    
    if (!otpVerified) {
      alert('Please verify your email and phone with OTP!');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/signup', {
        email,
        password,
        phoneNumber,
        companyName,
        industrySector,
        location,
        userType,
      });

      alert(response.data.message); // Show success message
      navigate('/erp'); // Redirect to ERP page after successful signup
    } catch (error) {
      alert('Signup failed: ' + (error.response?.data?.error || 'Unknown error'));
    }
  };

  const handleShowModal = () => setShowModal(true); // Show modal
  const handleCloseModal = () => setShowModal(false); // Close modal

  const handleTermsConfirmation = (accepted) => {
    if (accepted) {
      setCanAcceptTerms(true);
    } else {
      setCanAcceptTerms(false);
    }
    setShowModal(false); // Close modal on confirmation
  };

  const [captchaVerified, setCaptchaVerified] = useState(false);

const handleCaptchaChange = (value) => {
  setCaptchaVerified(true);  // Sets true when captcha is verified
};


  const handleOtpVerification = () => {
    // Example of OTP verification logic:
    const mockOtp = '1234'; // Assuming the correct OTP is '1234'
    const enteredOtp = otpInputValue; // assuming otpInputValue is bound to the OTP input field
    
    if (enteredOtp === mockOtp) {
      setOtpVerified(true);
      alert('OTP verification successful!');
    } else {
      alert('Invalid OTP. Please try again.');
    }
  };
  

  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundColor: '#f4f4f4',
      padding: '20px',
    },
    box: {
      maxWidth: '600px',
      width: '100%',
      backgroundColor: '#ffffff',
      borderRadius: '10px',
      boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
      padding: '30px',
      textAlign: 'center',
    },
    heading: {
      fontSize: '24px',
      marginBottom: '10px',
      color: '#333333',
      fontWeight: 'bold',
    },
    introText: {
      fontSize: '16px',
      color: '#555555',
      marginBottom: '20px',
      lineHeight: '1.5',
    },
    alreadyCustomer: {
      fontSize: '14px',
      color: '#666666',
      marginBottom: '10px',
    },
    link: {
      color: '#28a745',
      textDecoration: 'none',
      fontWeight: 'bold',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '14px',
      color: '#555555',
      transition: 'border-color 0.3s ease', // Add transition for input fields
    },
    otpInput: {
      width: '70%',
      padding: '8px',
      marginBottom: '15px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '14px',
      color: '#555555',
      textAlign: 'center',
    },
    otpButton: {
      display: 'flex',
      width: '10px',
      height: '30px',
      gap: '5px',
      flexDirection: 'row',
      justifyContent: 'space-between',
    },
    button: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#28a745',
      color: '#ffffff',
      border: 'none',
      borderRadius: '5px',
      fontSize: '16px',
      cursor: 'pointer',
      fontWeight: 'bold',
      transition: 'background-color 0.3s ease, transform 0.2s ease', // Added transition
    },
    buttonHover: {
      backgroundColor: '#218838',
      transform: 'scale(1.05)', // Button will scale up on hover
    },
    greyButton: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#ccc',
      color: '#ffffff',
      border: 'none',
      borderRadius: '5px',
      fontSize: '16px',
      cursor: 'not-allowed',
      fontWeight: 'bold',
    },
    select: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '14px',
      color: '#555555',
    },
    checkboxContainer: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '20px',
    },
    checkbox: {
      marginRight: '10px',
    },
    cancelbutton: {
      backgroundColor: 'grey',
      color: 'black',
      border: '1px',
    },
  };
  
  

  return (
    <div>
      {/* Navbar */}
      <header>
        <Navbar bg="light" expand="lg" className="shadow-sm py-3 custom-navbar">
          <Container>
            <Navbar.Brand href="/" className="d-flex align-items-center">
              <img
                src={GreenTrendz}
                alt="Logo"
                width="50"
                height="50"
                className="d-inline-block align-top me-2"
              />
              <span className="brand-text">GreenTrendz</span>
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="ms-auto d-flex align-items-center">
                <Button variant="success" href="/HomePage" className="mx-2 btn-sign" style={styles.link}>
                  Back
                </Button>
                <Nav.Link href="#services" className="nav-link-enhanced">Services</Nav.Link>
                <Nav.Link href="#about" className="nav-link-enhanced">About Us</Nav.Link>
                <Nav.Link href="#contact" className="nav-link-enhanced">Contact</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
      </header>

      {/* Signup Form */}

      <div style={styles.container}>
        <div style={styles.box}>
          <h2 style={styles.heading}>Sign Up</h2>
          <p style={styles.introText}>
            Join us today! Fill in the details below to create your account and start generating ESG reports effortlessly.
          </p>
          <p style={styles.alreadyCustomer}>
            Already a customer? <a href="/login" style={styles.link}>Log in here</a>.
          </p>
          <form style={styles.form} onSubmit={handleSignup}>
              {/* Company Name Input with limit */}
              <input
                type="text"
                placeholder="Company Name"
                value={companyName}
                onChange={(e) => setCompanyName(e.target.value)}
                style={styles.input}
                required
                maxLength={50} // Limit to 50 characters
              />
              
              {/* Email Input with limit */}
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                style={styles.input}
                required
                maxLength={50} // Limit to 20 characters
              />

              {/* OTP Input with limit */}
              <div style={{ display: 'flex', justifyContent: 'flex-start', gap: '10px' }}>
                <input
                  type="text"
                  placeholder="OTP"
                  style={styles.otpInput}
                  maxLength={4} // Limit to 4 characters
                  onChange={(e) => handleOtpChange(e)}
                />
                <div style={styles.otpButton}>
                  <Button style={styles.smallButton} onClick={handleOtpVerification}>Verify</Button>
                  <Button style={styles.smallButton} onClick={handleOtpVerification}>Resend</Button>
                </div>
              </div>

              {/* Phone Number Input (only numbers allowed) */}
              <input
                type="tel"
                placeholder="Phone Number"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value.replace(/\D/g, '').slice(0, 10))} // Restrict non-numeric input and limit to 10 digits
                style={styles.input}
                required
                maxLength={10} // Limit to 10 characters
              />

              {/* Location (City Name with suggestions) */}
              <input
                type="text"
                placeholder="Location (City)"
                value={location}
                onChange={handleCityInputChange}
                style={styles.input}
                list="city-suggestions"
              />
              <datalist id="city-suggestions">
                {citySuggestions.map((city, index) => (
                  <option key={index} value={city} />
                ))}
              </datalist>

              {/* Password and Confirm Password */}
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={styles.input}
                required
              />
              <input
                type="password"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                style={styles.input}
                required
              />

              {/* Industry Sector */}
              <select
                value={industrySector}
                onChange={(e) => setIndustrySector(e.target.value)}
                style={styles.select}
                required
              >
                <option value="" disabled>Select Industry Sector</option>
                <option value="Manufacturing">Manufacturing</option>
                <option value="Finance">Finance</option>
                <option value="Technology">Technology</option>
              </select>

              {/* User Type */}
              <select
                value={userType}
                onChange={(e) => setUserType(e.target.value)}
                style={styles.select}
                required
              >
                <option value="employee">Employee</option>
                <option value="admin">Admin</option>
              </select>

              {/* Terms Acceptance */}
              <div style={styles.checkboxContainer}>
                <input
                  type="checkbox"
                  id="terms"
                  style={styles.checkbox}
                  disabled={!canAcceptTerms}
                  onChange={() => setAcceptedTerms(!acceptedTerms)}
                />
                <label htmlFor="terms">
                  I accept the <a onClick={handleShowModal} style={{ cursor: 'pointer', color: '#28a745' }}>Terms and Conditions</a>
                </label>
              </div>

              {/* Create Account Button */}
              <button
                type="submit"
                style={isCaptchaVerified && otpVerified && acceptedTerms ? { ...styles.button, ...styles.buttonHover } : styles.greyButton}
                disabled={!isCaptchaVerified || !otpVerified || !acceptedTerms}
              >
                Create Account
              </button>
            </form>

        </div>
      </div>

      {/* Modal for Terms and Conditions */}
      <Modal show={showModal} onHide={handleCloseModal} size="lg" centered>
        <Modal.Header closeButton>
          <Modal.Title>Terms and Conditions</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <h5>Terms and Conditions Content</h5>
            <p>Put your Terms and Conditions here...</p>
          </div>
        </Modal.Body>
        <Modal.Footer>
        <Button 
            variant="secondary" 
            onClick={() => handleTermsConfirmation(false)} 
            className="cancel-button"  /* Apply the class */
          >
            Cancel
          </Button>
          <Button 
            variant="primary" 
            onClick={() => handleTermsConfirmation(true)} 
          >
            Confirm
          </Button>
        </Modal.Footer>
      </Modal>

      <footer id="footer" className="footer dark-background text-light">
                <div className="footer-intro text-center py-4">
                    <h3>Sustainability at the Core: Discover How Our Services Drive Progress.</h3>
                    <p className="sub-title">
                        <span className="green-text">Your ESG Partner for a Brighter Tomorrow</span>
                    </p>
                </div>

                <div className="footer-top py-5">
                    <div className="container">
                        <div className="row gy-4 justify-content-center">
                            <div className="col-lg-2 col-md-3 footer-links">
                                <h4>Useful Links</h4>
                                <ul>
                                    <li><a href="#">Home</a></li>
                                    <li><a href="#">About Us</a></li>
                                    <li><a href="#">Services</a></li>
                                    <li><a href="#">Terms of Service</a></li>
                                    <li><a href="#">Privacy Policy</a></li>
                                </ul>
                            </div>

                            <div className="col-lg-2 col-md-3 footer-links">
                                <h4>Resources</h4>
                                <ul>
                                    <li><a href="#">Media</a></li>
                                    <li><a href="#">Newsletter</a></li>
                                    <li><a href="#">Blog</a></li>
                                    <li><a href="#">Case Study</a></li>
                                </ul>
                            </div>

                            <div className="col-lg-2 col-md-3 footer-links">
                                <h4>Frameworks</h4>
                                <ul>
                                    <li><a href="#">ESG Reporting</a></li>
                                    <li><a href="#">BRSR- SEBI Framework</a></li>
                                    <li><a href="#">GRI Reporting Framework</a></li>
                                    <li><a href="#">PCAF-Carbon Accounting</a></li>
                                </ul>
                            </div>

                            <div className="col-lg-3 col-md-4 footer-column contact-box">
                                <h3>Contact Us</h3>
                                <form>
                                    <input type="text" placeholder="Your Name" className="footer-input" />
                                    <input type="email" placeholder="Your Email" className="footer-input" />
                                    <textarea placeholder="Your Message" className="footer-textarea"></textarea>
                                    <button type="submit" className="footer-button">Send Message</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="container text-center mb-4">
                    <div className="col-lg-3 col-md-6 footer-about mx-auto">
                        <a href="index.html" className="logo d-flex align-items-center mb-3 justify-content-center">
                            <span className="sitename">GreenTrendz</span>
                        </a>
                        <p>At GreenTrendz, we strive to make sustainability the foundation of modern business practices, providing services to help businesses build a greener future.</p>
                    </div>
                </div>

                <div className="footer-bottom py-3">
                    <div className="container d-flex justify-content-between align-items-center">
                        <div className="footer-contact">
                            <p>A108 Green Street</p>
                            <p>New York, NY 535022</p>
                            <p><strong>Phone:</strong> <span>+1 5589 55488 55</span></p>
                            <p><strong>Email:</strong> <span>info@greentrendz.com</span></p>
                        </div>

                        <div className="text-center">
                            <a href="index.html" className="logo d-flex align-items-center mb-3 justify-content-center">
                                <img src={logo} alt="GreenTrendz Logo" className="logo-img me-2" height="150" width="150" />
                            </a>
                            <div>© Copyright <strong><span>GreenTrendz</span></strong>. All Rights Reserved</div>
                        </div>

                        <div className="social-links d-flex align-items-center">
                            <a href="#" className="twitter"><i className="bi bi-twitter"></i></a>
                            <a href="#" className="facebook"><i className="bi bi-facebook"></i></a>
                            <a href="#" className="instagram"><i className="bi bi-instagram"></i></a>
                            <a href="#" className="linkedin"><i className="bi bi-linkedin"></i></a>
                            <a href="#" className="youtube"><i className="bi bi-youtube"></i></a>
                        </div>
                    </div>
                </div>
            </footer>
    </div>
    
  );
};

export default Signup;
