import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Navbar, Nav, Container, Button, Modal } from 'react-bootstrap'; // Ensure Modal is imported
import GreenTrendz from '../assets/img/logo_1.png';
import logo from '../assets/img/logo.png';

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [companyName, setCompanyName] = useState('');
  const [industrySector, setIndustrySector] = useState('');
  const [location, setLocation] = useState('');
  const [userType, setUserType] = useState('employee'); // Default user type
  const [acceptedTerms, setAcceptedTerms] = useState(false);
  const [showModal, setShowModal] = useState(false); // State for modal visibility
  const [canAcceptTerms, setCanAcceptTerms] = useState(false); // Track if terms can be accepted
  const [isCaptchaVerified, setIsCaptchaVerified] = useState(false); // Captcha verification
  const [otpVerified, setOtpVerified] = useState(false); // OTP verification state for email and phone
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    // Password and Terms Validation
    if (password !== confirmPassword) {
      alert('Passwords do not match!');
      return;
    }
    if (!acceptedTerms) {
      alert('You must accept the terms and conditions!');
      return;
    }
    
    if (!otpVerified) {
      alert('Please verify your email and phone with OTP!');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/signup', {
        email,
        password,
        phoneNumber,
        companyName,
        industrySector,
        location,
        userType,
      });

      alert(response.data.message); // Show success message
      navigate('/erp'); // Redirect to ERP page after successful signup
    } catch (error) {
      alert('Signup failed: ' + (error.response?.data?.error || 'Unknown error'));
    }
  };

  const handleShowModal = () => setShowModal(true); // Show modal
  const handleCloseModal = () => setShowModal(false); // Close modal

  const handleTermsConfirmation = (accepted) => {
    if (accepted) {
      setCanAcceptTerms(true);
    } else {
      setCanAcceptTerms(false);
    }
    setShowModal(false); // Close modal on confirmation
  };

  

  const handleOtpVerification = () => {
    // Assume OTP verification logic is implemented here
    // Mocking OTP verification for now
    setOtpVerified(true);
    alert("OTP verification successful!");
  };

  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundColor: '#f4f4f4',
      padding: '20px',
    },
    box: {
      maxWidth: '600px',
      width: '100%',
      backgroundColor: '#ffffff',
      borderRadius: '10px',
      boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
      padding: '30px',
      textAlign: 'center',
    },
    heading: {
      fontSize: '24px',
      marginBottom: '10px',
      color: '#333333',
      fontWeight: 'bold',
    },
    introText: {
      fontSize: '16px',
      color: '#555555',
      marginBottom: '20px',
      lineHeight: '1.5',
    },
    alreadyCustomer: {
      fontSize: '14px',
      color: '#666666',
      marginBottom: '10px',
    },
    link: {
      color: '#28a745',
      textDecoration: 'none',
      fontWeight: 'bold',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '14px',
      color: '#555555',
    },
    otpinput: {     
       width: '100%',      
       padding: '10px',      
       marginBottom: '15px',      
       borderRadius: '5px',      
       border: '1px solid #ccc',
       fontSize:'14px',
       color:'#cccccc',
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '14px',
      color: '#555555',
    },
    checkboxContainer: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '20px',
    },
    checkbox: {
      marginRight: '10px',
    },
    button: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#28a745',
      color: '#ffffff',
      border: 'none',
      borderRadius: '5px',
      fontSize: '16px',
      cursor: 'pointer',
      fontWeight: 'bold',
    },
    greyButton: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#ccc',
      color: '#fffff',
      border: 'none',
      borderRadius: '5px',
      fontSize: '16px',
      cursor: 'not-allowed',
      fontWeight: 'bold',
    },
  };

  return (
    <div>
      {/* Navbar */}
      <header>
        <Navbar bg="light" expand="lg" className="shadow-sm py-3 custom-navbar">
          <Container>
            <Navbar.Brand href="/" className="d-flex align-items-center">
              <img
                src={GreenTrendz}
                alt="Logo"
                width="50"
                height="50"
                className="d-inline-block align-top me-2"
              />
              <span className="brand-text">GreenTrendz</span>
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="ms-auto d-flex align-items-center">
                <Button variant="success" href="/HomePage" className="mx-2 btn-sign" style={styles.link}>
                  Back
                </Button>
                <Nav.Link href="#services" className="nav-link-enhanced">Services</Nav.Link>
                <Nav.Link href="#about" className="nav-link-enhanced">About Us</Nav.Link>
                <Nav.Link href="#contact" className="nav-link-enhanced">Contact</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
      </header>

      {/* Signup Form */}

      <div style={styles.container}>
        <div style={styles.box}>
          <h2 style={styles.heading}>Sign Up</h2>
          <p style={styles.introText}>
            Join us today! Fill in the details below to create your account and start generating ESG reports effortlessly.
          </p>
          <p style={styles.alreadyCustomer}>
            Already a customer? <a href="/login" style={styles.link}>Log in here</a>.
          </p>
          <form style={styles.form} onSubmit={handleSignup}>
            <input
              type="text"
              placeholder="Company Name"
              value={companyName}
              onChange={(e) => setCompanyName(e.target.value)}
              style={styles.input}
              required
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={styles.input}
              required
            />
            <Button onClick={handleOtpVerification}>Verify Email OTP</Button>
            <input
              type="tel"
              placeholder="Phone Number"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              style={styles.input}
              required
            />
            <Button onClick={handleOtpVerification}>Verify Phone OTP</Button>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={styles.input}
              required
            />
            <input
              type="password"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              style={styles.input}
              required
            />
            <select
              value={industrySector}
              onChange={(e) => setIndustrySector(e.target.value)}
              style={styles.select}
              required
            >
              <option value="" disabled>Select Industry Sector</option>
              <option value="Manufacturing">Manufacturing</option>
              <option value="Finance">Finance</option>
              <option value="Technology">Technology</option>
            </select>
            <input
              type="text"
              placeholder="Location"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              style={styles.input}
              required
            />
            <select
              value={userType}
              onChange={(e) => setUserType(e.target.value)}
              style={styles.select}
              required
            >
              <option value="employee">Employee</option>
              <option value="admin">Admin</option>
            </select>
            <div style={styles.checkboxContainer}>
              <input
                type="checkbox"
                id="terms"
                style={styles.checkbox}
                disabled={!canAcceptTerms}
                onChange={() => setAcceptedTerms(!acceptedTerms)}
              />
              <label htmlFor="terms">
                I accept the <a onClick={handleShowModal} style={{ cursor: 'pointer', color: '#28a745' }}>Terms and Conditions</a>
              </label>
            </div>
            
            <button type="submit" style={isCaptchaVerified && otpVerified && acceptedTerms ? styles.button : styles.greyButton} disabled={!isCaptchaVerified || !otpVerified || !acceptedTerms}>
              Create Account
            </button>
          </form>
        </div>
      </div>

      {/* Modal for Terms and Conditions */}
      <Modal show={showModal} onHide={handleCloseModal} size="lg" centered>
        <Modal.Header closeButton>
          <Modal.Title>Terms and Conditions</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <h5>Terms and Conditions Content</h5>
            <p>Put your Terms and Conditions here...</p>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => handleTermsConfirmation(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={() => handleTermsConfirmation(true)}>
            Confirm
          </Button>
        </Modal.Footer>
      </Modal>

      <footer id="footer" className="footer dark-background text-light">
                <div className="footer-intro text-center py-4">
                    <h3>Sustainability at the Core: Discover How Our Services Drive Progress.</h3>
                    <p className="sub-title">
                        <span className="green-text">Your ESG Partner for a Brighter Tomorrow</span>
                    </p>
                </div>

                <div className="footer-top py-5">
                    <div className="container">
                        <div className="row gy-4 justify-content-center">
                            <div className="col-lg-2 col-md-3 footer-links">
                                <h4>Useful Links</h4>
                                <ul>
                                    <li><a href="#">Home</a></li>
                                    <li><a href="#">About Us</a></li>
                                    <li><a href="#">Services</a></li>
                                    <li><a href="#">Terms of Service</a></li>
                                    <li><a href="#">Privacy Policy</a></li>
                                </ul>
                            </div>

                            <div className="col-lg-2 col-md-3 footer-links">
                                <h4>Resources</h4>
                                <ul>
                                    <li><a href="#">Media</a></li>
                                    <li><a href="#">Newsletter</a></li>
                                    <li><a href="#">Blog</a></li>
                                    <li><a href="#">Case Study</a></li>
                                </ul>
                            </div>

                            <div className="col-lg-2 col-md-3 footer-links">
                                <h4>Frameworks</h4>
                                <ul>
                                    <li><a href="#">ESG Reporting</a></li>
                                    <li><a href="#">BRSR- SEBI Framework</a></li>
                                    <li><a href="#">GRI Reporting Framework</a></li>
                                    <li><a href="#">PCAF-Carbon Accounting</a></li>
                                </ul>
                            </div>

                            <div className="col-lg-3 col-md-4 footer-column contact-box">
