<div className="section" style={styles.section}>
    <h3 style={styles.sectionTitle}>Upload CSV</h3>
    <div
        style={styles.fileUploadContainer}
        onDragOver={(e) => {
            e.preventDefault();
            e.currentTarget.style.backgroundColor = '#e0f7fa';
        }}
        onDragLeave={(e) => {
            e.preventDefault();
            e.currentTarget.style.backgroundColor = '#fff';
        }}
        onDrop={(e) => {
            e.preventDefault();
            e.currentTarget.style.backgroundColor = '#fff';
            const file = e.dataTransfer.files[0];
            if (file) handleCsvUpload({ target: { files: [file] } });
        }}
    >
        {/* File Input */}
        <input
            type="file"
            accept=".csv"
            onChange={handleCsvUpload}
            style={{ display: 'none' }} // Hide the input
            id="csv-upload" // Unique ID
        />
        {/* Label linked to the input */}
        <label htmlFor="csv-upload" style={styles.uploadLabel}>
            <p>Drag and drop your CSV file here</p>
            <p>or</p>
            <button
                type="button" // Ensure it's not a submit button
                style={styles.chooseFileButton}
                onClick={() => document.getElementById('csv-upload').click()} // Programmatically trigger the file input
            >
                Choose File
            </button>
            <p>Upload a CSV file (max. 5MB) containing Metric Name, Value, and Timestamp columns</p>
        </label>
    </div>
