import React from 'react';

import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
  ArrowRight,
  ShieldCheck,
  BarChart,
  Globe,
  CloudUpload,
  Trophy,  // Added new icon for benchmarking
} from 'lucide-react';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    fontFamily: 'Arial, sans-serif',
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #064e3b, #276749)',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed',
    height: '100vh',
    zIndex: 10,
    boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
    transition: 'width 0.3s ease',
  },
  logo: {
    fontSize: '30px',
    marginBottom: '30px',
    fontWeight: 'bold',
