import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';

import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    HelpCircle,
    CloudUpload,
} from 'lucide-react';

const styles = {
    landingPage: {
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f4f4f9',
        fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
        width: '20%',
        minWidth: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        position: 'fixed',
        height: '100vh',
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
    },
    logoContainer: {
        display: 'flex',
        alignItems: 'center',
        marginBottom: '30px',
    },
    logo: {
        fontSize: '20px',
        fontWeight: 'bold',
        color: '#fff',
        textAlign: 'center',
        marginLeft: '10px',
    },
    navList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        width: '100%',
    },
    navItem: {
        color: 'white',
        textDecoration: 'none',
        padding: '15px 20px',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        borderRadius: '8px',
        marginBottom: '12px',
        fontFamily: "'Poppins', sans-serif",
        fontWeight: '500',
        transition: 'all 0.3s ease',
    },
    navItemHover: {
        backgroundColor: '#3b6b5c',
    },
    navIcon: {
        marginRight: '20px',
    },
    content: {
        flex: 1,
        padding: '30px',
        marginLeft: '20%',
        backgroundColor: '#ffffff',
        overflowY: 'auto',
    },
    sectionTitle: {
        fontWeight: 'bold',
        marginBottom: '15px',
        fontSize: '18px',
    },
    inputField: {
        width: '100%',
        padding: '10px',
        marginBottom: '10px',
        borderRadius: '5px',
        border: '1px solid #ddd',
        fontSize: '14px',
    },
    button: {
        backgroundColor: '#ffffff',
        color: 'black',
        border: 'none',
        padding: '10px 20px',
        borderRadius: '5px',
        fontWeight: 'bold',
        fontSize: '16px',
        cursor: 'pointer',
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
        transition: 'all 0.3s ease',
    },
    buttonHover: {
        backgroundColor: '#3b6b5c',
        color: 'white',
    },
    preview: {
        marginTop: '20px',
        padding: '10px',
        backgroundColor: '#f9f9f9',
        borderRadius: '5px',
        boxShadow: '0 1px 6px rgba(0, 0, 0, 0.1)',
    },
};

const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
];

const Sidebar = () => {
    const [csvFile, setCsvFile] = useState(null);
    const [logoFile, setLogoFile] = useState(null);
    const [formErrors, setFormErrors] = useState([]);

    const handleCsvUpload = (e) => {
        const file = e.target.files[0];
        if (file && file.size < 5 * 1024 * 1024 && file.name.endsWith('.csv')) {
            setCsvFile(file);
            setFormErrors([]);
        } else {
            setFormErrors(['Invalid file format or size!']);
        }
    };

    const handleLogoUpload = (e) => {
        const file = e.target.files[0];
        if (
            file &&
            ['image/png', 'image/jpg', 'image/jpeg', 'image/svg+xml'].includes(file.type) &&
            file.size < 2 * 1024 * 1024
        ) {
            setLogoFile(URL.createObjectURL(file));
            setFormErrors([]);
        } else {
            setFormErrors(['Invalid logo format or size!']);
        }
    };

    return (
        <div style={styles.landingPage}>
            <nav style={styles.sidebar}>
                <div style={styles.logoContainer}>
                    <img src={GreenTrendz} alt="Logo" width="50" height="50" />
                    <h5 style={styles.logo}>GreenTrendz</h5>
                </div>
                <ul style={styles.navList}>
                    {menuItems.map((item) => (
                        <li key={item.path}>
                            <a
                                href={item.path}
                                style={styles.navItem}
                                onMouseEnter={(e) => (e.target.style.backgroundColor = styles.navItemHover.backgroundColor)}
                                onMouseLeave={(e) => (e.target.style.backgroundColor = 'transparent')}
                            >
                                <item.icon style={styles.navIcon} size={20} />
                                {item.label}
                            </a>
                        </li>
                    ))}
                </ul>
            </nav>
            <div style={styles.content}>
                <h2 style={styles.sectionTitle}>Upload ESG Data</h2>
                <input
                    style={styles.inputField}
                    type="file"
                    accept=".csv"
                    onChange={handleCsvUpload}
                />
                {csvFile && (
                    <div style={styles.preview}>
                        <h4>CSV File: {csvFile.name}</h4>
                        <p>Size: {Math.round(csvFile.size / 1024)} KB</p>
                    </div>
                )}
                <h3 style={styles.sectionTitle}>Upload Logo</h3>
                <input
                    style={styles.inputField}
                    type="file"
                    accept="image/*"
                    onChange={handleLogoUpload}
                />
                {logoFile && (
                    <div style={styles.preview}>
                        <img src={logoFile} alt="Logo Preview" width="100" />
                    </div>
                )}
            </div>
        </div>
    );
};

export default Sidebar;
