import { FaUserCircle } from 'react-icons/fa'; // User profile icon
import { Link } from 'react-router-dom'; // Use Link for routing
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  UserCircle,
  HelpCircle,
  CloudUpload,
} from 'lucide-react';
import GreenTrendz from '../assets/img/logo_1.png';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    fontFamily: 'Arial, sans-serif',
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #064e3b, #276749)',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed',
    height: '100vh',
    zIndex: 10,
    boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
    transition: 'width 0.3s ease',
  },
  logo: {
    fontSize: '25px',
    marginBottom: '30px',
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
    marginTop: '20px',
    transition: 'transform 0.3s ease',
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    width: '100%',
  },
  navItem: {
    color: 'white',
    textDecoration: 'none',
    padding: '15px 20px',
    display: 'flex',
    alignItems: 'center',
    width: '100%',
    borderRadius: '8px',
    marginBottom: '12px',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    cursor: 'pointer',
    fontFamily: "'Poppins', sans-serif",
    fontWeight: '500',
  },
  content: {
    flex: 1,
    padding: '30px',
    boxSizing: 'border-box',
    backgroundColor: '#f8f9fa',
    overflowY: 'auto',
    marginLeft: '250px',
    transition: 'margin-left 0.3s ease',
  },
  button: {
    backgroundColor: '#ffffff',
    color: 'Black',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    fontWeight: 'bold',
    fontSize: '16px',
    cursor: 'pointer',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
    transition: 'background-color 0.3s ease, transform 0.2s ease',
  },
};

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'CarbonCalculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
  { icon: UserCircle, label: 'Account', path: '/account' },
];

const Dashboard = () => {
  const [esgData, setEsgData] = useState([]);

  // Fetch data from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/esgdata');
        setEsgData(response.data);
      } catch (error) {
        console.error('Error fetching ESG data:', error);
      }
    };

    fetchData();
  }, []);

  // Process data for the chart
  const categories = esgData.map((item) => item.category);
  const values = esgData.map((item) => item.value);

  const chartData = {
    labels: categories,
    datasets: [
      {
        label: 'ESG Data',
        data: values,
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#4BC0C0',
          '#9966FF',
        ],
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div style={styles.landingPage}>
      <nav style={styles.sidebar}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img
            src={GreenTrendz}
            alt="Logo"
            width="50"
            height="50"
            className="d-inline-block align-top me-2"
          />
          <h5 style={styles.logo}>GreenTrendz</h5>
        </div>
        <ul style={styles.navList}>
          {menuItems.map((item) => (
            <li key={item.path}>
              <Link
                to={item.path}
                style={styles.navItem}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#3b6b5c';
                  e.target.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '';
                  e.target.style.transform = '';
                }}
              >
                <item.icon style={styles.navIcon} size={20} />
                <span>{item.label}</span>
              </Link>
            </li>
          ))}
        </ul>
        <div style={{ marginTop: 'auto', textAlign: 'center' }}>
          <button style={styles.button} onClick={() => alert('Logout clicked')}>
            Logout
          </button>
        </div>
      </nav>
      <div style={styles.content}>
        <h1>ESG Dashboard</h1>
        <div style={{ height: '400px' }}>
          <Bar data={chartData} options={chartOptions} />
        </div>
        <table border="1" style={{ marginTop: '20px', width: '100%' }}>
          <thead>
            <tr>
              <th>Title</th>
              <th>Value</th>
              <th>Category</th>
            </tr>
          </thead>
          <tbody>
            {esgData.map((item, index) => (
              <tr key={index}>
                <td>{item.title}</td>
                <td>{item.value}</td>
                <td>{item.category}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
