import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaUserCircle } from 'react-icons/fa';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  UserCircle,
  HelpCircle,
  CloudUpload,
} from 'lucide-react';
import GreenTrendz from '../assets/img/logo_1.png';
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend } from 'recharts';

const styles = {
  landingPage: {
      display: 'flex',
      height: '100vh',
      backgroundColor: '#f4f4f9',
      fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
      width: '250px',
      background: 'linear-gradient(to bottom right, #064e3b, #276749)',
      color: 'white',
      padding: '20px',
      boxSizing: 'border-box',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'flex-start',
      position: 'fixed',
      height: '100vh',
      zIndex: 10,
      boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
      transition: 'width 0.3s ease',
    },
    logo: {
      fontSize: '25px',
      marginBottom: '30px',
      fontWeight: 'bold',
      color: '#fff',
      textAlign: 'center',
      marginTop: '20px',
      transition: 'transform 0.3s ease',
    },
    logoHover: {
      transform: 'scale(1.1)',
    },
    navList: {
      listStyle: 'none',
      padding: 0,
      margin: 0,
      width: '100%',
    },
    navItem: {
      color: 'white',
      textDecoration: 'none',
      padding: '15px 20px',
      display: 'flex',
      alignItems: 'center',
      width: '100%',
      borderRadius: '8px',
      marginBottom: '12px',
      transition: 'background-color 0.3s ease, transform 0.3s ease',
      cursor: 'pointer',
      fontfamily: "'Poppins', sans-serif", // Custom font for links
          fontWeight: '500', // Optional for bold text
      },
        navItemHover: {
      backgroundColor: '#3b6b5c',
      transform: 'scale(1.05)',
    },
    navIcon: {
      marginRight: '20px',
      transition: 'transform 0.3s ease',
    },
   
      content: {
          flex: 1,
          padding: '30px',
          boxSizing: 'border-box',
          backgroundColor: '#f8f9fa', // Light gray background for a softer look
          overflowY: 'auto',
          marginLeft: '250px',
          transition: 'margin-left 0.3s ease',
      },
     
      
      
      
      
  button: {
      backgroundColor: '#ffffff', // Default background color
      color: 'Black', // Text color
      border: 'none', // No border
      padding: '10px 20px', // Padding for a nice clickable area
      borderRadius: '5px', // Rounded corners
      fontWeight: 'bold', // Bold text
      fontSize: '16px', // Font size
      cursor: 'pointer', // Pointer cursor on hover
      boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Subtle shadow for a 3D effect
      transition: 'background-color 0.3s ease, transform 0.2s ease', // Smooth transition for hover effects
    },
};

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'CarbonCalculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
  { icon: UserCircle, label: 'Account', path: '/account' }, // Added Account item
];
const Dashboard = () => {
  const [data, setData] = useState({ environmentalData: {}, socialData: {}, governanceData: {} });
  const [activeSection, setActiveSection] = useState('environmental');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/esg-data');
        setData(response.data || { environmentalData: {}, socialData: {}, governanceData: {} });
      } catch (error) {
        console.error('Error fetching ESG data:', error);
        setError('Failed to fetch data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) return <p>Loading ESG data...</p>;
  if (error) return <p>{error}</p>;

  const renderMetrics = (sectionData) => {
    if (!sectionData || typeof sectionData !== 'object') return <p>No data available</p>;
    return Object.entries(sectionData).map(([key, value]) => (
      <div key={key} className="flex justify-between items-center border-b pb-2">
        <span className="font-medium text-gray-600">{key.replace(/([A-Z])/g, ' $1').trim()}</span>
        <span className="text-lg font-bold">{value}</span>
      </div>
    ));
  };

  return (
    <div style={styles.landingPage}>
            <nav style={styles.sidebar}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img
                        src={GreenTrendz}
                        alt="Logo"
                        width="50"
                        height="50"
                        className="d-inline-block align-top me-2"
                    />
                    <h5 style={styles.logo}>GreenTrendz</h5>
                </div>
                <ul style={styles.navList}>
                    {menuItems.map((item) => (
                        <li key={item.path}>
                            <a
                                href={item.path}
                                style={styles.navItem}
                                onMouseEnter={(e) => {
                                    e.target.style.backgroundColor = '#3b6b5c';
                                    e.target.style.transform = 'scale(1.05)';
                                }}
                                onMouseLeave={(e) => {
                                    e.target.style.backgroundColor = '';
                                    e.target.style.transform = '';
                                }}
                            >
                                <item.icon style={styles.navIcon} size={20} />
                                <span>{item.label}</span>
                            </a>
                        </li>
                    ))}
                </ul>
                <div style={{ marginTop: 'auto', textAlign: 'center' }}>
                    <button
                        style={styles.button}
                        onClick={() => setShowLogoutPopup(true)}
                    >
                        Logout
                    </button>
                </div>
            </nav>
   {/* Main Content */}
   <main className="ml-64 p-6 w-full">
        <h1 className="text-2xl font-bold">Dashboard</h1>
        <p>Welcome to the Dashboard page!</p>

        {/* ESG Data Sections */}
        <div className="p-6 space-y-6">
          <div className="flex space-x-4">
            <Button 
              onClick={() => setActiveSection('environmental')}
              variant={activeSection === 'environmental' ? 'default' : 'outline'}
              className="w-1/3"
            >
              Environmental
            </Button>
            <Button 
              onClick={() => setActiveSection('social')}
              variant={activeSection === 'social' ? 'default' : 'outline'}
              className="w-1/3"
            >
              Social
            </Button>
            <Button 
              onClick={() => setActiveSection('governance')}
              variant={activeSection === 'governance' ? 'default' : 'outline'}
              className="w-1/3"
            >
              Governance
            </Button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <Card>
              <CardHeader>
                <CardTitle>Key Metrics</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {activeSection === 'environmental' && renderMetrics(data.environmentalData)}
                  {activeSection === 'social' && renderMetrics(data.socialData)}
                  {activeSection === 'governance' && renderMetrics(data.governanceData)}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Overview</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-4">
                  {activeSection === 'environmental' && (
                    <>
                      <div>Energy Systems: {data.environmentalData?.energyEfficientSystems}</div>
                      <div>Rainwater Harvesting: {data.environmentalData?.rainwaterHarvesting}</div>
                      <div>Zero Waste: {data.environmentalData?.zeroWasteToLandfill}</div>
                      <div>Biodiversity: {data.environmentalData?.biodiversityMeasures}</div>
                    </>
                  )}
                  {activeSection === 'social' && (
                    <>
                      <div>DEI Programs: {data.socialData?.deiPrograms}</div>
                      <div>Human Rights: {data.socialData?.humanRightsComplaints}</div>
                      <div>Safety: {data.socialData?.workforceSafetyIncidents}</div>
                      <div>Community: {data.socialData?.sustainableCommunityDevelopment}</div>
                    </>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </main>
    </div>
    
  );

};

export default Dashboard;
