import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';

const AccountPage = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [userData, setUserData] = useState({
    companyName: 'EcoReportHub Inc.',
    email: 'user@example.com',
    phone: '+1234567890',
    address: '123 Green Street, EcoCity, Earth',
  });

  const [formData, setFormData] = useState(userData);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setFormData(userData); // Reset form data to original data
  };

  const handleSave = (e) => {
    e.preventDefault();
    setUserData(formData); // Update user data
    setIsEditing(false); // Exit edit mode
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  return (
    <div style={{ display: 'flex', height: '100vh', backgroundColor: '#f4f4f9' }}>
      {/* Sidebar */}
      <nav style={{
        width: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        display: 'flex',
        flexDirection: 'column',
        height: '100vh',
        position: 'fixed',
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
      }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '30px' }}>
          <img
            src={GreenTrendz}
            alt="Logo"
            width="50"
            height="50"
          />
          <h5 style={{ color: 'white', marginLeft: '10px', fontWeight: 'bold' }}>GreenTrendz</h5>
        </div>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li style={{ marginBottom: '15px' }}>
            <a href="/dashboard" style={{ color: 'white', textDecoration: 'none' }}>Dashboard</a>
          </li>
          <li style={{ marginBottom: '15px' }}>
            <a href="/settings" style={{ color: 'white', textDecoration: 'none' }}>Settings</a>
          </li>
          <li>
            <button style={{
              background: 'white',
              color: '#064e3b',
              border: 'none',
              borderRadius: '5px',
              padding: '10px 15px',
              cursor: 'pointer',
            }}>Logout</button>
          </li>
        </ul>
      </nav>

      {/* Content */}
      <div style={{
        marginLeft: '250px',
        padding: '30px',
        width: '100%',
        overflowY: 'auto',
      }}>
        <h2 style={{ color: '#064e3b', marginBottom: '20px' }}>Account Details</h2>

        {isEditing ? (
          <form onSubmit={handleSave}>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="companyName" style={{ display: 'block', marginBottom: '5px' }}>Company Name</label>
              <input
                type="text"
                id="companyName"
                name="companyName"
                value={formData.companyName}
                onChange={handleChange}
                style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="email" style={{ display: 'block', marginBottom: '5px' }}>Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="phone" style={{ display: 'block', marginBottom: '5px' }}>Phone</label>
              <input
                type="text"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="address" style={{ display: 'block', marginBottom: '5px' }}>Address</label>
              <input
                type="text"
                id="address"
                name="address"
                value={formData.address}
                onChange={handleChange}
                style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
              />
            </div>
            <button type="submit" style={{
              background: '#38a169',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              marginRight: '10px',
              cursor: 'pointer',
            }}>Save</button>
            <button type="button" onClick={handleCancel} style={{
              background: '#e53e3e',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}>Cancel</button>
          </form>
        ) : (
          <div>
            <p><strong>Company Name:</strong> {userData.companyName}</p>
            <p><strong>Email:</strong> {userData.email}</p>
            <p><strong>Phone:</strong> {userData.phone}</p>
            <p><strong>Address:</strong> {userData.address}</p>
            <button onClick={handleEdit} style={{
              background: '#3182ce',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}>Edit</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AccountPage;
