import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Home, LayoutDashboard, FileText, BarChart2, Calculator,
  Settings, UserCircle, HelpCircle, CloudUpload
} from 'lucide-react';
import { Card, CardHeader, CardTitle, CardContent } from './ui/card';
import { Button } from './ui/button';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, Legend,
  PieChart, Pie, LineChart, Line, AreaChart, Area
} from 'recharts';

// Sidebar menu
const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'CarbonCalculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
  { icon: UserCircle, label: 'Account', path: '/account' },
];

const Dashboard = () => {
  const [data, setData] = useState({});
  const [activeSection, setActiveSection] = useState('environmental');
  const navigate = useNavigate();

  // Fetch data from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/esg-data');
        setData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  // Ensure data exists for selected section
  const sectionData = data[`${activeSection}Data`] || {};

  // Convert data for charts
  const chartData = Object.entries(sectionData).map(([key, value]) => ({
    name: key.replace(/([A-Z])/g, ' $1').trim(),
    value: parseFloat(value) || 0,
  }));

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <nav className="w-64 bg-green-700 text-white p-5 flex flex-col fixed h-full shadow-md">
        <div className="flex items-center space-x-3">
          <h5 className="text-xl font-bold">GreenTrendz</h5>
        </div>

        <ul className="mt-6 space-y-2">
          {menuItems.map((item) => (
            <li key={item.path}>
              <a href={item.path} className="flex items-center space-x-2 px-4 py-2 hover:bg-green-800 rounded">
                <item.icon size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>

        <button
          className="mt-auto bg-white text-green-700 p-2 rounded flex items-center justify-center space-x-2"
          onClick={() => navigate('/')}
        >
          <Settings size={18} />
          <span>Logout</span>
        </button>
      </nav>

      {/* Main Content */}
      <main className="ml-64 p-6 flex-1">
        <h1 className="text-3xl font-bold mb-4">ESG Dashboard</h1>

        {/* Section Toggle Buttons */}
        <div className="flex space-x-4 mb-6">
          {['environmental', 'social', 'governance'].map((section) => (
            <Button
              key={section}
              onClick={() => setActiveSection(section)}
              variant={activeSection === section ? 'default' : 'outline'}
              className="w-1/3 bg-green-600 hover:bg-green-700 text-white"
            >
              {section.charAt(0).toUpperCase() + section.slice(1)}
            </Button>
          ))}
        </div>

        {/* Metrics Cards (2 Rows, 4 Columns) */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {Object.entries(sectionData).slice(0, 4).map(([key, value]) => (
            <Card key={key}>
              <CardHeader>
                <CardTitle>{key.replace(/([A-Z])/g, ' $1').trim()}</CardTitle>
              </CardHeader>
              <CardContent>
                <span className="text-lg font-bold">{value}</span>
              </CardContent>
            </Card>
          ))}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mt-6">
          {Object.entries(sectionData).slice(4, 8).map(([key, value]) => (
            <Card key={key}>
              <CardHeader>
                <CardTitle>{key.replace(/([A-Z])/g, ' $1').trim()}</CardTitle>
              </CardHeader>
              <CardContent>
                <span className="text-lg font-bold">{value}</span>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Charts Section */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
          {/* Bar Chart */}
          <Card>
            <CardHeader>
              <CardTitle>Bar Chart</CardTitle>
            </CardHeader>
            <CardContent>
              <BarChart width={500} height={300} data={chartData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="value" fill="#8884d8" />
              </BarChart>
            </CardContent>
          </Card>

          {/* Pie Chart */}
          <Card>
            <CardHeader>
              <CardTitle>Pie Chart</CardTitle>
            </CardHeader>
            <CardContent>
              <PieChart width={400} height={300}>
                <Pie data={chartData} dataKey="value" cx="50%" cy="50%" outerRadius={100} fill="#82ca9d" label />
                <Tooltip />
                <Legend />
              </PieChart>
            </CardContent>
          </Card>

          {/* Line Chart */}
          <Card>
            <CardHeader>
              <CardTitle>Line Chart</CardTitle>
            </CardHeader>
            <CardContent>
              <LineChart width={500} height={300} data={chartData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="value" stroke="#8884d8" />
              </LineChart>
            </CardContent>
          </Card>

          {/* Area Chart */}
          <Card>
            <CardHeader>
              <CardTitle>Area Chart</CardTitle>
            </CardHeader>
            <CardContent>
              <AreaChart width={500} height={300} data={chartData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Area type="monotone" dataKey="value" stroke="#82ca9d" fill="#82ca9d" />
              </AreaChart>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
