import React, { useState } from 'react';
import backgroundImage from '../assets/img/bg2.jpg';
import GreenTrendz from '../assets/img/logo_1.png';
import { Link } from 'react-router-dom';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
  ArrowRight,
  CloudUpload,
  Trophy,
} from 'lucide-react';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    fontFamily: 'Arial, sans-serif',
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #064e3b, #276749)',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed',
    height: '100vh',
    zIndex: 10,
    boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
    transition: 'width 0.3s ease',
  },
  logo: {
    fontSize: '25px',
    marginBottom: '30px',
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
    marginTop: '20px',
    transition: 'transform 0.3s ease',
  },
  logoHover: {
    transform: 'scale(1.1)',
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    width: '100%',
  },
  navItem: {
    color: 'white',
    textDecoration: 'none',
    padding: '15px 20px',
    display: 'flex',
    alignItems: 'center',
    width: '100%',
    borderRadius: '8px',
    marginBottom: '12px',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    cursor: 'pointer',
    fontFamily: "'Poppins', sans-serif",
    fontWeight: '500',
  },
  navItemHover: {
    backgroundColor: '#3b6b5c',
    transform: 'scale(1.05)',
  },
  navIcon: {
    marginRight: '20px',
    transition: 'transform 0.3s ease',
  },
  content: {
    flex: 1,
    padding: '30px',
    boxSizing: 'border-box',
    backgroundColor: '#fffff',
    overflowY: 'auto',
    marginLeft: '250px',
    transition: 'margin-left 0.3s ease',
  },
  header: {
    fontSize: '50px',
    fontWeight: 1000,
    marginBottom: '10px',
    marginLeft: '90px',
    fontFamily: "'Roboto', sans-serif",
    color: '#ffffff',
  },
  button: {
    backgroundColor: 'linear-gradient(to right, #38a169, #2f855a)',
    color: 'white',
    padding: '12px 25px',
    borderRadius: '8px',
    border: 'none',
    fontWeight: 'bold',
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
    transition: 'transform 0.3s ease, background-color 0.3s ease, box-shadow 0.3s ease',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
    cursor: 'pointer',
  },
  buttonHover: {
    transform: 'scale(1.05)',
    backgroundColor: 'linear-gradient(to right, #2f855a, #22543d)',
    color: 'white',
    boxShadow: '0 6px 15px rgba(0, 0, 0, 0.2)',
  },
};

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/data-upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
];

const LandingPage = () => {
  const [showLogoutPopup, setShowLogoutPopup] = useState(false);

  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img src={GreenTrendz} alt="Logo" width="50" height="50" className="d-inline-block align-top me-2" />
          <h5
            style={{
              ...styles.logo,
              fontSize: '18px', // Adjust font size here
              margin: 0, // Remove default margin
              fontWeight: 'bold',
            }}
            onMouseEnter={(e) => (e.target.style.transform = 'scale(1.1)')}
            onMouseLeave={(e) => (e.target.style.transform = '')}
          >
            GreenTrendz
          </h5>
        </div>

        <ul style={styles.navList}>
          {menuItems.map((item) => (
            <li key={item.path}>
              <Link
                to={item.path}
                style={styles.navItem}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#3b6b5c';
                  e.target.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '';
                  e.target.style.transform = '';
                }}
              >
                <item.icon style={styles.navIcon} size={20} />
                <span>{item.label}</span>
              </Link>
            </li>
          ))}
        </ul>

        {/* Logo and User Info */}
        <div style={{ display: 'flex', flexDirection: 'column', marginTop: 'auto', alignItems: 'center' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <img src={GreenTrendz} alt="Company Logo" width="50" height="50" style={{ borderRadius: '50%' }} />
            <span style={{ color: 'white', fontSize: '16px', fontWeight: 'bold' }}>GreenTrendz</span>
          </div>
          <button
            style={styles.button}
            onClick={() => setShowLogoutPopup(true)}
            onMouseEnter={(e) => (e.target.style.backgroundColor = '#276749')}
            onMouseLeave={(e) => (e.target.style.backgroundColor = '#3b6b5c')}
          >
            Logout
          </button>
        </div>
      </nav>

      {/* Main Content */}
      <main style={styles.content}>
        {/* Hero Section */}
        <div
          style={{
            backgroundImage: `url(${backgroundImage})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            padding: '60px 0',
            minHeight: '350px',
            color: 'white',
            textAlign: 'left',
            borderRadius: '20px',
            overflow: 'hidden',
          }}
        >
          <h1 style={styles.header}>Transform Your ESG Reporting Process</h1>
          <p
            style={{
              fontSize: '18px',
              marginBottom: '20px',
              lineHeight: '1.6',
              color: '#cffad4',
              marginLeft: '90px',
              fontFamily: 'Arial, sans-serif',
              marginRight: '30%',
              fontStyle: 'italic',
            }}
          >
            Generate comprehensive ESG reports, track sustainability metrics, and make data-driven decisions with
            our powerful platform.
          </p>

          <div style={{ display: 'flex', gap: '10px' }}>
            <Link
              to="/upload"
              style={{
                textDecoration: 'none',
              }}
            >
              <button
                style={{
                  backgroundColor: '#fff',
                  color: '#276749',
                  padding: '10px 20px',
                  borderRadius: '5px',
                  border: 'none',
                  fontWeight: 'bold',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                }}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#276749';
                  e.target.style.color = '#fff';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '#fff';
                  e.target.style.color = '#276749';
                }}
              >
                <ArrowRight size={16} /> Start Now
              </button>
            </Link>

            <Link
              to="/demo"
              style={{
                textDecoration: 'none',
              }}
            >
              <button
                style={{
                  backgroundColor: 'transparent',
                  border: '2px solid #276749',
                  color: '#276749',
                  padding: '10px 20px',
                  borderRadius: '5px',
                  fontWeight: 'bold',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                }}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#276749';
                  e.target.style.color = '#fff';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = 'transparent';
                  e.target.style.color = '#276749';
                }}
              >
                <ArrowRight size={16} /> Request a Demo
              </button>
            </Link>
          </div>
        </div>
      </main>
    </div>
  );
};

export default LandingPage;
