import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  UserCircle,
  HelpCircle,
  CloudUpload,
  LogOut
} from 'lucide-react';
import { Card, CardHeader, CardTitle, CardContent } from './card';
import { Button } from '/button';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  PieChart,
  Pie,
  LineChart,
  Line,
  ResponsiveContainer
} from 'recharts';

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'CarbonCalculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
  { icon: UserCircle, label: 'Account', path: '/account' },
];

const Dashboard = () => {
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [activeSection, setActiveSection] = useState('environmental');
  const navigate = useNavigate();

  const data = {
    environmental: {
      renewableEnergyUsage: '75%',
      totalEnergyConsumption: '5000 kWh',
      greenBuildingCertifications: 'LEED Gold',
      ghgEmissionTargets: '20% reduction',
      energyEfficientSystems: 'Renewable',
      wastewaterReused: '30%',
      rainwaterHarvesting: 'Yes',
      recycledWaterUse: '40%',
      zeroWasteToLandfill: 'Yes',
      wasteSentToRecyclers: '80%',
      nativeLandscaping: 'Yes',
      biodiversityMeasures: 'Yes',
    },
    social: {
      genderDiversity: '45% female',
      employeeTrainingHours: '20 hours',
      volunteerismHoursPerEmployee: '10 hours',
      workforceSafetyIncidents: 'LTIFR',
      csrInitiatives: '5 projects',
      communityEngagementHours: '1000 hours',
      sustainableCommunityDevelopment: 'Yes',
      humanRightsComplaints: 'No',
      wellCertifiedProjects: '3 projects',
      deiPrograms: 'Yes',
    },
    governance: {
      independentDirectors: '40%',
      boardGovernanceInitiatives: 'Yes',
      regulatoryComplianceStatus: 'Yes',
      nonComplianceCases: '0',
      employeesTrainedOnCodeOfConduct: '90%',
      ethicsViolationsReported: '2',
      riskManagementFramework: 'ISO 31000',
      riskAssessmentsConducted: 'Quarterly',
    },
  };

  const chartData = {
    environmental: [
      { name: 'Renewable Energy', value: 75 },
      { name: 'Wastewater Reused', value: 30 },
      { name: 'Recycled Water', value: 40 },
      { name: 'Waste to Recyclers', value: 80 },
    ],
    social: [
      { name: 'Gender Diversity', value: 45 },
      { name: 'Training Hours', value: 20 },
      { name: 'Volunteerism Hours', value: 10 },
      { name: 'Community Hours', value: 1000 },
    ],
    governance: [
      { name: 'Independent Directors', value: 40 },
      { name: 'Employees Trained', value: 90 },
      { name: 'Ethics Violations', value: 2 },
      { name: 'Risk Assessments', value: 4 },
    ],
  };

  const renderChart = () => {
    const chartWidth = window.innerWidth < 768 ? window.innerWidth - 64 : 500;
    
    switch (activeSection) {
      case 'environmental':
        return (
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie 
                data={chartData.environmental}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={100}
                fill="#10b981"
                label
              />
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        );
      case 'social':
        return (
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={chartData.social}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="value" fill="#10b981" />
            </BarChart>
          </ResponsiveContainer>
        );
      case 'governance':
        return (
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={chartData.governance}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="value" stroke="#10b981" />
            </LineChart>
          </ResponsiveContainer>
        );
      default:
        return null;
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <nav className="fixed w-64 h-full bg-gradient-to-br from-green-900 to-green-700 text-white p-6">
        <div className="flex items-center gap-3 mb-8">
          <img src="/api/placeholder/50/50" alt="Logo" className="w-12 h-12" />
          <h5 className="text-2xl font-bold">GreenTrendz</h5>
        </div>

        <ul className="space-y-2">
          {menuItems.map((item) => (
            <li key={item.path}>
              <Button
                variant="ghost"
                className="w-full justify-start text-white hover:bg-green-800 hover:text-white transition-all"
                onClick={() => navigate(item.path)}
              >
                <item.icon className="mr-2 h-5 w-5" />
                {item.label}
              </Button>
            </li>
          ))}
        </ul>

        <Button
          variant="secondary"
          className="absolute bottom-6 left-6 right-6 bg-white text-green-900 hover:bg-gray-100"
          onClick={() => setShowLogoutModal(true)}
        >
          <LogOut className="mr-2 h-4 w-4" />
          Logout
        </Button>
      </nav>

      {/* Main Content */}
      <main className="ml-64 flex-1 p-8">
        <h1 className="text-3xl font-bold mb-4">ESG Dashboard</h1>
        <p className="text-gray-600 mb-6">Welcome to the GreenTrendz ESG Dashboard!</p>

        <div className="space-y-6">
          <div className="flex space-x-4">
            {['environmental', 'social', 'governance'].map((section) => (
              <Button
                key={section}
                onClick={() => setActiveSection(section)}
                variant={activeSection === section ? 'default' : 'outline'}
                className={`flex-1 ${
                  activeSection === section
                    ? 'bg-green-600 hover:bg-green-700 text-white'
                    : 'hover:bg-green-50'
                }`}
              >
                {section.charAt(0).toUpperCase() + section.slice(1)}
              </Button>
            ))}
          </div>

          <div className="grid md:grid-cols-2 gap-6">
            <Card>
              <CardHeader>
                <CardTitle>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Metrics</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {Object.entries(data[activeSection]).map(([key, value]) => (
                    <div key={key} className="flex justify-between items-center border-b pb-2">
                      <span className="font-medium text-gray-600">
                        {key.replace(/([A-Z])/g, ' $1').trim()}
                      </span>
                      <span className="text-lg font-semibold">{value}</span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Visualization</CardTitle>
              </CardHeader>
              <CardContent>
                {renderChart()}
              </CardContent>
            </Card>
          </div>
        </div>
      </main>

      {/* Logout Modal */}
      {showLogoutModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <Card className="w-96">
            <CardHeader>
              <CardTitle>Confirm Logout</CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-gray-600 mb-6">Are you sure you want to logout?</p>
              <div className="flex justify-end space-x-4">
                <Button
                  variant="outline"
                  onClick={() => setShowLogoutModal(false)}
                >
                  Cancel
                </Button>
                <Button
                  variant="destructive"
                  onClick={() => navigate('/')}
                >
                  Logout
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
