import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';
import './style.css'; // If style.css is in the same folder as FileUploads.js

import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    UserCircle,
    HelpCircle,
    CloudUpload,
} from 'lucide-react';

const styles = {
    landingPage: {
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f4f4f9',
        fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
        width: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        position: 'fixed',
        height: '100vh',
        zIndex: 10,
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
    },
    logo: {
        fontSize: '25px',
        marginBottom: '30px',
        fontWeight: 'bold',
        color: '#fff',
        textAlign: 'center',
    },
    navList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        width: '100%',
    },
    navItem: {
        color: 'white',
        textDecoration: 'none',
        padding: '15px 20px',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        borderRadius: '8px',
        marginBottom: '12px',
        cursor: 'pointer',
    },
    content: {
        flex: 1,
        padding: '30px',
        backgroundColor: '#f8f9fa',
        overflowY: 'auto',
        marginLeft: '250px',
    },
    button: {
        backgroundColor: '#ffffff',
        color: 'Black',
        border: 'none',
        padding: '10px 20px',
        borderRadius: '5px',
        fontWeight: 'bold',
        fontSize: '16px',
        cursor: 'pointer',
    },
};

const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
    { icon: UserCircle, label: 'Account', path: '/account' },
];

const Dropdown = ({ label, value, options, onChange }) => (
    <div>
        <label>{label}</label>
        <select
            value={value || ''}
            onChange={(e) => onChange(e.target.value)}
            className="inputField"
        >
            <option value="" disabled>
                Select an option
            </option>
            {options.map((option, i) => (
                <option key={i} value={option}>
                    {option}
                </option>
            ))}
        </select>
    </div>
);

const Sidebar = () => {
    const [showLogoutPopup, setShowLogoutPopup] = useState(false);

    const [environmentalData, setEnvironmentalData] = useState({
        energyEfficientSystems: '',
        rainwaterHarvesting: '',
    });

    const [dataImportData, setDataImportData] = useState({
        startDate: '',
        endDate: '',
    });

    const [csvFile, setCsvFile] = useState(null);
    const [logoFile, setLogoFile] = useState(null);

    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleCsvUpload = (e) => {
        const file = e.target.files[0];
        if (file && file.type !== 'text/csv') {
            alert('Please upload a valid CSV file.');
            return;
        }
        setCsvFile(file);
    };

    const handleLogoUpload = (e) => {
        const file = e.target.files[0];
        if (file && !['image/png', 'image/jpeg'].includes(file.type)) {
            alert('Please upload a valid image file (PNG or JPEG).');
            return;
        }
        setLogoFile(file);
    };

    const handleSubmit = async () => {
        if (!dataImportData.startDate || !dataImportData.endDate) {
            alert('Please select both start and end dates.');
            return;
        }

        if (!csvFile || !logoFile) {
            alert('Please upload both CSV and Logo files.');
            return;
        }

        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        const formData = new FormData();
        formData.append('environmentalData', JSON.stringify(environmentalData));
        formData.append('startDate', dataImportData.startDate);
        formData.append('endDate', dataImportData.endDate);

        if (csvFile) formData.append('csvFile', csvFile);
        if (logoFile) formData.append('logoFile', logoFile);

        try {
            const response = await fetch('http://localhost:5003/api/save-esg-data', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const result = await response.json();
                setSuccessMessage(result.message || 'Data submitted successfully!');
            } else {
                const errorDetail = await response.text();
                throw new Error(`Error ${response.status}: ${errorDetail}`);
            }
        } catch (error) {
            setErrorMessage('Error submitting data: ' + error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.landingPage}>
            <nav style={styles.sidebar}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img src={GreenTrendz} alt="Logo" width="50" height="50" />
                    <h5 style={styles.logo}>GreenTrendz</h5>
                </div>
                <ul style={styles.navList}>
                    {menuItems.map((item) => (
                        <li key={item.path}>
                            <a href={item.path} style={styles.navItem}>
                                <item.icon size={20} />
                                <span>{item.label}</span>
                            </a>
                        </li>
                    ))}
                </ul>
                <button style={styles.button} onClick={() => setShowLogoutPopup(true)}>
                    Logout
                </button>
            </nav>
            <div style={styles.content}>
                <h1>Upload ESG Data</h1>
                <div>
                    <Dropdown
                        label="Energy-efficient Systems"
                        value={environmentalData.energyEfficientSystems}
                        options={['Renewable', 'Grid', 'Fuel Energy']}
                        onChange={(value) =>
                            setEnvironmentalData({ ...environmentalData, energyEfficientSystems: value })
                        }
                    />
                    <Dropdown
                        label="Rainwater Harvesting"
                        value={environmentalData.rainwaterHarvesting}
                        options={['Yes', 'No']}
                        onChange={(value) =>
                            setEnvironmentalData({ ...environmentalData, rainwaterHarvesting: value })
                        }
                    />
                </div>
                <input type="file" onChange={handleCsvUpload} />
                <input type="file" onChange={handleLogoUpload} />
                <button onClick={handleSubmit}>Submit Data</button>
                {loading && <p>Loading...</p>}
                {successMessage && <p>{successMessage}</p>}
                {errorMessage && <p>{errorMessage}</p>}
            </div>
        </div>
    );
};

export default Sidebar;
