// LandingPage.js

import React from 'react';

const styles = {
    landingPage: {
      display: 'flex',
      height: '100vh',
      backgroundColor: '#f4f4f9',
    },
    sidebar: {
      width: '250px',
      backgroundColor: '#2f3b52',
      color: 'white',
      padding: '20px',
      boxSizing: 'border-box',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'flex-start',
    },
    logo: {
      fontSize: '24px',
      marginBottom: '30px',
    },
    navList: {
      listStyle: 'none',
      padding: 0,
      margin: 0,
      width: '100%',
    },
    navItem: {
      color: 'white',
      textDecoration: 'none',
      padding: '10px 15px',
      display: 'block',
      width: '100%',
      transition: 'background-color 0.3s',
    },
    navItemHover: {
      backgroundColor: '#3e4a61',
    },
    content: {
      flex: 1,
      padding: '20px',
      boxSizing: 'border-box',
      backgroundColor: '#fff',
    },
    header: {
      marginBottom: '30px',
      borderBottom: '2px solid #f0f0f0',
      paddingBottom: '10px',
    },
    mainSection: {
      padding: '20px',
      backgroundColor: '#f9f9f9',
      borderRadius: '8px',
      boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    },

}
const LandingPage = () => {
  return (
    <div style={styles.landingPage}>
      <nav style={styles.sidebar}>
        <h2 style={styles.logo}>ERP System</h2>
        <ul style={styles.navList}>
          <li><a href="#dashboard" style={styles.navItem}>Dashboard</a></li>
          <li><a href="#profile" style={styles.navItem}>Profile</a></li>
          <li><a href="#projects" style={styles.navItem}>Projects</a></li>
          <li><a href="#tasks" style={styles.navItem}>Tasks</a></li>
          <li><a href="#reports" style={styles.navItem}>Reports</a></li>
          <li><a href="#settings" style={styles.navItem}>Settings</a></li>
        </ul>
      </nav>

      <main style={styles.content}>
        <header style={styles.header}>
          <h1>Welcome to the ERP System</h1>
          <p>Manage your projects, tasks, and reports seamlessly.</p>
        </header>

        <section style={styles.mainSection}>
          <h2>Dashboard</h2>
          <p>This is the main content area where you can display dashboard widgets or other components.</p>
        </section>
      </main>
    </div>
  );
};



export default LandingPage;
