import React, { useState } from 'react';
import { FaBars, FaTimes } from 'react-icons/fa'; // Import icons for open/close
import { Link } from 'react-router-dom';
import './SideNavbar.css'; // Import the CSS file for styling

const SideNavbar = () => {
  const [isOpen, setIsOpen] = useState(true); // State to track sidebar open/close

  // Toggle sidebar function
  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`side-navbar ${isOpen ? 'open' : 'closed'}`}>
      {/* Toggle icon to open/close sidebar */}
      <div className="toggle-icon" onClick={toggleSidebar}>
        {isOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
      </div>

      {/* Sidebar content (visible only when open) */}
      {isOpen && (
        <>
          {/* Clickable logo that links to the homepage */}
          <Link to="/">
            <img
              src="/logo1-2.png" // Path to your logo in the public folder
              alt="Logo"
              width="150"
              height="50"
              className="logo"
            />
          </Link>

          {/* Dashboard link below the logo */}
          <ul>
            <li><Link to="/dashboard">Dashboard</Link></li>
            <li><Link to="Manualinput">Manual Input</Link></li>
            <li><Link to="csv file upload">CSV File Upload</Link></li>
            <li><Link to="upload reports">Upload Reports</Link></li>
          </ul>
        </>
      )}
    </div>
  );
};

export default SideNavbar;
