import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  UserCircle,
  HelpCircle,
  CloudUpload,
} from 'lucide-react';
import GreenTrendz from '../assets/img/logo_1.png';
import { Card, CardHeader, CardTitle, CardContent } from './card';
import { Button } from './button';
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, PieChart, Pie, LineChart, Line } from 'recharts';

const Dashboard = () => {
  const navigate = useNavigate();
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [activeSection, setActiveSection] = useState('environmental');

  const [data, setData] = useState(null);

  useEffect(() => {
    // Simulate API call or set default values
    setTimeout(() => {
      setData({
        environmentalData: {
          renewableEnergyUsage: '75%',
          totalEnergyConsumption: '5000 kWh',
          greenBuildingCertifications: 'LEED Gold',
          ghgEmissionTargets: '20% reduction',
          energyEfficientSystems: 'Renewable',
          wastewaterReused: '30%',
          rainwaterHarvesting: 'Yes',
          recycledWaterUse: '40%',
          zeroWasteToLandfill: 'Yes',
          wasteSentToRecyclers: '80%',
          nativeLandscaping: 'Yes',
          biodiversityMeasures: 'Yes',
        },
        socialData: {
          genderDiversity: '45% female',
          employeeTrainingHours: '20 hours',
          volunteerismHoursPerEmployee: '10 hours',
          workforceSafetyIncidents: 'LTIFR',
          csrInitiatives: '5 projects',
          communityEngagementHours: '1000 hours',
          sustainableCommunityDevelopment: 'Yes',
          humanRightsComplaints: 'No',
          wellCertifiedProjects: '3 projects',
          deiPrograms: 'Yes',
        },
        governanceData: {
          independentDirectors: '40%',
          boardGovernanceInitiatives: 'Yes',
          regulatoryComplianceStatus: 'Yes',
          nonComplianceCases: '0',
          employeesTrainedOnCodeOfConduct: '90%',
          ethicsViolationsReported: '2',
          riskManagementFramework: 'ISO 31000',
          riskAssessmentsConducted: 'Quarterly',
        },
      });
    }, 1000);
  }, []);

  // Handle null data gracefully
  if (!data) {
    return <div>Loading...</div>;
  }

  const getChartData = () => {
    if (activeSection === 'environmental') {
      return [
        { name: 'Renewable Energy', value: 75 },
        { name: 'Wastewater Reused', value: 30 },
        { name: 'Recycled Water', value: 40 },
        { name: 'Waste to Recyclers', value: 80 },
      ];
    } else if (activeSection === 'social') {
      return [
        { name: 'Gender Diversity', value: 45 },
        { name: 'Training Hours', value: 20 },
        { name: 'Volunteerism Hours', value: 10 },
        { name: 'Community Hours', value: 1000 },
      ];
    } else {
      return [
        { name: 'Independent Directors', value: 40 },
        { name: 'Employees Trained', value: 90 },
        { name: 'Ethics Violations', value: 2 },
        { name: 'Risk Assessments', value: 4 },
      ];
    }
  };

  return (
    <div style={{ display: 'flex', height: '100vh', backgroundColor: '#f4f4f9', fontFamily: 'Arial, sans-serif' }}>
      {/* Sidebar */}
      <nav style={{ width: '250px', background: 'linear-gradient(to bottom right, #064e3b, #276749)', color: 'white', padding: '20px', position: 'fixed', height: '100vh' }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img src={GreenTrendz} alt="Logo" width="50" height="50" />
          <h5 style={{ fontSize: '25px', marginLeft: '10px', color: '#fff' }}>GreenTrendz</h5>
        </div>
        <ul style={{ listStyle: 'none', padding: 0, marginTop: '20px' }}>
          {[
            { icon: Home, label: 'Home', path: '/land' },
            { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
            { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
            { icon: FileText, label: 'Reports', path: '/reports' },
            { icon: BarChart2, label: 'Comparison', path: '/comparison' },
            { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
            { icon: Settings, label: 'Settings', path: '/settings' },
            { icon: HelpCircle, label: 'Help & Support', path: '/help' },
            { icon: UserCircle, label: 'Account', path: '/account' },
          ].map((item, index) => (
            <li key={index} onClick={() => navigate(item.path)} style={{ cursor: 'pointer', padding: '10px', display: 'flex', alignItems: 'center', borderRadius: '8px', marginBottom: '12px', transition: '0.3s', color: '#fff' }}>
              <item.icon size={20} style={{ marginRight: '10px' }} />
              {item.label}
            </li>
          ))}
        </ul>
      </nav>

      {/* Main Content */}
      <div style={{ marginLeft: '270px', padding: '20px', width: '100%' }}>
        <h2>Dashboard Overview</h2>
        <Button onClick={() => setShowLogoutModal(true)} style={{ marginTop: '10px' }}>Logout</Button>

        {/* Chart */}
        <Card style={{ marginTop: '20px' }}>
          <CardHeader>
            <CardTitle>{activeSection.toUpperCase()} DATA</CardTitle>
          </CardHeader>
          <CardContent>
            <PieChart width={400} height={300}>
              <Pie data={getChartData()} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={100} fill="#82ca9d" label />
              <Tooltip />
            </PieChart>
          </CardContent>
        </Card>
      </div>

      {/* Logout Modal */}
      {showLogoutModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ background: 'white', padding: '20px', borderRadius: '8px' }}>
            <p>Are you sure you want to logout?</p>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
              <Button onClick={() => setShowLogoutModal(false)}>Cancel</Button>
              <Button onClick={() => navigate('/login')}>Logout</Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
