import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Signup.css'; // Import the CSS file

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('employee'); // Default user type (in lowercase)
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    try {
      // Send signup data to the backend
      const response = await axios.post('http://localhost:5000/api/signup', {
        email,
        password,
        userType, // Should be lowercase
      });

      alert(response.data.message); // Show success message
      navigate('/login'); // Redirect to login after successful signup
    } catch (error) {
      alert('Signup failed: ' + error.response.data.error); // Show error message
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-box">
        <h2>Sign Up</h2>
        <p className="login-prompt">Already a member? <a href="/login">Login</a></p>
        <form onSubmit={handleSignup}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <select value={userType} onChange={(e) => setUserType(e.target.value.toLowerCase())}>
            <option value="employee">Employee</option>
            <option value="admin">Admin</option>
          </select>
          <button type="submit">Create Account</button>
        </form>
      </div>
    </div>
  );
};

export default Signup;
