import React, { useState } from 'react';
import { FaUserCircle } from 'react-icons/fa'; // Import user profile icon
import { Route, Routes } from 'react-router-dom';
import ManualInput from './ManualInput';
import FileUploads from './FileUploads'; // Use './' instead of '../'
import UploadReports from './UploadReports'; // Use './' instead of '../'

const Dashboard = () => {
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  const toggleDropdown = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <div style={{ flex: 1, padding: '20px' }}>
        <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center' }}>
          <FaUserCircle
            style={{ fontSize: '2rem', cursor: 'pointer' }}
            onClick={toggleDropdown}
          />
          {showProfileDropdown && (
            <div className="profile-dropdown">
              <p><strong>Email:</strong> user@example.com</p>
              <p><strong>Password:</strong> ********</p>
            </div>
          )}
        </div>
        <Routes>
          <Route path="Manualinput" element={<ManualInput />} />
          <Route path="csv file upload" element={<FileUploads />} />
          <Route path="upload reports" element={<UploadReports />} />
          <Route path="comparison" element={<h2>Comparison</h2>} />
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
