import React from 'react';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
} from 'lucide-react';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
  },
  sidebar: {
    width: '250px',
    backgroundColor: '#2f3b52',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
  },
  logo: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '20px',
  },
  logoIcon: {
    marginRight: '10px',
  },
  logoText: {
    fontSize: '20px',
    fontWeight: 'bold',
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
  },
  navItem: {
    display: 'flex',
    alignItems: 'center',
    textDecoration: 'none',
    color: '#cfd8dc',
    padding: '10px 15px',
    borderRadius: '4px',
    marginBottom: '8px',
    transition: 'background-color 0.3s',
  },
  navItemHover: {
    backgroundColor: '#3e4a61',
  },
  navIcon: {
    marginRight: '10px',
  },
  content: {
    flex: 1,
    padding: '20px',
    boxSizing: 'border-box',
    backgroundColor: '#fff',
  },
  header: {
    marginBottom: '30px',
    borderBottom: '2px solid #f0f0f0',
    paddingBottom: '10px',
  },
  mainSection: {
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
  },
};

const menuItems = [
  { icon: Home, label: 'Home', path: '/' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
];

const LandingPage = () => {
  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <aside style={styles.sidebar}>
        <div style={styles.logo}>
          <FileText style={styles.logoIcon} className="h-6 w-6 text-emerald-400" />
          <span style={styles.logoText}>ESG Reporter</span>
        </div>
        <nav>
          <ul style={styles.navList}>
            {menuItems.map((item) => (
              <li key={item.path}>
                <a
                  href={item.path}
                  style={styles.navItem}
                  onMouseEnter={(e) => (e.target.style.backgroundColor = '#3e4a61')}
                  onMouseLeave={(e) => (e.target.style.backgroundColor = '')}
                >
                  <item.icon style={styles.navIcon} className="h-5 w-5" />
                  <span>{item.label}</span>
                </a>
              </li>
            ))}
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main style={styles.content}>
        <header style={styles.header}>
          <h1>Welcome to ESG Reporter</h1>
          <p>Analyze and manage your ESG reports effectively.</p>
        </header>

        <section style={styles.mainSection}>
          <h2>Dashboard</h2>
          <p>This is the main content area where you can display dashboard widgets or other components.</p>
        </section>
      </main>
    </div>
  );
};

export default LandingPage;
