import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Home, LayoutDashboard, FileText, BarChart2, Calculator,
  Settings, UserCircle, HelpCircle, CloudUpload
} from 'lucide-react';
import GreenTrendz from '../assets/img/logo_1.png';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, Legend,
  PieChart, Pie, LineChart, Line, AreaChart, Area
} from 'recharts';

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'CarbonCalculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
  { icon: UserCircle, label: 'Account', path: '/account' },
];

const Dashboard = () => {
  const [data, setData] = useState({});
  const [activeSection, setActiveSection] = useState('environmental');
  const navigate = useNavigate();

  // Fetch Data from Backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/esg-data');
        setData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  // Ensure environmental data exists
  const environmentalData = data.environmentalData || {};

  // Convert Data for Charts
  const barChartData = Object.entries(environmentalData).map(([key, value]) => ({
    name: key.replace(/([A-Z])/g, ' $1').trim(),
    value: parseFloat(value) || 0,
  }));

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <nav className="w-64 bg-green-700 text-white p-5 flex flex-col fixed h-full shadow-md">
        <div className="flex items-center space-x-3">
          <img src={GreenTrendz} alt="Logo" width="50" />
          <h5 className="text-xl font-bold">GreenTrendz</h5>
        </div>

        <ul className="mt-6 space-y-2">
          {menuItems.map((item) => (
            <li key={item.path}>
              <a href={item.path} className="flex items-center space-x-2 px-4 py-2 hover:bg-green-800 rounded">
                <item.icon size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>

        <button
          className="mt-auto bg-white text-green-700 p-2 rounded flex items-center justify-center space-x-2"
          onClick={() => navigate('/')}
        >
          <Settings size={18} />
          <span>Logout</span>
        </button>
      </nav>

      {/* Main Content */}
      <main className="ml-64 p-6 flex-1">
        <h1 className="text-3xl font-bold mb-4">ESG Dashboard</h1>

        {/* Section Toggle Buttons */}
        <div className="flex space-x-4 mb-6">
          <button
            onClick={() => setActiveSection('environmental')}
            className={`w-1/3 p-2 rounded ${activeSection === 'environmental' ? 'bg-green-600 text-white' : 'bg-gray-300'}`}
          >
            Environmental
          </button>
        </div>

        {/* Metrics + Charts */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Metrics Section */}
          <div className="bg-white p-4 rounded shadow">
            <h2 className="text-xl font-bold mb-4">Environmental Metrics</h2>
            <ul className="space-y-2">
              {Object.entries(environmentalData).map(([key, value]) => (
                <li key={key} className="flex justify-between border-b pb-2">
                  <span className="text-gray-600">{key.replace(/([A-Z])/g, ' $1').trim()}</span>
                  <span className="font-bold">{value}</span>
                </li>
              ))}
            </ul>
          </div>

          {/* Charts */}
          <div className="grid grid-cols-1 gap-6">
            {/* Bar Chart */}
            <div className="bg-white p-4 rounded shadow">
              <h2 className="text-xl font-bold mb-4">Bar Chart</h2>
              <BarChart width={500} height={300} data={barChartData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="value" fill="#8884d8" />
              </BarChart>
            </div>

            {/* Pie Chart */}
            <div className="bg-white p-4 rounded shadow">
              <h2 className="text-xl font-bold mb-4">Pie Chart</h2>
              <PieChart width={400} height={300}>
                <Pie data={barChartData} dataKey="value" cx="50%" cy="50%" outerRadius={100} fill="#82ca9d" label />
                <Tooltip />
                <Legend />
              </PieChart>
            </div>

            {/* Line Chart */}
            <div className="bg-white p-4 rounded shadow">
              <h2 className="text-xl font-bold mb-4">Line Chart</h2>
              <LineChart width={500} height={300} data={barChartData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="value" stroke="#8884d8" />
              </LineChart>
            </div>

            {/* Area Chart */}
            <div className="bg-white p-4 rounded shadow">
              <h2 className="text-xl font-bold mb-4">Area Chart</h2>
              <AreaChart width={500} height={300} data={barChartData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Area type="monotone" dataKey="value" stroke="#82ca9d" fill="#82ca9d" />
              </AreaChart>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
