  const handleCityInputChange = (e) => {
    const query = e.target.value.toLowerCase();
    const filteredCities = cities.filter(city => city.toLowerCase().startsWith(query)); // Start with query instead of includes
    setCitySuggestions(filteredCities);
    setLocation(e.target.value);  // Update the location state
  };

  
  const handleSignup = async (e) => {
    e.preventDefault();
    console.log('OTP Verified:', otpVerified);
    console.log('Accepted Terms:', acceptedTerms);
    

    // Password and Terms Validation
    if (!passwordRegex.test(password)) {
      alert('Password must be at least 8 characters long and include an uppercase letter, a lowercase letter, a number, and a symbol.');
      return;
    }
  
    // Password Match Validation
    if (password !== confirmPassword) {
      alert('Passwords do not match!');
      return;
    }
    
    if (!acceptedTerms) {
      alert('You must accept the terms and conditions!');
      return;
    }
    
    if (!otpVerified) {
      alert('Please verify your email and phone with OTP!');
      return;
    }

    const handleSignup = async (e) => {
      e.preventDefault();
      
      const signupData = {
        companyName,
        email,
        phoneNumber,
        password,
        industrySector,
        location,
        userType,
      };
    
      try {
        const response = await fetch('http://localhost:5001/signup', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(signupData),
        });
    
        if (response.ok) {
          alert('Signup successful!');
          // Redirect to login page or dashboard
        } else {
          const error = await response.json();
          alert(error.error || 'Signup failed');
        }
      } catch (err) {
        console.error('Error:', err);
        alert('Something went wrong!');
      }
    };
    
  };

  const handleShowModal = () => setShowModal(true); // Show modal
  const handleCloseModal = () => setShowModal(false); // Close modal

  const handleTermsConfirmation = (accepted) => {
    if (accepted) {
      setCanAcceptTerms(true);
    } else {
      setCanAcceptTerms(false);
    }
    setShowModal(false); // Close modal on confirmation
  };

  

// Function to request OTP
const requestOtp = async (type) => {
  const data = { email: type === 'email' ? email : undefined, phone: type === 'phone' ? phoneNumber : undefined };

  try {
    const response = await fetch('http://localhost:5001/send-otp', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });

    if (response.ok) {
      if (type === 'email') setEmailOtpSent(true);
      if (type === 'phone') setPhoneOtpSent(true);
      alert(`OTP sent successfully to your ${type}`);
    } else {
      const error = await response.json();
      alert(error.error || 'Failed to send OTP');
    }
  } catch (err) {
    console.error('Error:', err);
    alert('Something went wrong!');
  }
};

// Function to verify OTP
const verifyOtp = async () => {
  const data = { email, phone: phoneNumber, otp };

  try {
    const response = await fetch('http://localhost:5001/verify-otp', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });

    if (response.ok) {
      setOtpVerified(true);
      alert('OTP verified successfully!');
    } else {
      const error = await response.json();
      alert(error.error || 'Failed to verify OTP');
    }
  } catch (err) {
    console.error('Error:', err);
    alert('Something went wrong!');
  }
};

  const styles = {
