    },
    alreadyCustomer: {
      fontSize: '14px',
      color: '#666666', // Lighter shade for subtlety
      marginBottom: '10px',
    },
    link: {
      color: '#28a745', // Blue for better contrast and standard link color
      fontWeight: 'bold',
      cursor: 'pointer',
    },
    form: {
      display: 'flex',
