      border: '1px solid #ccc',
      fontSize: '14px',
      color: '#555555',
      transition: 'border-color 0.3s ease, box-shadow 0.3s ease', // Add box-shadow transition
    },
    inputFocus: {
      borderColor: '#007bff', // Blue border on focus
      boxShadow: '0 0 8px rgba(0, 123, 255, 0.25)', // Glow effect
    },
    otpInput: {
      width: '70%',
      padding: '10px',
      marginBottom: '15px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '14px',
      color: '#555555',
      textAlign: 'center',
    },
    otpButton: {
      display: 'flex',
      gap: '10px',
      justifyContent: 'center',
      alignItems: 'center',
    },
    button: {
      width: '100%',
      padding: '14px', // Slightly larger for prominence
      backgroundColor: '#28a745',
      color: '#ffffff',
      border: 'none',
      borderRadius: '5px',
      fontSize: '16px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease, transform 0.2s ease',
    },
    buttonHover: {
      backgroundColor: '#218838',
      transform: 'scale(1.05)', // Slight zoom effect
    },
    greyButton: {
      width: '100%',
      padding: '14px',
      backgroundColor: '#e0e0e0', // Softer grey
      color: '#888888',
      border: 'none',
      borderRadius: '5px',
      fontSize: '16px',
      cursor: 'not-allowed',
      fontWeight: '600',
    },
    select: {
      width: '100%',
      padding: '12px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '14px',
      color: '#555555',
    },
    checkboxContainer: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      marginBottom: '20px',
    },
    checkbox: {
      transform: 'scale(1.2)', // Slightly larger for visibility
      cursor: 'pointer',
    },
    cancelbutton: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#6c757d', // Bootstrap grey
      color: '#ffffff',
      border: 'none',
      borderRadius: '5px',
      fontSize: '16px',
      cursor: 'pointer',
      fontWeight: '600',
      transition: 'background-color 0.3s ease',
    },
    cancelbuttonHover: {
