                src={GreenTrendz}
                alt="Logo"
                width="50"
                height="50"
                className="d-inline-block align-top me-2"
              />
              <span className="brand-text">GreenTrendz</span>
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
