import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { LineChart, BarChart, PieChart, Line, Bar, Pie, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Home, LayoutDashboard, FileText, BarChart2, Calculator, Settings, UserCircle, HelpCircle, CloudUpload } from 'lucide-react';

const Dashboard = () => {
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [activeSection, setActiveSection] = useState('environmental');
  const [data, setData] = useState({
    environmentalData: {},
    socialData: {},
    governanceData: {}
  });

  // Sample chart data - replace with real data from MongoDB
  const environmentalChartData = [
    { month: 'Jan', energy: 65, water: 45, waste: 30 },
    { month: 'Feb', energy: 59, water: 49, waste: 28 },
    { month: 'Mar', energy: 80, water: 52, waste: 35 },
    { month: 'Apr', energy: 81, water: 58, waste: 40 },
    { month: 'May', energy: 56, water: 42, waste: 25 },
    { month: 'Jun', energy: 55, water: 45, waste: 30 }
  ];

  const socialPieData = [
    { name: 'Male', value: 55 },
    { name: 'Female', value: 45 }
  ];

  const governanceBarData = [
    { category: 'Board Diversity', value: 85 },
    { category: 'Ethics Training', value: 92 },
    { category: 'Risk Management', value: 78 },
    { category: 'Compliance', value: 95 }
  ];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/esg-data');
        if (!response.ok) throw new Error('Network response was not ok');
        const jsonData = await response.json();
        setData(jsonData);
      } catch (error) {
        console.error('Error fetching ESG data:', error);
      }
    };
    fetchData();
  }, []);

  const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'CarbonCalculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
    { icon: UserCircle, label: 'Account', path: '/account' }
  ];

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar */}
      <nav className="w-64 bg-gradient-to-br from-emerald-800 to-emerald-600 text-white fixed h-full">
        <div className="flex items-center p-4">
          <img src="/api/placeholder/50/50" alt="Logo" className="w-12 h-12" />
          <h5 className="text-2xl font-bold ml-2">GreenTrendz</h5>
        </div>

        <ul className="mt-6">
          {menuItems.map((item) => (
            <li key={item.path}>
              <a
                href={item.path}
                className="flex items-center px-6 py-3 text-white hover:bg-emerald-700 hover:scale-105 transition-all duration-200 rounded-lg mx-2"
              >
                <item.icon className="mr-3" size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>

        <div className="absolute bottom-8 w-full px-4">
          <button
            onClick={() => setShowLogoutModal(true)}
            className="w-full bg-white text-emerald-800 py-2 px-4 rounded-lg font-semibold flex items-center justify-center gap-2 hover:bg-gray-100 transition-colors"
          >
            <Settings size={18} />
            Logout
          </button>
        </div>
      </nav>

      {/* Main Content */}
      <main className="ml-64 flex-1 p-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-800">ESG Dashboard</h1>
          <p className="text-gray-600 mt-2">Monitoring Environmental, Social, and Governance Metrics</p>
        </div>

        <div className="flex space-x-4 mb-8">
          {['environmental', 'social', 'governance'].map((section) => (
            <Button
              key={section}
              onClick={() => setActiveSection(section)}
              className={`w-1/3 h-12 text-lg ${
                activeSection === section
                  ? 'bg-emerald-600 hover:bg-emerald-700'
                  : 'bg-white text-emerald-600 hover:bg-emerald-50'
              }`}
            >
              {section.charAt(0).toUpperCase() + section.slice(1)}
            </Button>
          ))}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Main Metrics Card */}
          <Card className="col-span-2">
            <CardHeader>
              <CardTitle className="text-2xl">
                {activeSection === 'environmental' && 'Environmental Performance Trends'}
                {activeSection === 'social' && 'Social Impact Metrics'}
                {activeSection === 'governance' && 'Governance Overview'}
              </CardTitle>
            </CardHeader>
            <CardContent className="h-80">
              <ResponsiveContainer width="100%" height="100%">
                {activeSection === 'environmental' && (
                  <LineChart data={environmentalChartData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="month" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="energy" stroke="#059669" />
                    <Line type="monotone" dataKey="water" stroke="#0ea5e9" />
                    <Line type="monotone" dataKey="waste" stroke="#6366f1" />
                  </LineChart>
                )}
                {activeSection === 'social' && (
                  <PieChart>
                    <Pie
                      data={socialPieData}
                      cx="50%"
                      cy="50%"
                      innerRadius={60}
                      outerRadius={100}
                      fill="#059669"
                      dataKey="value"
                      label
                    />
                    <Tooltip />
                    <Legend />
                  </PieChart>
                )}
                {activeSection === 'governance' && (
                  <BarChart data={governanceBarData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="category" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="value" fill="#059669" />
                  </BarChart>
                )}
              </ResponsiveContainer>
            </CardContent>
          </Card>

          {/* Key Metrics */}
          <Card>
            <CardHeader>
              <CardTitle>Key Performance Indicators</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {activeSection === 'environmental' && (
                  <div className="grid gap-4">
                    <div className="p-4 bg-emerald-50 rounded-lg">
                      <p className="text-sm text-emerald-600">Renewable Energy Usage</p>
                      <p className="text-2xl font-bold">{data.environmentalData?.renewableEnergyUsage || '45%'}</p>
                    </div>
                    <div className="p-4 bg-emerald-50 rounded-lg">
                      <p className="text-sm text-emerald-600">Water Efficiency</p>
                      <p className="text-2xl font-bold">{data.environmentalData?.wastewaterReused || '78%'}</p>
                    </div>
                  </div>
                )}
                {activeSection === 'social' && (
                  <div className="grid gap-4">
                    <div className="p-4 bg-emerald-50 rounded-lg">
                      <p className="text-sm text-emerald-600">Employee Training Hours</p>
                      <p className="text-2xl font-bold">{data.socialData?.employeeTrainingHours || '40hrs'}</p>
                    </div>
                    <div className="p-4 bg-emerald-50 rounded-lg">
                      <p className="text-sm text-emerald-600">Community Engagement</p>
                      <p className="text-2xl font-bold">{data.socialData?.communityEngagementHours || '2000hrs'}</p>
                    </div>
                  </div>
                )}
                {activeSection === 'governance' && (
                  <div className="grid gap-4">
                    <div className="p-4 bg-emerald-50 rounded-lg">
                      <p className="text-sm text-emerald-600">Board Independence</p>
                      <p className="text-2xl font-bold">{data.governanceData?.independentDirectors || '80%'}</p>
                    </div>
                    <div className="p-4 bg-emerald-50 rounded-lg">
                      <p className="text-sm text-emerald-600">Ethics Training Completion</p>
                      <p className="text-2xl font-bold">{data.governanceData?.employeesTrainedOnCodeOfConduct || '95%'}</p>
                    </div>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Additional Metrics */}
          <Card>
            <CardHeader>
              <CardTitle>Detailed Metrics</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {activeSection === 'environmental' && (
                  <>
                    <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                      <span>Energy Systems</span>
                      <span className="font-semibold">{data.environmentalData?.energyEfficientSystems || 'Renewable'}</span>
                    </div>
                    <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                      <span>Zero Waste to Landfill</span>
                      <span className="font-semibold">{data.environmentalData?.zeroWasteToLandfill || 'Yes'}</span>
                    </div>
                  </>
                )}
                {activeSection === 'social' && (
                  <>
                    <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                      <span>DEI Programs</span>
                      <span className="font-semibold">{data.socialData?.deiPrograms || 'Active'}</span>
                    </div>
                    <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                      <span>Safety Incidents</span>
                      <span className="font-semibold">{data.socialData?.workforceSafetyIncidents || 'LTIFR: 0.5'}</span>
                    </div>
                  </>
                )}
                {activeSection === 'governance' && (
                  <>
                    <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                      <span>Risk Framework</span>
                      <span className="font-semibold">{data.governanceData?.riskManagementFramework || 'ISO 31000'}</span>
                    </div>
                    <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                      <span>Compliance Status</span>
                      <span className="font-semibold">{data.governanceData?.regulatoryComplianceStatus || 'Compliant'}</span>
                    </div>
                  </>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      </main>

      {/* Logout Modal */}
      {showLogoutModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-96">
            <h3 className="text-xl font-bold mb-4">Confirm Logout</h3>
            <p className="text-gray-600 mb-6">Are you sure you want to logout?</p>
            <div className="flex justify-end space-x-4">
              <Button
                variant="outline"
                onClick={() => setShowLogoutModal(false)}
              >
                Cancel
              </Button>
              <Button
                className="bg-emerald-600 hover:bg-emerald-700"
                onClick={() => window.location.href = '/'}
              >
                Yes, Logout
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
