import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';
import './style.css';  // If style.css is in the same folder as FileUploads.js
import { Info } from 'lucide-react';


import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    UserCircle,
    HelpCircle,
    CloudUpload,
} from 'lucide-react';

const styles = {
    landingPage: {
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f4f4f9',
        fontFamily: 'Arial, sans-serif',
      },
      sidebar: {
        width: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        position: 'fixed',
        height: '100vh',
        zIndex: 10,
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
        transition: 'width 0.3s ease',
      },
      logo: {
        fontSize: '25px',
        marginBottom: '30px',
        fontWeight: 'bold',
        color: '#fff',
        textAlign: 'center',
        marginTop: '20px',
        transition: 'transform 0.3s ease',
      },
      logoHover: {
        transform: 'scale(1.1)',
      },
      navList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        width: '100%',
      },
      navItem: {
        color: 'white',
        textDecoration: 'none',
        padding: '15px 20px',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        borderRadius: '8px',
        marginBottom: '12px',
        transition: 'background-color 0.3s ease, transform 0.3s ease',
        cursor: 'pointer',
        fontfamily: "'Poppins', sans-serif", // Custom font for links
            fontWeight: '500', // Optional for bold text
        },
          navItemHover: {
        backgroundColor: '#3b6b5c',
        transform: 'scale(1.05)',
      },
      navIcon: {
        marginRight: '20px',
        transition: 'transform 0.3s ease',
      },

        content: {
            flex: 1,
            padding: '30px',
            boxSizing: 'border-box',
            backgroundColor: '#f8f9fa', // Light gray background for a softer look
            overflowY: 'auto',
            marginLeft: '250px',
            transition: 'margin-left 0.3s ease',
        },





    button: {
        backgroundColor: '#ffffff', // Default background color
        color: 'Black', // Text color
        border: 'none', // No border
        padding: '10px 20px', // Padding for a nice clickable area
        borderRadius: '5px', // Rounded corners
        fontWeight: 'bold', // Bold text
        fontSize: '16px', // Font size
        cursor: 'pointer', // Pointer cursor on hover
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Subtle shadow for a 3D effect
        transition: 'background-color 0.3s ease, transform 0.2s ease', // Smooth transition for hover effects
      },
      infoIcon: {
        marginLeft: '8px',
        cursor: 'pointer',
        fontSize: '6px',
        color: '#666',
        lineHeight: '1',
        width: '20px', // Adjust width to control circle size
    height: '20px', // Adjust height to control circle size
    display: 'inline-flex', // Ensures proper centering
    alignItems: 'center', // Vertically centers the icon within the circle
    justifyContent: 'center', // Horizontally centers the icon within the circle
    borderRadius: '50%', // Ensures it's a circle
    padding: '2px', // Adjust padding for inner spacing
    },
    tooltip: {
        position: 'absolute',
        backgroundColor: '#333',
        color: '#fff',
        padding: '5px 10px',
        borderRadius: '4px',
        fontSize: '12px',
        zIndex: '1000',
        visibility: 'hidden',
        opacity: 0,
        transition: 'opacity 0.3s',
    },
    tooltipVisible: {
        visibility: 'visible',
        opacity: 1,
    },
};

const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
    { icon: UserCircle, label: 'Account', path: '/account' }, // Added Account item

];

const Sidebar = () => {
    const [showLogoutPopup, setShowLogoutPopup] = useState(false);

    const [tooltipVisible, setTooltipVisible] = useState({});

    const showTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: true }));
    };

    const hideTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: false }));
    };
    const environmentalSections = [
        {
            title: 'Energy',
            metrics: [
                { label: 'Coal (tons, GJ)', key: 'coal', type: 'number' },
                { label: 'Diesel (L, GJ)', key: 'diesel', type: 'number' },
                { label: 'High-Speed Diesel (HSD) (L, GJ)', key: 'hsd', type: 'number' },
                { label: 'Petrol (L, GJ)', key: 'petrol', type: 'number' },
                { label: 'Liquefied Petroleum Gas (LPG) (m³, GJ)', key: 'lpg', type: 'number' },
                { label: 'Natural Gas (m³, GJ)', key: 'naturalGas', type: 'number' },
                { label: 'LNG/PNG (m³, GJ)', key: 'lngPng', type: 'number' },
                { label: 'Solar Energy (MWh, kWh)', key: 'solarEnergy', type: 'number' },
                { label: 'Wind Energy (MWh, kWh)', key: 'windEnergy', type: 'number' },
                { label: 'Hydroelectric Energy (MWh, kWh)', key: 'hydroEnergy', type: 'number' },
                { label: 'Biomass (tons, GJ)', key: 'biomass', type: 'number' },
                { label: 'Renewable Energy Generated (MWh, %)', key: 'renewableEnergyGenerated', type: 'number' },
                { label: 'On-site Energy Consumption (MWh, GJ)', key: 'onsiteEnergyConsumption', type: 'number' },
                { label: 'Biodiesel (L, GJ)', key: 'biodiesel', type: 'number' },
            ],
        },
        {
            title: 'Water',
            metrics: [
                { label: 'Produced Water Withdrawal (m³, L)', key: 'producedWaterWithdrawal', type: 'number' },
                { label: 'Surface Water Withdrawal (m³, L)', key: 'surfaceWaterWithdrawal', type: 'number' },
                { label: 'Ground Water Withdrawal (m³, L)', key: 'groundWaterWithdrawal', type: 'number' },
                { label: 'Seawater Withdrawal (m³, L)', key: 'seawaterWithdrawal', type: 'number' },
                { label: 'Third-Party Water Withdrawal (m³, L)', key: 'thirdPartyWaterWithdrawal', type: 'number' },
                { label: 'Water Consumption (m³, L)', key: 'waterConsumption', type: 'number' },
