                { label: 'Water Discharge to Groundwater (m³, L)', key: 'waterDischargeGround', type: 'number' },
                { label: 'Water Discharge to Third-Party (m³, L)', key: 'waterDischargeThirdParty', type: 'number' },
                { label: 'Water Discharge to Seawater (m³, L)', key: 'waterDischargeSeawater', type: 'number' },
