                { label: 'Civil Waste (kg, tons, % recycled)', key: 'civilWaste', type: 'number' },
                { label: 'Miscellaneous Waste (kg, tons, % recycled)', key: 'miscWaste', type: 'number' },
                { label: 'Biomedical Waste (kg, tons, % recycled)', key: 'biomedicalWaste', type: 'number' },
                { label: 'Feed Waste (kg, tons, % recycled)', key: 'feedWaste', type: 'number' },
