import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  UserCircle,
  HelpCircle,
  CloudUpload,
} from 'lucide-react';
import GreenTrendz from '../assets/img/logo_1.png'; // Ensure this path is correct
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  PieChart,
  Pie,
  LineChart,
  Line,
  AreaChart,
  Area,
} from 'recharts';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    fontFamily: 'Arial, sans-serif',
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #064e3b, #276749)',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed',
    height: '100vh',
    zIndex: 10,
    boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
    transition: 'width 0.3s ease',
  },
  logo: {
    fontSize: '25px',
    marginBottom: '30px',
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
    marginTop: '20px',
    transition: 'transform 0.3s ease',
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    width: '100%',
  },
  navItem: {
    color: 'white',
    textDecoration: 'none',
    padding: '15px 20px',
    display: 'flex',
    alignItems: 'center',
    width: '100%',
    borderRadius: '8px',
    marginBottom: '12px',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    cursor: 'pointer',
    fontFamily: "'Poppins', sans-serif",
    fontWeight: '500',
  },
  navIcon: {
    marginRight: '20px',
    transition: 'transform 0.3s ease',
  },
  content: {
    flex: 1,
    padding: '30px',
    boxSizing: 'border-box',
    backgroundColor: '#f8f9fa',
    overflowY: 'auto',
    marginLeft: '250px',
    transition: 'margin-left 0.3s ease',
  },
  button: {
    backgroundColor: '#ffffff',
    color: 'black',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    fontWeight: 'bold',
    fontSize: '16px',
    cursor: 'pointer',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
    transition: 'background-color 0.3s ease, transform 0.2s ease',
  },
};

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'CarbonCalculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
  { icon: UserCircle, label: 'Account', path: '/account' },
];

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [activeSection, setActiveSection] = useState('environmental');
  const navigate = useNavigate();

  // Fetch Data from Backend
  const API_URL = 'http://localhost:5003/api/esg-data';  // âœ… Ensure correct port

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(API_URL);
        setData(response.data);
      } catch (error) {
        console.error('Error fetching ESG data:', error);
      }
    };
  
    fetchData();
  }, []);
  

  // Filter Data Based on Active Section
  const filteredData = data.find((item) => item.section === activeSection)?.metrics || {};

  // Chart Data
  const chartData = Object.entries(filteredData).map(([key, value]) => ({
    name: key,
    value: parseFloat(value) || 0, // Convert string values to numbers for charts
  }));

  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img src={GreenTrendz} alt="Logo" width="50" height="50" />
          <h5 style={styles.logo}>GreenTrendz</h5>
        </div>

        <ul style={styles.navList}>
          {menuItems.map((item) => (
            <li key={item.path}>
              <a
                href={item.path}
                style={styles.navItem}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#3b6b5c';
                  e.target.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '';
                  e.target.style.transform = '';
                }}
              >
                <item.icon style={styles.navIcon} size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>

        <div style={{ marginTop: 'auto', textAlign: 'center' }}>
          <button
            style={styles.button}
            onClick={() => navigate('/')} // Navigate to home on logout
          >
            Logout
          </button>
        </div>
      </nav>

      {/* Main Content */}
      <main style={styles.content}>
        <h1>ESG Dashboard</h1>
        <p>Welcome to the GreenTrendz ESG Dashboard!</p>

        <div style={{ marginBottom: '20px' }}>
          <button
            onClick={() => setActiveSection('environmental')}
            style={{
              marginRight: '10px',
              backgroundColor: activeSection === 'environmental' ? '#4CAF50' : '#ddd',
              color: activeSection === 'environmental' ? '#fff' : '#000',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            Environmental
          </button>
          <button
            onClick={() => setActiveSection('social')}
            style={{
              marginRight: '10px',
              backgroundColor: activeSection === 'social' ? '#4CAF50' : '#ddd',
              color: activeSection === 'social' ? '#fff' : '#000',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            Social
          </button>
          <button
            onClick={() => setActiveSection('governance')}
            style={{
              backgroundColor: activeSection === 'governance' ? '#4CAF50' : '#ddd',
              color: activeSection === 'governance' ? '#fff' : '#000',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            Governance
          </button>
        </div>

        <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
          {/* Metrics Display */}
          <div style={{ flex: 1, minWidth: '300px' }}>
            <h2>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Metrics</h2>
            <ul>
              {Object.entries(filteredData).map(([key, value]) => (
                <li key={key}>
                  <strong>{key}:</strong> {value}
                </li>
              ))}
            </ul>
          </div>

          {/* Chart Display */}
          <div style={{ flex: 2, minWidth: '400px' }}>
            <h2>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Visualization</h2>
            <BarChart width={500} height={300} data={chartData}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="value" fill="#8884d8" />
            </BarChart>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
