import { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import "chart.js/auto";
import { Card, CardHeader, CardTitle, CardContent } from "./card";

const ESGDashboard = () => {
  const [esgData, setEsgData] = useState(null);

  useEffect(() => {
    // Fetch ESG Data from backend API
    fetch("/api/esg")
      .then((res) => res.json())
      .then((data) => setEsgData(data))
      .catch((err) => console.error(err));
  }, []);

  if (!esgData) return <div className="text-center">Loading...</div>;

  // Bar Chart Data for Environmental Metrics
  const envChartData = {
    labels: ["Renewable Energy", "Wastewater Reused", "Rainwater Harvesting", "Zero Waste"],
    datasets: [
      {
        label: "% Compliance",
        data: [
          esgData.environmentalData.renewableEnergyUsage || 0,
          esgData.environmentalData.wastewaterReused || 0,
          esgData.environmentalData.rainwaterHarvesting === "Yes" ? 100 : 0,
          esgData.environmentalData.zeroWasteToLandfill === "Yes" ? 100 : 0,
        ],
        backgroundColor: ["#4CAF50", "#2196F3", "#FF9800", "#9C27B0"],
      },
    ],
  };

  // Bar Chart Data for Social Metrics
  const socialChartData = {
    labels: ["Gender Diversity", "Volunteerism", "Workforce Safety"],
    datasets: [
      {
        label: "% Engagement",
        data: [
          esgData.socialData.genderDiversity || 0,
          esgData.socialData.volunteerismHoursPerEmployee || 0,
          esgData.socialData.workforceSafetyIncidents === "LTIFR" ? 50 : 20,
        ],
        backgroundColor: ["#F44336", "#3F51B5", "#FFC107"],
      },
    ],
  };

  return (
    <div className="p-6 space-y-6 bg-gray-100 min-h-screen rounded-lg shadow-md">
      <h1 className="text-2xl font-bold text-center">ESG Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Environmental Metrics Chart */}
        <Card className="shadow-lg border rounded-lg bg-white p-4">
          <CardHeader>
            <CardTitle className="text-lg font-bold text-gray-700">Environmental Performance</CardTitle>
          </CardHeader>
          <CardContent>
            <Bar data={envChartData} />
          </CardContent>
        </Card>

        {/* Social Metrics Chart */}
        <Card className="shadow-lg border rounded-lg bg-white p-4">
          <CardHeader>
            <CardTitle className="text-lg font-bold text-gray-700">Social Impact</CardTitle>
          </CardHeader>
          <CardContent>
            <Bar data={socialChartData} />
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default ESGDashboard;
