import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';
import './style.css';  // If style.css is in the same folder as FileUploads.js
import { Info } from 'lucide-react';
import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    UserCircle,
    HelpCircle,
    CloudUpload,
} from 'lucide-react';

const styles = {
    landingPage: {
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f4f4f9',
        fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
        width: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        position: 'fixed',
        height: '100vh',
        zIndex: 10,
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
        transition: 'width 0.3s ease',
    },
    logo: {
        fontSize: '25px',
        marginBottom: '30px',
        fontWeight: 'bold',
        color: '#fff',
        textAlign: 'center',
        marginTop: '20px',
        transition: 'transform 0.3s ease',
    },
    navList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        width: '100%',
    },
    navItem: {
        color: 'white',
        textDecoration: 'none',
        padding: '15px 20px',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        borderRadius: '8px',
        marginBottom: '12px',
        transition: 'background-color 0.3s ease, transform 0.3s ease',
        cursor: 'pointer',
        fontFamily: "'Poppins', sans-serif",
        fontWeight: '500',
    },
    content: {
        flex: 1,
        padding: '30px',
        boxSizing: 'border-box',
        backgroundColor: '#f8f9fa',
        overflowY: 'auto',
        marginLeft: '250px',
        transition: 'margin-left 0.3s ease',
    },
    button: {
        backgroundColor: '#ffffff',
        color: 'Black',
        border: 'none',
        padding: '10px 20px',
        borderRadius: '5px',
        fontWeight: 'bold',
        fontSize: '16px',
        cursor: 'pointer',
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
        transition: 'background-color 0.3s ease, transform 0.2s ease',
    },
    infoIcon: {
        marginLeft: '8px',
        cursor: 'pointer',
        fontSize: '6px',
        color: '#666',
        lineHeight: '1',
        width: '20px',
        height: '20px',
        display: 'inline-flex',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '50%',
        padding: '2px',
    },
    tooltip: {
        position: 'absolute',
        backgroundColor: '#333',
        color: '#fff',
        padding: '5px 10px',
        borderRadius: '4px',
        fontSize: '12px',
        zIndex: '1000',
        visibility: 'hidden',
        opacity: 0,
        transition: 'opacity 0.3s',
    },
    tooltipVisible: {
        visibility: 'visible',
        opacity: 1,
    },
    sectionHeader: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        cursor: 'pointer',
        padding: '10px',
        backgroundColor: '#e9e9e9',
        borderRadius: '8px',
    },
    inputContainer: {
        marginBottom: '15px',
    },
    inputField: {
        width: '100%',
        padding: '8px',
        borderRadius: '4px',
        border: '1px solid #ccc',
    },



};

const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
    { icon: UserCircle, label: 'Account', path: '/account' },
];

const Sidebar = () => {
    const [showLogoutPopup, setShowLogoutPopup] = useState(false);
    const [tooltipVisible, setTooltipVisible] = useState({});
    const [expandedSections, setExpandedSections] = useState({});

    const showTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: true }));
    };

    const hideTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: false }));
    };

    const toggleSection = (section) => {
        setExpandedSections((prev) => ({
            ...prev,
            [section]: !prev[section],
        }));
    };

    const [environmentalData, setEnvironmentalData] = useState({});
    const [socialData, setSocialData] = useState({});
    const [governanceData, setGovernanceData] = useState({});
    const [dataImportData, setDataImportData] = useState({ startDate: '', endDate: '' });
    const [csvFile, setCsvFile] = useState(null);
    const [logoFile, setLogoFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleCsvUpload = (e) => {
        const file = e.target.files[0];
        if (file && file.type !== 'text/csv') {
            alert('Please upload a valid CSV file.');
            return;
        }
        setCsvFile(file);
    };

    const handleLogoUpload = (e) => {
        const file = e.target.files[0];
