import React, { useState } from "react";
import GreenTrendz from "../assets/img/logo_1.png";

import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
  CloudUpload,
} from "lucide-react";

const styles = {
  landingPage: {
    display: "flex",
    height: "100vh",
    backgroundColor: "#f4f4f9",
    fontFamily: "Arial, sans-serif",
  },
  sidebar: {
    width: "250px",
    background: "linear-gradient(to bottom right, #064e3b, #276749)",
    color: "white",
    padding: "20px",
    boxSizing: "border-box",
    display: "flex",
    flexDirection: "column",
    position: "fixed",
    height: "100vh",
    zIndex: 10,
    boxShadow: "2px 0 15px rgba(0, 0, 0, 0.15)",
  },
  logoContainer: {
    display: "flex",
    alignItems: "center",
    marginBottom: "20px",
  },
  logo: {
    marginLeft: "10px",
    fontSize: "22px",
    fontWeight: "bold",
    color: "#fff",
  },
  navList: {
    listStyle: "none",
    padding: 0,
    margin: 0,
    width: "100%",
  },
  navItem: {
    color: "white",
    textDecoration: "none",
    padding: "12px 16px",
    display: "flex",
    alignItems: "center",
    borderRadius: "6px",
    marginBottom: "10px",
    transition: "background-color 0.3s ease, transform 0.3s ease",
    cursor: "pointer",
    fontFamily: "'Poppins', sans-serif",
    fontWeight: "500",
  },
  navItemHover: {
    backgroundColor: "#3b6b5c",
    transform: "scale(1.05)",
  },
  content: {
    flex: 1,
    padding: "30px",
    marginLeft: "250px",
    backgroundColor: "#fff",
    overflowY: "auto",
    boxSizing: "border-box",
  },
  button: {
    backgroundColor: "#ffffff",
    color: "Black",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    fontWeight: "bold",
    fontSize: "16px",
    cursor: "pointer",
    boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
    transition: "background-color 0.3s ease, transform 0.2s ease",
  },
  inputField: {
    width: "100%",
    padding: "10px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "1px solid #ddd",
    fontSize: "14px",
  },
  preview: {
    marginTop: "10px",
    padding: "10px",
    backgroundColor: "#f9f9f9",
    borderRadius: "5px",
    boxShadow: "0 1px 6px rgba(0, 0, 0, 0.1)",
  },
};

const menuItems = [
  { icon: Home, label: "Home", path: "/land" },
  { icon: CloudUpload, label: "Data Upload", path: "/upload" },
  { icon: LayoutDashboard, label: "Dashboard", path: "/dashboard" },
  { icon: FileText, label: "Reports", path: "/reports" },
  { icon: BarChart2, label: "Comparison", path: "/comparison" },
  { icon: Calculator, label: "Carbon Calculator", path: "/calculator" },
  { icon: Settings, label: "Settings", path: "/settings" },
  { icon: HelpCircle, label: "Help & Support", path: "/help" },
];

const Sidebar = () => {
  const [csvFile, setCsvFile] = useState(null);
  const [logoFile, setLogoFile] = useState(null);
  const [formErrors, setFormErrors] = useState([]);

  // Handle CSV Upload
  const handleCsvUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.name.endsWith(".csv") && file.size < 5 * 1024 * 1024) {
      setCsvFile(file);
      setFormErrors([]);
    } else {
      setFormErrors(["Invalid file format or size!"]);
    }
  };

  // Handle Logo Upload
  const handleLogoUpload = (e) => {
    const file = e.target.files[0];
    if (file && ["image/png", "image/jpeg", "image/svg+xml"].includes(file.type) && file.size < 2 * 1024 * 1024) {
      setLogoFile(URL.createObjectURL(file));
      setFormErrors([]);
    } else {
      setFormErrors(["Invalid logo format or size!"]);
    }
  };

  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
        <div style={styles.logoContainer}>
          <img src={GreenTrendz} alt="GreenTrendz Logo" width={40} height={40} />
          <span style={styles.logo}>GreenTrendz</span>
        </div>
        <ul style={styles.navList}>
          {menuItems.map((item) => (
            <li key={item.path}>
              <a href={item.path} style={styles.navItem}>
                <item.icon size={18} style={{ marginRight: "10px" }} />
                {item.label}
              </a>
            </li>
          ))}
        </ul>
      </nav>

      {/* Content */}
      <div style={styles.content}>
        <h2>Upload ESG Data</h2>
        <div>
          <input type="file" accept=".csv" style={styles.inputField} onChange={handleCsvUpload} />
          {csvFile && (
            <div style={styles.preview}>
              <p>Uploaded File: {csvFile.name}</p>
            </div>
          )}
        </div>
        <div>
          <input type="file" accept="image/*" style={styles.inputField} onChange={handleLogoUpload} />
          {logoFile && (
            <div style={styles.preview}>
              <img src={logoFile} alt="Logo Preview" width="100" />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
