import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  height: 100vh;
  font-family: Arial, sans-serif;
`;

const Sidebar = styled.div`
  width: 250px;
  background: #004d40;
  color: #ffffff;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const SidebarItem = styled.div`
  padding: 15px;
  cursor: pointer;
  transition: background 0.3s;
  &:hover {
    background: #00796b;
  }
`;

const Content = styled.div`
  flex: 1;
  padding: 20px;
  background: #f4f4f4;
  overflow-y: auto;
`;

const Section = styled.div`
  background: #ffffff;
  border: 1px solid #ddd;
  margin-bottom: 20px;
  border-radius: 8px;
  padding: 20px;
`;

const SectionHeader = styled.div`
  font-weight: bold;
  margin-bottom: 10px;
  cursor: pointer;
`;

const SectionContent = styled.div`
  margin-top: 10px;
`;

const InputField = styled.div`
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 5px;
`;

const Input = styled.input`
  padding: 10px;
  width: 100%;
  border: 1px solid #ddd;
  border-radius: 4px;
`;

const Button = styled.button`
  background: #004d40;
  color: #ffffff;
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover {
    background: #00796b;
  }
`;

const Footer = styled.footer`
  background: #004d40;
  color: #ffffff;
  text-align: center;
  padding: 10px;
  position: fixed;
  bottom: 0;
  width: 100%;
`;

const DataUploadPage = () => {
  const [showSection, setShowSection] = useState({
    environmental: false,
    social: false,
    governance: false,
  });

  const toggleSection = (section) => {
    setShowSection((prev) => ({ ...prev, [section]: !prev[section] }));
  };

  return (
    <Container>
      <Sidebar>
        <SidebarItem>Home</SidebarItem>
        <SidebarItem>Dashboard</SidebarItem>
        <SidebarItem>Report</SidebarItem>
        <SidebarItem>Comparison</SidebarItem>
        <SidebarItem>Carbon Footprint Calculator</SidebarItem>
      </Sidebar>
      <Content>
        <h2>Data Upload Page</h2>
        <Section>
          <SectionHeader onClick={() => toggleSection("environmental")}>
            Environmental Metrics
          </SectionHeader>
          {showSection.environmental && (
            <SectionContent>
              <InputField>
                <Label>Energy Consumption (kWh)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Renewable Energy Percentage (%)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Greenhouse Gas Emissions (CO2e)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Water Usage (liters)</Label>
                <Input type="text" />
              </InputField>
            </SectionContent>
          )}
        </Section>

        <Section>
          <SectionHeader onClick={() => toggleSection("social")}>
            Social Metrics
          </SectionHeader>
          {showSection.social && (
            <SectionContent>
              <InputField>
                <Label>Employee Diversity (%)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Employee Engagement Score (%)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Employee Turnover Rate (%)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Number of Community Initiatives</Label>
                <Input type="text" />
              </InputField>
            </SectionContent>
          )}
        </Section>

        <Section>
          <SectionHeader onClick={() => toggleSection("governance")}>
            Governance Metrics
          </SectionHeader>
          {showSection.governance && (
            <SectionContent>
              <InputField>
                <Label>Board Diversity (%)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Cybersecurity Incidents (#)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>Regulatory Compliance Violations (#)</Label>
                <Input type="text" />
              </InputField>
              <InputField>
                <Label>CEO to Median Employee Pay Ratio</Label>
                <Input type="text" />
              </InputField>
            </SectionContent>
          )}
        </Section>

        <Section>
          <SectionHeader>Upload CSV File</SectionHeader>
          <InputField>
            <Label>Select File:</Label>
            <Input type="file" accept=".csv" />
          </InputField>
        </Section>

        <Section>
          <SectionHeader>Upload Company Logo</SectionHeader>
          <InputField>
            <Label>Logo File:</Label>
            <Input type="file" accept=".png, .jpg, .svg" />
          </InputField>
        </Section>

        <Button>Submit</Button>
      </Content>
      <Footer>
        © 2024 Your Company Name. All rights reserved. | Privacy Policy | Terms
        of Service | Contact Us
      </Footer>
    </Container>
  );
};

export default DataUploadPage;
